INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("cancer Data Standards Registry and Repository (caDSR)","Within the caDSR, ICD-O-3 codes are incorporated into the Common Data Elements (CDEs) and Value Sets, providing standardized definitions and classifications for cancer-related data elements.","https://cadsr.cancer.gov/");
