INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("MEDIN discovery metadata standard","The MEDIN Discovery Metadata Standard is a marine profile of the UK government Standard GEMINI and also complies with other international conventions such as INSPIRE and ISO19115.","https://rdamsc.bath.ac.uk/api2/m123");
INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("DCAT-AP","DCAT-AP is an application profile of DCAT (Data Catalog Vocabulary W3C Recommendation) to be used in European data portals. It is a universal metadata scheme based on RDF, ready to be further profiled for specific domain needs.","https://rdamsc.bath.ac.uk/api2/m124");
INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("CSV on the Web (CSVW)","CSV on the Web is a W3C Recommendation for describing CSV files on the Web, ensuring their proper description, including mapping to JSON and RDF formats.","https://rdamsc.bath.ac.uk/api2/m125");
