INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("NIAID Ecosystem Metadata Model","The Portal does not contain data but features descriptions about each dataset and information about each dataset (metadata). The Discovery Portal provides external links that allow users to access the data directly from each repository","https://www.niaid.nih.gov/research/data-ecosystem-discovery-portal");
INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("Gene Expression Omnibus (GEO) data","GEO encourages submitters to supply MIAME- and MINSEQE-compliant data. GEO submission procedures are designed to closely follow the MGEO supports MIAME and MINSEQE checklists; if you provide all requested information, your submission will be compliant. ","https://www.ncbi.nlm.nih.gov/geo/info/faq.html#MIAME");
