INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("Software Package Data Exchange (SPDX)","System Package Data Exchange is an open standard capable of representing systems with digital components as bills of materials. First designed to describe software components, SPDX can describe the components of software systems, AI models, software builds, security data, and other data packages.","https://spdx.dev/");
INSERT INTO `metadataStandards` (`name`, `description`, `uri`) VALUES ("Repositório de Dados de Pesquisa da Unicamp","O REDU é a ferramenta oficial da Universidade para o depósito, preservação, compartilhamento, reutilização e reprodutibilidade de todos os dados de pesquisa aqui gerados. Os dados podem não estar inseridos no REDU, mas é obrigatório o preenchimento do metadado direcionando o local do armazenamento dos Dados.","https://redu.unicamp.br/");
