INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Harvard Dataverse","The Harvard Dataverse is open to all scientific data from all disciplines worldwide. It includes the world's largest collection of social science research data. It is hosting data for projects, archives, researchers, journals, organizations, and institutions.","https://www.re3data.org/api/v1/repository/r3d100010051","https://dataverse.harvard.edu/","[""FAIR"", ""automes research"", ""demography"", ""epidemiology"", ""human  societies"", ""human behavior"", ""multidisciplinary"", ""social societies""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Inter-university Consortium for Political and Social Research","ICPSR maintains a data archive of more than 250,000 files of research in the social and behavioral sciences. It hosts 21 specialized collections of data in education, aging, criminal justice, substance abuse, terrorism, and other fields. ICPSR advances and expands social and behavioral research, acting as a global leader in data stewardship and providing rich data resources and responsive educational opportunities for present and future generations.","r3d100010255","https://www.icpsr.umich.edu/web/pages/","[""aging"", ""census"", ""criminal justice"", ""education"", ""engineering"", ""geography"", ""health"", ""icpsr"", ""inter-university consortium for political and social research"", ""legal systems"", ""nacjd"", ""organizational behavior"", ""political science"", ""politics"", ""social science"", ""substance abuse"", ""terrorism"", ""university of michigan"", ""urban studies"", ""voting behavior""]");, "[""DISCIPLINARY"",""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("CUHK Research Data Repository","The CUHK Research Data Repository serves as an institutional research data repository for The Chinese University of Hong Kong (CUHK). It facilitates CUHK researchers to deposit, publish, and curate their research data and for the worldwide to discover, access, and reuse the research data outputs at CUHK.","r3d100013661","https://researchdata.cuhk.edu.hk","[""FAIR"", ""TRUST"", ""multidisciplinary""]");, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("UNC Dataverse","UNC Dataverse is an open-source repository software application for archiving, sharing, and accessing research data of all kinds. Each dataverse within the larger repository contains a multitude of datasets, and each dataset contains descriptive metadata and data files. UNC Dataverse is hosted by Odum Institute for Research in Social Science.","https://www.re3data.org/api/v1/repository/r3d100000005","https://dataverse.unc.edu/","[""FAIR"", ""census"", ""demographic survey"", ""demography"", ""human behavior"", ""human societies"", ""microdata"", ""multidisciplinary"", ""public health""]");, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("NCBI dbGaP","The NCBI database of Genotypes and Phenotypes archives and distributes the results of studies that have investigated the interaction of genotype and phenotype, including genome-wide association studies, medical sequencing, molecular diagnostic assays, and association between genotype and non-clinical traits. The database provides summaries of studies, the contents of measured variables, and original study document text. dbGaP provides two types of access for users, open and controlled. Through the controlled access, users may access individual-level data such as phenotypic data tables and genotypes.","https://www.re3data.org/api/v1/repository/r3d100010788","http://www.ncbi.nlm.nih.gov/gap","[""cells - morphology"", ""genetic recombination"", ""genotype-environment interaction"", ""homology (Biology)"", ""molecular diagnosis"", ""morphology"", ""phenotype""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Mendeley Data","For datasets big and small; Store your research data online. Quickly and easily upload files of any type and we will host your research data for you. Your experimental research data will have a permanent home on the web that you can refer to.","https://www.re3data.org/api/v1/repository/r3d100011868","https://data.mendeley.com/","[""FAIR"", ""data collection platform"", ""multidisciplinary""]");, "[""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Johns Hopkins Data Archive Dataverse Network","The Johns Hopkins Research Data Repository is an open access repository for Johns Hopkins University researchers to share their research data. The Repository is administered by professional curators at JHU Data Services, who will work with depositors to enable future discovery and reuse of your data, and ensure your data is Findable, Accessible, Interoperable and Reusable (FAIR). More information about the benefits of archiving data  can be found here: https://dataservices.library.jhu.edu/","https://www.re3data.org/api/v1/repository/r3d100011836","https://archive.data.jhu.edu/","[""FAIR"", ""multidsciplinary""]");, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Texas Data Repository","The Texas Data Repository is a platform for publishing and archiving datasets (and other data products) created by faculty, staff, and students at Texas higher education institutions. The repository is built in an open-source application called Dataverse, developed and used by Harvard University. The repository is hosted by the Texas Digital Library, a consortium of academic libraries in Texas with a proven history of providing shared technology services that support secure, reliable access to digital collections of research and scholarship. For a list of TDL participating institutions, please visit:  https://www.tdl.org/members/.","https://www.re3data.org/api/v1/repository/r3d100012385","https://data.tdl.org/","[""multidisciplinary""]");, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Sequence Read Archive","The Sequence Read Archive stores the raw sequencing data from such sequencing platforms as the Roche 454 GS System, the Illumina Genome Analyzer, the Applied Biosystems SOLiD System, the Helicos Heliscope, and the Complete Genomics. It archives the sequencing data associated with RNA-Seq, ChIP-Seq, Genomic and Transcriptomic assemblies, and 16S ribosomal RNA data.","https://www.re3data.org/api/v1/repository/r3d100010775","https://www.ncbi.nlm.nih.gov/sra","[""Chemicals"", ""DNA"", ""Genes"", ""Proteins"", ""RNA""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("National Science Foundation Public Access Respository","In support of NSF's plan for providing public access to its funded research, the NSF Public Access Repository (NSF-PAR) is the designated repository where NSF-funded investigators deposit peer-reviewed, published journal articles and juried conference papers. NSF-PAR also provides search mechanisms to enable you to find and use these articles and papers.","https://par.nsf.gov/","https://par.nsf.gov",NULL);, "[""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("figshare","figshare allows researchers to publish all of their research outputs in an easily citable, sharable and discoverable manner. All file formats can be published, including videos and datasets. Optional peer review process. figshare uses creative commons licensing. figshare+ repository allows figshare users to share larger datasets, over 20GB up to many TBs, see: https://plus.figshare.com/","https://www.re3data.org/api/v1/repository/r3d100010066","http://figshare.com/","[""FAIR"", ""data collection platform"", ""multidisciplinary""]");, "[""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("GenBank","GenBank® is a comprehensive database that contains publicly available nucleotide sequences for almost 260 000 formally described species. These sequences are obtained primarily through submissions from individual laboratories and batch submissions from large-scale sequencing projects, including whole-genome shotgun (WGS) and environmental sampling projects. Most submissions are made using the web-based BankIt or standalone Sequin programs, and GenBank staff assigns accession numbers upon data receipt. Daily data exchange with the European Nucleotide Archive (ENA) and the DNA Data Bank of Japan (DDBJ) ensures worldwide coverage. GenBank is accessible through the NCBI Entrez retrieval system, which integrates data from the major DNA and protein sequence databases along with taxonomy, genome, mapping, protein structure and domain information, and the biomedical journal literature via PubMed. BLAST provides sequence similarity searches of GenBank and other sequence databases. Complete bimonthly releases and daily updates of the GenBank database are available by FTP.","https://www.re3data.org/api/v1/repository/r3d100010528","https://www.ncbi.nlm.nih.gov/genbank/","[""COVID-19"", ""DNA"", ""EST"", ""GSS"", ""STS"", ""bioSample"", ""clone"", ""epigenomics"", ""genomes"", ""metagenomes"", ""nucleotide"", ""sequence data"", ""transcriptome""]");, "[""DISCIPLINARY""]");
