INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Ocean Biogeographic Information System","OBIS strives to document the ocean's diversity, distribution and abundance of life. Created by the Census of Marine Life, OBIS is now part of the Intergovernmental Oceanographic Commission (IOC) of UNESCO, under its International Oceanographic Data and Information Exchange (IODE) programme","https://www.re3data.org/api/v1/repository/r3d100010088","http://iobis.org/","[""animalia"", ""archaea"", ""bacteria"", ""biodiversity"", ""chromista"", ""eukarya"", ""fungi"", ""life in the oceans"", ""maintainance of species distribution"", ""marine ecosystems"", ""marine organisms"", ""plantae"", ""protoctista"", ""protozoa""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Vivli","Vivli is a non-profit organization working to advance human health through the insights and discoveries gained by sharing and analyzing data. It is home to an independent global data-sharing and analytics platform which serves all elements of the international research community. The platform includes a data repository, in-depth search engine and cloud-based analytics, and harmonizes governance, policy and processes to make sharing data easier. Vivli acts as a neutral broker between data contributor and data user and the wider data sharing community.","https://www.re3data.org/api/v1/repository/r3d100012823","https://vivli.org/","[""clinical trial"", ""clinical trials"", ""data discovery methods"", ""health care"", ""individual participant-level data (IPD)"", ""medical treatments"", ""patients"", ""studies""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Hakai EIMS","Hakai Data stores and shares research information associated with Hakai Institute. The Hakai Institute is a scientific research institution that advances long-term research at remote locations on the coastal margin of British Columbia, Canada. Hakai Data Systems: Data Catalogue, Sensor Network, Geospatial Data, Weather Stations and Webcams, ERDDAP Data Server","https://www.re3data.org/api/v1/repository/r3d100013227","https://data.hakai.org/","[""LiDAR"", ""Oceanography"", ""coastal watershed"", ""deep time"", ""nearshore habitats"", ""ocean acidification"", ""ocean food webs"", ""salmon"", ""sensor network"", ""shellfish""]");, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("JMU Open Science Framework (OSF)","Open Science Framework (OSF) is a free, open source web application built to help researchers manage their workflows. The OSF is part collaboration tool, part version control software, and part data archive. The OSF connects to popular tools researchers already use, like Dropbox, Box, Github and Mendeley, to streamline workflows and increase efficiency.   The OSF integrates with the researcher's daily workflow. The OSF helps document and archive study designs, materials, and data. The OSF facilitates sharing of materials and data within a laboratory/group or across laboratories/groups. The OSF also facilitates transparency of laboratory research and provides a network design that details and credits individual contributions for all aspects of the research process. ","https://osf.io/institutions/jmu",NULL,NULL);, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Ag Data Commons","Ag Data Commons provides access to a wide variety of open data relevant to agricultural research. We are a centralized repository for data already on the web, as well as for new data being published for the first time. While compliance with the U.S. Federal public access and open data directives is important, we aim to surpass them. Our goal is to foster innovative data re-use, integration, and visualization to support bigger, better science and policy.","https://www.re3data.org/api/v1/repository/r3d100011890","https://data.nal.usda.gov","[""agroecosystem"", ""agronomy"", ""bioenergy"", ""earth sciences"", ""economic statistics"", ""environmental sciences"", ""genomics"", ""hydrology"", ""life cycle assessment"", ""science"", ""soil"", ""sustainability""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("NITRC","Neuroimaging Tools and Resources Collaboratory (NITRC) is currently a free one-stop-shop environment for science researchers that need resources such as neuroimaging analysis software, publicly available data sets, and computing power. Since its debut in 2007, NITRC has helped the neuroscience community to use software and data produced from research that, before NITRC, was routinely lost or disregarded, to make further discoveries. NITRC provides free access to data and enables pay-per-use cloud-based access to unlimited computing power, enabling worldwide scientific collaboration with minimal startup and cost. With NITRC and its components—the Resources Registry (NITRC-R), Image Repository (NITRC-IR), and Computational Environment (NITRC-CE)—a researcher can obtain pilot or proof-of-concept data to validate a hypothesis for a few dollars.","https://www.re3data.org/api/v1/repository/r3d100011515","https://www.nitrc.org/","[""clinical neuroinformatics"", ""genetics"", ""genomics"", ""information theory"", ""mental health"", ""neuroimaging"", ""neuroinformatics"", ""neuroinformatics"", ""neuroscience""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("UNL Data Repository","University of Nebraska Lincoln's Digital Commons site where data and publications can be made available to the public.","https://digitalcommons.unl.edu/unldr/","https://dataregistry.unl.edu",NULL);, "[""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("National Institute of Mental Health Data Archive","The National Institute of Mental Health Data Archive (NDA) makes available human subjects data collected from hundreds of research projects across many scientific domains. The NDA provides infrastructure for sharing research data, tools, methods, and analyses enabling collaborative science and discovery. De-identified human subjects data, harmonized to a common standard, are available to qualified researchers.  Summary data is available to all. The primary point of entry to the NDA is currently through the National Database for Autism Research (NDAR) website, which serves the autism research community.  All NDA repositories can be accessed through this website for data contribution and querying with other scientific communities, allowing for aggregation and secondary analysis of data.","https://www.re3data.org/api/v1/repository/r3d100012653","https://data-archive.nimh.nih.gov/","[""Adolescent Brain"", ""Connectome Coordination"", ""EEG"", ""Eye Tracking"", ""Osteoarthritis"", ""Research Domain Criteria"", ""autism"", ""fMRI"", ""mental illness""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("ProteomeXchange","The ProteomeXchange consortium has been set up to provide a single point of submission of MS proteomics data to the main existing proteomics repositories, and to encourage the data exchange between them for optimal data dissemination. Current members accepting submissions are: The PRIDE PRoteomics IDEntifications database at the European Bioinformatics Institute focusing mainly on shotgun mass spectrometry proteomics data PeptideAtlas/PASSEL focusing on SRM/MRM datasets.","https://www.re3data.org/api/v1/repository/r3d100010692","http://www.proteomexchange.org/","[""MS proteomics data"", ""mass spectrometry"", ""protein / peptide identifications"", ""protein identification"", ""proteomics""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("OpenICPSR","It is a common platform to deposit, store and share the research data in the area of social and behavioral sciences. openICPSR is undergoing development commiting international archiving standard and is currently free for all users to share their data up to a 2GB limit. It has a distribution network of over 760 institutions, governed by the Attribution 4.0 Creative Commons License and its' data catalog indexed by major search engines. OpenICPSR is a research data-sharing service that allows depositors to rapidly self-publish research data, enabling the public to access the data without charge. Otherwise via standard ICPSR deposits, one can publish and preserve reseach data with restricted-use having nominal charge. ICPSR is part of the Institute for Social Research at the University of Michigan.","https://www.re3data.org/api/v1/repository/r3d100012693","https://www.openicpsr.org/openicpsr/","[""FAIR"", ""gender issues"", ""health behaviour"", ""immigration"", ""political parties"", ""racial and ethnic""]");, "[""DISCIPLINARY""]");
