INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("4TU.ResearchData | science.engineering.design","4TU.ResearchData, previously known as 4TU.Centre for Research Data, is a research data repository dedicated to the science, engineering and design disciplines. It offers the knowledge, experience and the tools to manage, publish and find scientific research data in a standardized, secure and well-documented manner. 4TU.ResearchData provides the research community with: Customised advice and support on research data management; A long-term repository for scientific research data; Support for current research projects; Tools to enhance reuse of research data.","https://www.re3data.org/api/v1/repository/r3d100010216","https://data.4tu.nl/info/en/","[""FAIR"", ""Hydrology"", ""Planing Urban"", ""civil engineering"", ""design"", ""earth sciences"", ""technical sciences"", ""technology and construction"", ""urban area""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Academic Torrents","Academic Torrents is a distributed data repository. The academic torrents network is built for researchers, by researchers. Its distributed peer-to-peer library system automatically replicates your datasets on many servers, so you don't have to worry about managing your own servers or file availability. Everyone who has data becomes a mirror for those data so the system is fault-tolerant.","https://www.re3data.org/api/v1/repository/r3d100011043","https://academictorrents.com/","[""multidisciplinary""]");, "[""INSTITUTIONAL"",""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("ClinicalTrials.gov","ClinicalTrials.gov is a website and online database of clinical research studies and information about their results. The purpose of ClinicalTrials.gov is to provide information about clinical research studies to the public, researchers, and health care professionals. The U.S. government does not review or approve the safety and science of all studies listed on this website.","https://www.re3data.org/api/v1/repository/r3d100010211","https://clinicaltrials.gov/","[""COVID-19"", ""behavioral"", ""clinical research"", ""clinical studies"", ""clinical trial"", ""clinical trial"", ""drug"", ""observation"", ""therapy""]");, "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Data and Specimen Hub","The Eunice Kennedy Shriver National Institute of Child Health and Human Development (NICHD) Data and Specimen Hub (DASH) is a centralized resource that allows researchers to share and access de-identified data from studies funded by NICHD. DASH also serves as a portal for requesting biospecimens from selected DASH studies.","https://www.re3data.org/api/v1/repository/r3d100012915","https://dash.nichd.nih.gov/","[""HIV/AIDS"", ""biomedical science"", ""biospecimens"", ""clinical studies"", ""critical care medicine"", ""data governance"", ""data management"", ""data submission, annotation, and curation"", ""demographics"", ""drug"", ""environmental samples"", ""medical trials"", ""musculoskeletal medicine"", ""obstetrics"", ""reproductive health""]");, "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("DataverseNO","DataverseNO  is a curated, FAIR-aligned national generic repository for open research data from all academic disciplines. DataverseNO commits to facilitate that published data remain accessible and (re)usable in a long-term perspective. The repository is owned and operated by UiT The Arctic University of Norway. DataverseNO accepts submissions from researchers primarily from Norwegian research institutions. Datasets in DataverseNO are grouped into institutional collections as well as special collections. The technical infrastructure of the repository is based on the open source application Dataverse (https://dataverse.org), which is developed by an international developer and user community led by Harvard University.","https://www.re3data.org/api/v1/repository/r3d100012538","https://dataverse.no/","[""FAIR-enabling"", ""curated"", ""multi-disciplinary"", ""multi-institutional""]");, "[""DISCIPLINARY"",""INSTITUTIONAL"",""GENERALIST""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Mouse Phenome Database","The Mouse Phenome Database (MPD; phenome.jax.org) has characterizations of hundreds of strains of laboratory mice to facilitate translational discoveries and to assist in selection of strains for experimental studies.","https://www.re3data.org/api/v1/repository/r3d100010552","http://phenome.jax.org/","[""blood"", ""bone"", ""brain"", ""cancer"", ""cardiovascular"", ""cell"", ""endocrine"", ""genetic"", ""immune system"", ""muscle"", ""nervous system""]");, "[""DISCIPLINARY""]");
