INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("FlowRepository","FlowRepository is a web-based application accessible from a web browser that serves as an online database of flow cytometry experiments where users can query and download data collected and annotated according to the MIFlowCyt standard. It is primarily used as a data deposition place for experimental findings published in peer-reviewed journals in the flow cytometry field. FlowRepository is funded by the International Society for Advancement of Cytometry (ISAC) and powered by the Cytobank engine specifically extended for the purposes of this repository. FlowRepository has been developed by forking and extending Cytobank in 2011.","https://www.re3data.org/api/v1/repository/r3d100011280","https://flowrepository.org","[""FCM"", ""OMIP"", ""bioinformatics"", ""cytometry"", ""life sciences"", ""statistics""]", "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("DANDI","The US BRAIN Initiative archive for publishing and sharing neurophysiology data including electrophysiology, optophysiology, and behavioral time-series, and images from immunostaining experiments.","r3d100013638","https://dandiarchive.org/","[""FAIR"", ""electrophysiology"", ""neurophsyiology"", ""optophysiology""]", "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("OpenNeuro","The OpenNeuro project (formerly known as the OpenfMRI project) was established in 2010 to provide a resource for researchers interested in making their neuroimaging data openly available to the research community. It is managed by Russ Poldrack and Chris Gorgolewski of the Center for Reproducible Neuroscience at Stanford University. The project has been developed with funding from the National Science Foundation, National Institute of Drug Abuse, and the Laura and John Arnold Foundation.","https://www.re3data.org/api/v1/repository/r3d100010924","https://openneuro.org/","[""behavioral scientists"", ""biology"", ""brain"", ""brain chemistry"", ""cells"", ""magnetic resonance imaging"", ""molecular biology"", ""stimulants""]", "[""DISCIPLINARY""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("Biologic Specimen and Data Repository Information Coordinating Center","BioLINCC is the Biologic Specimen and Data Repository Coordinating Center. The center coordinates data and biospecimens from NHLBI-funded studies that are available for use in other approved studies. The center also creates teaching data sets from NHLBI-funded studies for use in training future biostatisticians.","https://www.re3data.org/api/v1/repository/r3d100010834","https://biolincc.nhlbi.nih.gov/home/","[""biological specimen"", ""blood"", ""diseases"", ""epidemiology"", ""health"", ""heart"", ""hematology"", ""lung""]", "[""DISCIPLINARY"",""INSTITUTIONAL""]");
INSERT INTO `repositories` (`name`, `description`, `uri`, `website`, `keywords`) VALUES ("ASU Library Research Data Repository","The Arizona State University (ASU) Research Data Repository provides a platform for ASU-affiliated researchers to share, preserve, cite, and make research data accessible and discoverable. The ASU Research Data Repository provides a permanent digital identifier for research data, which complies with data sharing policies. The repository is powered by the Dataverse open-source application, developed and used by Harvard University. Both the ASU Research Data Repository and the KEEP Institutional Repository are managed by the ASU Library to ensure research produced at Arizona State University is discoverable and accessible to the global community.","https://www.re3data.org/api/v1/repository/r3d100013468","https://dataverse.asu.edu","[""FAIR"", ""multidisciplinary""]", "[""INSTITUTIONAL""]");
