INSERT INTO `sections` (`templateId`, `name`, `introduction`, `requirements`, `guidance`, `displayOrder`, `isDirty`, `createdById`, `created`, `modifiedById`, `modified`) (SELECT `templates`.`id`, 'Data products to be produced including Observational Data, Event Lists, and Simulation Output', '<p>For each data product, provide: &nbsp;(a) a short description of the product; (b) the scientific importance of the product (one sentence); (c) the type of data (e.g., time series, spectrograms, images); (d) estimated data volume; (e) planned documentation, e.g., for methodology and algorithms used; (f) expected delivery time, in project years; and&nbsp; (g) the archive you plan to work with, namely one or more of the Space Physics Data Facility (SPDF; <a href="https://spdf.gsfc.nasa.gov/">https://spdf.gsfc.nasa.gov/</a>), the Solar Data Analysis Center ( SDAC, <a href="https://umbra.nascom.nasa.gov/index.html/">https://umbra.nascom.nasa.gov/index.html/</a>), and the Community Coordinated Modeling Center (CCMC; <a href="https://ccmc.gsfc.nasa.gov/">https://ccmc.gsfc.nasa.gov/</a>).&nbsp;</p>', '', '', 1, 0, `templates`.`createdById`, `templates`.`created`, `templates`.`modifiedById`, `templates`.`modified` FROM `templates` WHERE `ownerId` = 'https://ror.org/027ka1x80' AND SUBSTRING(`name`, 1, 100) = 'NASA Heliophysics Science Division - Research Opportunities in Space and Earth Science (ROSES)');
