-- Assign the email domain for each affiliation based on the homepage
  `uri`,
  TRIM(REGEXP_REPLACE(
    REGEXP_SUBSTR(`homepage`, '(?<=//)[^/]+'),
    '^www\\.',
    ''
  )) AS emailDomain,
  @default_super_id, CURDATE(), @default_super_id, CURDATE()
  FROM `affiliations` WHERE homepage LIKE 'http%'
);

