INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (309, 59, 254, '<p>State whether an Institutional Review Board (IRB) or analogous review body has reviewed the genomic data sharing aspects of your project, or provide a timeline for such review. &#8232;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
