INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (307, 59, 252, '<p>Identify the data repositories to which the data will be submitted, and for human data, whether the data will be available through unrestricted or controlled-access. &#8232;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
