INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (364, 73, 301, '<p>Policies and provisions for re-use, re-distribution, and the production of derivatives.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (363, 73, 300, '<p>Standards to be used for data and metadata format and content (where existing standards are absent or deemed inadequate, this should be documented along with any proposed solutions or remedies).</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (362, 73, 299, '<p>Types of data, samples, physical collections, software, curriculum materials, and other materials to be produced in the course of the project.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (648, 100, 436, '<p>Are any restrictions on data sharing required?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What action will you take to overcome or minimize restrictions?</li><li>For how long do you need exclusive use of the data and why?</li><li>Will a data sharing agreement (or equivalent) be required?</li></ul><p>Outline any expected difficulties in sharing data with acknowledged long-term value, along with causes and possible measures to overcome these. Restrictions may be due to confidentiality, lack of consent agreements or IPR, for example. Consider whether a non-disclosure agreement would give sufficient protection for confidential data.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (649, 100, 437, '<p>Who will be responsible for data management?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Who is responsible for implementing the DMP, and ensuring it is reviewed and revised?</li><li>Who will be responsible for each data management activity?</li><li>How will responsibilities be split across partner sites in collaborative research projects?</li><li>Will data ownership and responsibilities for research data management be part of any consortium agreement or contract agreed between partners?</li></ul><p>Outline the roles and responsibilities for all activities e.g. data capture, metadata production, data quality, storage and backup, data archiving and data sharing. Consider who will be responsible for ensuring relevant policies will be respected. Individuals should be named where possible.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (650, 100, 437, '<p>What resources will you require to deliver your plan?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Is additional specialist expertise (or training for existing staff) required?</li><li>Do you require hardware or software which is additional or exceptional to existing institutional provision?</li><li>Will charges be applied by data repositories?</li></ul><p>Carefully consider any resources needed to deliver the plan, e.g. software, hardware, technical expertise, etc. Where dedicated resources are needed, these should be outlined and justified.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (647, 100, 436, '<p>How will you share the data?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>How will potential users find out about your data?</li><li>With whom will you share the data, and under what conditions?</li><li>Will you share data via a repository, handle requests directly or use another mechanism?</li><li>When will you make the data available?</li><li>Will you pursue getting a persistent identifier for your data?</li></ul><p>Consider where, how, and to whom data with acknowledged long-term value should be made available. The methods used to share data will be dependent on a number of factors such as the type, size, complexity and sensitivity of data. If possible, mention earlier examples to show a track record of effective data sharing. Consider how people might acknowledge the reuse of your data.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (646, 100, 435, '<p>What is the long-term preservation plan for the dataset?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Where e.g. in which repository or archive will the data be held?</li><li>What costs if any will your selected data repository or archive charge?</li><li>Have you budgeted for time and effort to prepare the data for sharing / preservation?</li></ul><p>Consider how datasets that have long-term value will be preserved and curated beyond the lifetime of the grant. Also outline the plans for preparing and documenting data for sharing and archiving. If you do not propose to use an established repository, the data management plan should demonstrate that resources and systems will be in place to enable the data to be curated effectively beyond the lifetime of the grant.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (645, 100, 435, '<p>Which data are of long-term value and should be retained, shared, and/or preserved?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What data must be retained/destroyed for contractual, legal, or regulatory purposes?</li><li>How will you decide what other data to keep?</li><li>What are the foreseeable research uses for the data?</li><li>How long will the data be retained and preserved?</li></ul><p>Consider how the data may be reused e.g. to validate your research findings, conduct new studies, or for teaching. Decide which data to keep and for how long. This could be based on any obligations to retain certain data, the potential reuse value, what is economically viable to keep, and any additional effort required to prepare the data for data sharing and preservation. Remember to consider any additional effort required to prepare the data for sharing and preservation, such as changing file formats.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (644, 100, 434, '<p>How will you manage access and security?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What are the risks to data security and how will these be managed?</li><li>How will you control access to keep the data secure?</li><li>How will you ensure that collaborators can access your data securely?</li><li>If creating or collecting data in the field how will you ensure its safe transfer into your main secured systems?</li></ul><p>If your data is confidential (e.g. personal data not already in the public domain, confidential information or trade secrets), you should outline any appropriate security measures and note any formal standards that you will comply with e.g. ISO 27001."</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (643, 100, 434, '<p>How will the data be stored and backed up during the research?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Do you have sufficient storage or will you need to include charges for additional services?</li><li>How will the data be backed up?</li><li>Who will be responsible for backup and recovery?</li><li>How will the data be recovered in the event of an incident?</li></ul><p>State how often the data will be backed up and to which locations. How many copies are being made? Storing data on laptops, computer hard drives or external storage devices alone is very risky. The use of robust, managed storage provided by university IT teams is preferable. Similarly, it is normally better to use automatic backup services provided by IT Services than rely on manual processes. If you choose to use a third-party service, you should ensure that this does not conflict with any funder, institutional, departmental or group policies, for example in terms of the legal jurisdiction in which data are held or the protection of sensitive data.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (642, 100, 433, '<p>How will you manage copyright and Intellectual Property Rights (IP/IPR) issues?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Who owns the data?</li><li>How will the data be licensed for reuse?</li><li>Are there any restrictions on the reuse of third-party data?</li><li>Will data sharing be postponed / restricted e.g. to publish or seek patents?</li></ul><p>State who will own the copyright and IPR of any data that you will collect or create, along with the licence(s) for its use and reuse. For multi-partner projects, IPR ownership may be worth covering in a consortium agreement. Consider any relevant funder, institutional, departmental or group policies on copyright or IPR. Also consider permissions to reuse third-party data and any restrictions needed on data sharing.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (641, 100, 433, '<p>How will you manage any ethical issues?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>Have you gained consent for data preservation and sharing?</li><li>How will you protect the identity of participants if required? e.g. via anonymization</li><li>How will sensitive data be handled to ensure it is stored and transferred securely?</li></ul><p>Ethical issues affect how you store data, who can see/use it and how long it is kept. Managing ethical concerns may include: anonymization of data; referral to departmental or institutional ethics committees; and formal consent agreements. You should show that you are aware of any issues and have planned accordingly. If you are carrying out research involving human participants, you must also ensure that consent is requested to allow data to be shared and reused.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (640, 100, 432, '<p>What documentation and metadata will accompany the data?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What information is needed for the data to be to be read and interpreted in the future?</li><li>How will you capture / create this documentation and metadata?</li><li>What metadata standards will you use and why?</li></ul><p>Describe the types of documentation that will accompany the data to help secondary users to understand and reuse it. This should at least include basic details that will help people to find the data, including who created or contributed to the data, its title, date of creation and under what conditions it can be accessed.</p><p>Documentation may also include details on the methodology used, analytical and procedural information, definitions of variables, vocabularies, units of measurement, any assumptions made, and the format and file type of the data. Consider how you will capture this information and where it will be recorded. Wherever possible you should identify and use existing community standards.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (639, 100, 431, '<p>How will the data be collected or created?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What standards or methodologies will you use?</li><li>How will you structure and name your folders and files?</li><li>How will you handle versioning?</li><li>What quality assurance processes will you adopt?</li></ul><p>Outline how the data will be collected/created and which community data standards (if any) will be used. Consider how the data will be organized during the project, mentioning for example naming conventions, version control and folder structures. Explain how the consistency and quality of data collection will be controlled and documented. This may include processes such as calibration, repeat samples or measurements, standardized data capture or recording, data entry validation, peer review of data or representation with controlled vocabularies.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (638, 100, 431, '<p>What data will you collect or create?</p>', '', '<p>&nbsp;</p><p class="guidance_header">Questions to consider:</p><ul><li>What type, format and volume of data?</li><li>Do your chosen formats and software enable sharing and long-term access to the data?</li><li>Are there any existing data that you can reuse?</li></ul><p>Give a brief description of the data, including any existing data or third-party sources that will be used, in each case noting its content, type and coverage. Outline and justify your choice of format and consider the implications of data format and data volumes in terms of storage, backup and access.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 1, @default_admin_id, CURDATE(), @default_admin_id, CURDATE());
