INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2710, 310, 1369, '<p>List the repository or location where researchers can access the tools, software and/ or code and how they can or will be accessed. Access examples include open source and freely available, generally available for a fee in the marketplace, available only from the research team. &nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
