INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2708, 310, 1368, '<p>Use this section to plan for data submission to and sharing from the repositor(ies) listed in the data type section. Consider publication timelines, performance period, and data repository review and release timelines when planning data submissions and communicate through Plan updates if there are major changes to planned timelines. Shared scientific data should be made accessible as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first.</p><p>&nbsp;</p><table style="border-collapse: collapse; width: 100%; height: 36px;" border="1"><colgroup><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.1732%;"></colgroup><tbody><tr style="height: 18px;"><td style="height: 18px;"><strong>Data Repository</strong></td><td style="height: 18px;"><strong>Expected number and frequency of submissions</strong><br><strong>&nbsp;</strong></td><td style="height: 18px;"><strong>Projected timeline for first submission to repository</strong></td><td style="height: 18px;"><strong>Projected timeline for last submission to repository</strong></td><td style="height: 18px;"><strong>Target timelines for release</strong></td><td style="height: 18px;"><strong>Type of persistent IDs that will be used for data releases, to enable findability and citation of shared datasets</strong></td><td style="height: 18px;"><strong>If you will be contributing data to a dataset that is already registered with a data repository, provide that ID</strong></td></tr><tr style="height: 18px;"><td style="height: 18px;"><em>Name the data repository described in the Data Type section. Add additional rows as necessary.</em></td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;"><em>Releases associated with data underlying publications, other scheduled releases, and remaining scientific data by the end of the performance period.&nbsp;</em></td><td style="height: 18px;"><em>Samples include dataset-level digital object identifier (DOI), accession number, globally unique identifier.</em></td><td style="height: 18px;">&nbsp;</td></tr></tbody></table>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2707, 310, 1367, '<p>&nbsp;&nbsp;</p><table style="border-collapse: collapse; width: 100%; height: 90px;" border="1"><colgroup><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"></colgroup><tbody><tr style="height: 72px;"><td style="height: 72px;"><strong>Data Type</strong></td><td style="height: 72px;"><strong>Brief Description</strong></td><td style="height: 72px;"><strong>Organism, Model, or Other Sources</strong></td><td style="height: 72px;"><strong>Amount of Data</strong></td><td style="height: 72px;"><strong>Standards</strong></td><td style="height: 72px;"><strong>Shared Formats</strong></td><td style="height: 72px;"><strong>Data Repository</strong></td><td style="height: 72px;"><strong>Data Access Type</strong></td></tr><tr style="height: 18px;"><td style="height: 18px;"><em>Define each data type add additional rows to describe multiple data types</em></td><td style="height: 18px;"><em>Summarize how the data of this type will be managed and prepared for sharing</em></td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;"><em>Projected number of participants or samples from which the data will be generated or other appropriate metrics to describe the scale of the data</em></td><td style="height: 18px;"><em>List the standards that will be applied to the scientific data and associated metadata if standards exist</em></td><td style="height: 18px;"><em>Formats of data to be submitted to the data repository</em></td><td style="height: 18px;"><em>Name the repository where scientific data and metadata will be preserved and shared</em></td><td style="height: 18px;"><em>Examples include open, registered, controlled or enclave&nbsp;</em></td></tr></tbody></table>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2706, 310, 1366, '<p>If no, provide a justification and explain the factors that determine which scientific data will not be shared:</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 5, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2705, 310, 1366, '<p>Will all scientific data generated by the research project be shared in a data repository that makes data available to the larger research community?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 4, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2704, 310, 1366, '<p>If yes, describe data source(s) and provide a dataset identifier (if available). For example, Digital object identifier (DOI, accession number, globally unique identifier, etc.&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2703, 310, 1366, '<p>Will you be performing secondary analysis of extant data to generate scientific data for this project?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2702, 310, 1366, '<p>Describe project-associated documentation that will be made accessible to facilitate interpretation of the scientific data and where the document will be shared. Examples include study protocols and data collection instruments.&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2701, 310, 1365, '<p>Describe what measures will be taken to protect the privacy of participants and the confidentiality of the data. Examples include de-identification, Certificates of Confidentiality, and other protective measures.&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 4, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2699, 310, 1365, '<p>Will the individuals from whom the data are collected or derived have provided informed consent for the collection of the data?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No (Describe under what auspices data will or have been collected in the space below)", "value": "No (Describe under what auspices data will or have been collected in the space below)", "selected": 0}], "attributes": {"multiple": 0}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2698, 310, 1365, '<p>Will the project be managing and/or sharing data derived from humans?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (2697, 310, 1364, '<p>If yes, list the individual(s) and their organization(s) and describe their role(s) and responsibilities. Examples include a data coordinating center, institutional librarians, or investigators on other NIH awards (list award numbers, if relevant).&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 8, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3011, 339, 1535, '<p>Types and amount of scientific data expected to be generated in the project: <em>Summarize the types and estimated amount of scientific data expected to be generated in the project.&nbsp;</em></p><p>Describe data in general terms that address the type and amount/size of scientific data expected to be collected and used in the project (e.g., 256-channel EEG data and fMRI images from ~50 research participants). Descriptions may indicate the data modality (e.g., imaging, genomic, mobile, survey), level of aggregation (e.g., individual, aggregated, summarized), and/or the degree of data processing that has occurred (i.e., how raw or processed the data will be).</p>', '', '<p><strong>NIH Guidance</strong></p><p><br>The final 2023 NIH DMS Policy (<a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-013.html" target="_blank" rel="noopener">NOT-OD-21-013</a>) defines scientific data as the recorded factual material commonly accepted in the scientific community as of sufficient quality to validate and replicate research findings, regardless of whether the data are used to support scholarly publications.&nbsp;</p><p>&nbsp;</p><p><strong>NIMH Guidance</strong></p><p><br>Please check the 2023 NIMH DMS policy (<a href="https://grants.nih.gov/grants/guide/notice-files/NOT-MH-23-100.html" target="_blank" rel="noopener">NOT-MH-23-100</a>) for NIMH-specific requirements in addition to the 2023 NIH DMS policy (<a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-013.html" target="_blank" rel="noopener">NOT-OD-21-013</a>). &nbsp;</p><p><br>NIMH expects data sharing plans to also include a description of the standard(s) and/or data dictionaries that will be used to describe the data set, as well as a proposed schedule to validate that the data are compliant with the data dictionary that is being used.</p><p><br>NIMH has certain requirements for these standards such as a set of common data elements (see <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-MH-20-067.html" target="_blank" rel="noopener">NOT-MH-20-067 for non-HIV research</a>; for HIV-related research, see <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-MH-23-105.html" target="_blank" rel="noopener">NOT-MH-23-105</a>).</p><p><br>The NDA provides an online Data Dictionary with a searchable interface to find data structures that awardees are expected to use for new data collection. The NDA Data Dictionary is updated as researchers extend existing data collection instruments or create new instruments. For more information, see the <a href="https://nda.nih.gov/nda/nimh-common-data-elements.html" target="_blank" rel="noopener">NDA NIMH Common Data Elements page</a>.</p><p><br><strong>NIH Genomic Data Sharing (GDS) Policy Considerations</strong></p><p><br>Check if your research is subject to NIH GDS (Genomic Data Sharing) policy using <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/does-gds-apply-to-my-research" target="_blank" rel="noopener">this criteria</a> and list those data and the levels of processing here.&nbsp;</p><p><br>Individual NIH Institutes and Centers (IC) may have additional expectations or requirements for genomic data sharing as well. NIMH expects genomic data to be deposited at the NIMH Data Archive (NDA) unless NIMH agrees to a different database.&nbsp;</p><p>&nbsp;</p>', '<p>All example DMSPs can be found <a title="at the end of the NIMH data sharing page" href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener">at the end of the NIMH data sharing page</a> or in the<a title=" Sample plans section of the sharing.nih.gov site." href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener"> sample plans section of the sharing.nih.gov site.</a></p><ul><li>For human clinical and/or MRI data, please refer to the&nbsp; <a title="Clinical imaging example" href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging example</a> on the NIMH site, also known as <a title="Sample Plan A" href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan A</a> on the NIH sharing site.</li><li>For human genomic data, consider the <a title="human genomics example" href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">human genomics example</a> on the NIMH site, also known as <a title="Sample Plan B" href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan B</a> on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the<a title=" non-human genomics example " href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener"> non-human genomics example </a>on the NIMH site, also known as <a title="Sample Plan C" href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.</li><li>For secondary data analysis, consider the<a title="Secondary data example " href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Secondary_Data_Analysis_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener"> secondary data example</a> on the NIMH site, also known as <a title=" Sample Plan D " href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan D </a>on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3012, 339, 1535, '<p>Scientific data that will be preserved and shared, and the rationale for doing so:<em> Describe which scientific data from the project will be preserved and shared and provide the rationale for this decision.</em></p>', '', '<p><strong>NIH Guidance</strong></p><p><br>Per the <a title="Policy" href="https://grants.nih.gov/grants/guide/notice-files/not-od-21-013.html" target="_blank" rel="noopener">Policy</a>, even those scientific data not used to support a publication are considered to be scientific data and to fall within the final DMS Policy&rsquo;s scope.&nbsp;</p><p><br>NIH does not anticipate that researchers will preserve and share all scientific data generated in a study. Researchers should decide which scientific data to preserve and share based on ethical, legal, and technical factors that may affect the extent to which scientific data are preserved and shared. Provide the rationale for these decisions.&nbsp;</p><p><strong>Additional Guidance from DMPTool</strong></p><p>If human subjects data will be collected and only de-identified subsets are to be shared, consider specific de-identification approaches that fit the population and purposes. Guidance on protecting privacy is at &nbsp;<a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html" target="_blank" rel="noopener">NOT-OD-22-213</a>.</p><p>&nbsp;</p><p><strong>NIH GDS Policy Considerations for NIMH</strong></p><p><br>If you are generating genomic data, follow specific sharing requirements (data submission and release expectation) under the NIH GDS policy (<a title="five levels of processing and associated expectations for data submission and release" href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations" target="_blank" rel="noopener">five levels of processing and associated expectations for data submission and release</a>) &nbsp; &nbsp;</p><p><br>NIMH expects genomic data to be deposited at the <a href="https://nda.nih.gov/" target="_blank" rel="noopener">NIMH Data Archive (NDA)</a> unless NIMH agrees to a different database. All data associated with new projects at the <a title="NIMH Repository and Genomics Resource" href="https://www.nimhgenetics.org/" target="_blank" rel="noopener">NIMH Repository and Genomics Resource</a> will also be deposited in the <a href="https://nda.nih.gov/" target="_blank" rel="noopener">NDA</a>. Awardees who are measuring human genomic data are required to register with the Database of Genotypes and Phenotypes (dbGaP).</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3013, 339, 1535, '<p>Metadata, other relevant data, and associated documentation: Briefly list the metadata, other relevant data, and any associated documentation (e.g., study protocols and data collection instruments) that will be made accessible to facilitate interpretation of the scientific data.</p>', '', '<p><strong>NIH</strong></p><p>In addition to the documentation examples, consider metadata that will provide additional information intended to make scientific data interpretable and reusable (e.g., date, independent sample and variable construction and description, methodology, data provenance, data transformations, any intermediate or descriptive observational variables).</p><p><strong>NIMH Guidance</strong></p><p>Investigators should review the planning section of the <a href="https://nda.nih.gov/contribute/contribute-data.html" target="_blank" rel="noopener">NIMH Data Archive</a> website, Use of the NDA includes the <a title="NDA data harmonization approach" href="https://nda.nih.gov/nda/nda-data-harmonization-approach.html" target="_blank" rel="noopener">NDA data harmonization approach</a> for metadata and documentation.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3014, 339, 1536, '<p>State whether specialized tools, software, and/or code are needed to access or manipulate shared scientific data, and if so, provide the name(s) of the needed tool(s) and software and specify how they can be accessed.</p>', '', '<p><strong>Additional Guidance from DMPTool</strong></p><p>Tool(s) and software should be identified; then plans should specify how the tools can be accessed (e.g., open source and freely available, generally available for a fee in the marketplace, available only from the research team). When known, the longevity or period of time for which custom or proprietary tools will be available should be addressed.</p><p><br>In addition, file formats in which data are saved in a digital format can be divided into two general categories.</p><ul><li>Proprietary - The specification of the data encoding format is not released or is restricted in some way. Proprietary formats can only be easily opened and manipulated by particular software tools.&nbsp;</li><li>Open - The specification of the data encoding format which can be used and implemented by anyone. Open formats can often be easily opened and manipulated by a large number of software tools.</li></ul>', '<p>All example DMSPs can be found <a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener">at the end of the NIMH data sharing page</a> or in the <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">sample plans section of the sharing.nih.gov</a> site.</p><p>For human clinical and/or MRI data, please refer to the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan A</a> on the NIH sharing site.</p><ul><li>For human genomic data, consider the<a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener"> human genomics example</a> on the NIMH site, also known as<a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener"> Sample Plan B </a>on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the<a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener"> non-human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.</li><li>For secondary data analysis, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Secondary_Data_Analysis_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">secondary data example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan D</a> on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3015, 339, 1537, '<p>State what common data standards will be applied to the scientific data and associated metadata to enable interoperability of datasets and resources, and describe how these data standards will be applied. If applicable, indicate that no consensus standards exist.</p>', '', '<p><strong>NIH Guidance</strong></p><p>While many scientific fields have developed and adopted common data standards, others have not. In such cases, the Plan may indicate that no consensus data standards exist for the scientific data and metadata to be generated, preserved, and shared.</p><p><br><strong>NIMH Guidance</strong></p><p>Applicants are strongly encouraged to use clinical and phenotypic data collection instruments/data dictionaries that have already been defined rather than create new versions of those data dictionaries. There are several required data collection instruments, mostly related to demographic and sample information, that must be used by all researchers for data harmonization purposes except for HIV-related applications (https://nda.nih.gov/contribute/harmonization-standards.html).&nbsp;</p><p><br>The NIHM Data Archive (NDA) &nbsp;provides its own standards for metadata and data structures. The standards that a PI intends to use for compatibility with the NDA should be briefly described in this section.&nbsp;</p><p><br><strong>Additional Guidance from DMPTool</strong></p><p><br>A <em>standard</em> specifies how exactly data and related materials should be stored, organized, and described. In the context of research data, the term typically refers to the use of specific and well-defined formats, schemas, vocabularies, and ontologies in the description and organization of data.&nbsp;</p><p><br>Furthermore, if formal standards such as specific imaging or sequencing filetypes, descriptive metadata, collection formats, etc., beyond the NDA standards will be used, that should also be addressed in this Element.</p>', '<p>All example DMSPs can be found <a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener">at the end of the NIMH data sharing page</a> or in the <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">sample plans section of the sharing.nih.gov site.</a></p><ul><li>For human clinical and/or MRI data, please refer to the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging</a> example on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan A</a> on the NIH sharing site.</li><li>For human genomic data, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan B</a> on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">non-human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.</li><li>For secondary data analysis, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Secondary_Data_Analysis_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">secondary data example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan D</a> on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3016, 339, 1538, '<p>Repository where scientific data and metadata will be archived: Provide the name of the repository(ies) where scientific data and metadata arising from the project will be archived; see <a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository" target="_blank" rel="noopener">Selecting a Data Repository</a>)</p>', '', '<p><strong>NIH Guidance</strong></p><p>NIH has provided additional information to assist in selecting suitable repositories for scientific data resulting from funded research: <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html" target="_blank" rel="noopener">NOT-OD-21-016</a>. <a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository" target="_blank" rel="noopener">Selecting a Data Repository</a> Page of the NIH sharing website.</p><p><strong>NIMH Guidance</strong></p><p>Research funded by the NIMH are required to deposit all raw and analyzed data (including, but not limited to, clinical, genomic, imaging, and phenotypic data) from studies involving human subjects into the <a href="https://nda.nih.gov/" target="_blank" rel="noopener">NIMH Data Archive (NDA)</a>.</p><p><br><strong>NIMH Guidance on NIH GDS Policy Considerations</strong></p><p><br><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-MH-23-100.html" target="_blank" rel="noopener">NOT-MH-23-100</a> requires that the NIMH Data Archive (NDA) serve as the repository for genomic data funded by the NIMH unless the NIMH approves a different data repository during the negotiation of the terms and conditions of the award.</p><p>Awardees who are measuring human genomic data are required to register with the Database of Genotypes and Phenotypes (<a href="https://www.ncbi.nlm.nih.gov/projects/gap/cgi-bin/GetPdf.cgi?document_name=HowToSubmit.pdf" target="_blank" rel="noopener">dbGaP Submission Process</a>). After registration, all data (including, but not limited to, clinical, genomic, imaging, and phenotypic data) will be deposited in the NDA. A link to NDA will be added to the dbGaP registration. Aggregating the genomic data in a single cloud-based data archive will facilitate the re-analysis, replication, and additional analyses of these important data sets. Computational credits may be available to conduct these analyses in the cloud.&nbsp;</p><p><br>All data associated with new projects at the <a href="https://www.nimhgenetics.org/" target="_blank" rel="noopener">NIMH Repository and Genomics Resource</a> will be deposited in the NDA. Appropriate data will then be transmitted to the NIMH Repository and Genomics Resource for quality control and to allow the research community to identify samples that are relevant to their research efforts.</p><p><br><strong>Additional Guidance from DMPTool</strong></p><p>The NIMH notice of data sharing policy does not provide specific guidance on access and preservation of non-human data for the data management and sharing policy. Consult the <a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener">NIMH data sharing website</a> or the<a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/repositories-for-sharing-scientific-data" target="_blank" rel="noopener"> trans-NIH repositories</a> list for further possibilities.</p>', '<p>All example DMSPs can be found<a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener"> at the end of the NIMH data sharing page</a> or in the <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">sample plans section of the sharing.nih.gov site</a>.</p><ul><li>For human clinical and/or MRI data, please refer to the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging example </a>on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan A </a>on the NIH sharing site.</li><li>For human genomic data, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan B</a> on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">non-human genomics example </a>on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3017, 339, 1538, '<p><strong>How scientific data will be findable and identifiable</strong>: Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier or other standard indexing tools.</p>', '', '<p><strong>NIH Guidance</strong></p><p>Unique Persistent Identifiers: The repository assigns datasets a citable, unique persistent identifier, such as a digital object identifier (DOI) or accession number, to support data discovery, reporting, and research assessment. The identifier points to a persistent landing page that remains accessible even if the dataset is de-accessioned or no longer available.</p><p><br><strong>Additional Guidance from DMPTool</strong></p><p>NIMH Data Archive data collections have DOIs to help make the data in them findable and identifiable.&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3018, 339, 1538, '<p><strong>When and how long the scientific data will be made available</strong>: Describe when the scientific data will be made available to other users (i.e., no later than time of an associated publication or end of the performance period, whichever comes first) and for how long data will be available.</p>', '', '<p><strong>NIH Guidance&nbsp;</strong></p><p><br>NIH encourages scientific data be shared as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first. The NIMH further specifies that data must be shared with the research community when papers using the data have been accepted for publication or at the end of the award period (including the first no cost extension). Researchers are encouraged to consider relevant requirements and expectations (e.g., data repository policies, award record retention requirements, journal policies) as guidance for the minimum time frame scientific data should be made available. NIH encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public. Identify any differences in timelines for different subsets of scientific data to be shared. &nbsp; &nbsp;&nbsp;</p><p><br><strong>NIH GDS Policy Considerations &nbsp; &nbsp;&nbsp;</strong></p><p><br>Genomic data is subject to <a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations" target="_blank" rel="noopener">further guidance on release expectations and timelines</a>. &nbsp; &nbsp; &nbsp;</p><p><br><strong>&nbsp;NIMH Guidance</strong></p><p>The general expectation is that data from NIMH-funded awards that involve human subjects will be submitted to NDA every 6 months throughout the duration of the award (typically January and July). Awardees will provide a Data Submission Agreement signed by the principal investigator and an institutional business official within 6 months of the notice of award. Although submission does not lead to release of the data, awardees are encouraged to share basic demographic and raw baseline data shortly after data submission to encourage collaborations in the research community.</p><p>In addition to regular submission of data associated with an award, awardees are expected to separately submit to NDA the specific data that was used f<a href="https://nda.nih.gov/nda/manuscript-preparation.html" target="_blank" rel="noopener">or each resulting publication by creating an NDA Study</a>.&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3019, 339, 1539, '<p><strong>Factors affecting subsequent access, distribution, or reuse of scientific data</strong>: NIH expects that in drafting Plans, researchers maximize the appropriate sharing of scientific data. Describe and justify any applicable factors or data use limitations affecting subsequent access, distribution, or reuse of scientific data related to informed consent, privacy and confidentiality protections, and any other considerations that may limit the extent of data sharing. See <a href="https://sharing.nih.gov/faqs#/data-management-and-sharing-policy.htm" target="_blank" rel="noopener">Frequently Asked Questions</a> for examples of justifiable reasons for limiting sharing of data.</p>', '', '<p><strong>NIH Guidance</strong></p><p>The DMS Policy acknowledges certain factors (i.e., legal, ethical, or technical) that may affect the extent to which scientific data are preserved and shared.</p><p><br>In addition, <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html" target="_blank" rel="noopener">NOT-OD-22-213 addresses specific considerations about human subjects privacy.</a></p><p><strong>Additional Guidance from DMPTool&nbsp;</strong></p><p>This is the section to describe what legal, ethical, or technical issues may require limiting the sharing of your data. Examples may include ethical considerations such as IACUC restrictions on sharing videos or images of procedures. Other nonhuman data factors affecting distribution may include existing legal limits such as data licenses or use agreements or technical limits about the size or structure of the data.</p><p><br>For human data, the NIMH requires the application of privacy protections through the use of the NDA. It is likely to be sufficient in this subsection to say that human ethics require the use of the NIMH Data Archive for privacy.&nbsp;</p><p><strong>NIH GDS Policy Considerations</strong></p><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing" target="_blank" rel="noopener">Genomic data may have further considerations</a> to address. &nbsp;The NIH now expects a single data sharing plan at the time of funding application to satisfy both the Genomic Data Sharing (<a href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html" target="_blank" rel="noopener">GDS</a>) Policy and the DMS Policy (per <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-198.html" target="_blank" rel="noopener">NOT-OD-22-198</a>). How to access genomic data varies depending on which repository you selected. Please refer to the <a href="https://sharing.nih.gov/accessing-data/accessing-genomic-data/accessing-genomic-data-from-nih-repositories" target="_blank" rel="noopener">Accessing Genomic Data from NIH Repositories </a>page on the NIH sharing site.</p>', '<p>All example DMSPs can be found<a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener"> at the end of the NIMH data sharing page</a> or in the <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">sample plans section of the sharing.nih.gov site.</a></p><ul><li>For human clinical and/or MRI data, please refer to the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging</a> example on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan A </a>on the NIH sharing site.</li><li>For human genomic data, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan B</a> on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">non-human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.<br>For secondary data analysis, consider the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Secondary_Data_Analysis_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">secondary data example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan D </a>on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3020, 339, 1539, '<p><strong>Whether access to scientific data will be controlled</strong>: State whether access to the scientific data will be controlled (i.e., made available by a data repository only after approval).</p><p>&nbsp;</p>', '', '<p><a href="https://nda.nih.gov/nda/access-data-info.html" target="_blank" rel="noopener">The NDA uses controlled access</a>. Summary information on the data shared in NDA will be available in the NDA Query Tool without the need for an NDA user account. To request access to record-level human subject data, researchers must submit a Data Access Request.</p><p><strong>Additional guidance from DMPTool</strong></p><p>Check the repository you intend to use to find out more about whether and how the repository supports controlled access.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3021, 339, 1539, '<p><strong>Protections for privacy, rights, and confidentiality of human research participants</strong>:<br>If generating scientific data derived from humans, describe how the privacy, rights, and confidentiality of human research participants will be protected (e.g., through de-identification, Certificates of Confidentiality, and other protective measures).</p>', '', '<p><strong>NIH Guidance</strong></p><p>Effective data stewardship and protection of human research participant (hereinafter &ldquo;participant&rdquo;) privacy are achieved in tandem through responsible scientific data sharing practices. Accordingly, <a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html" target="_blank" rel="noopener">NIH has developed supplemental information to the DMS Policy to assist researchers</a> in responsible data sharing by establishing 1) operational principles for protecting participants&rsquo; privacy when sharing scientific data, 2) best practices for implementing these principles, and 3) points to consider for choosing whether to designate scientific data for controlled access.</p><p><strong>NIMH Guidance</strong></p><p>Applicants should also plan to collect the data needed<a href="https://nda.nih.gov/contribute/harmonization-standards.html#guid" target="_blank" rel="noopener"> to generate global unique identifiers (GUIDs)</a> for each study subject. â€‹â€‹The<a href="https://nda.nih.gov/nda/using-the-nda-guid.html" target="_blank" rel="noopener"> GUID, or Global Unique Identifier</a>, is used as an identifier for a research participant. The GUID provides a secure mechanism to link research participants within and across research project datasets in NDA.</p><p><br>Informed consent documents should <a href="https://nda.nih.gov/contribute/contribute-data.html#infocon" target="_blank" rel="noopener">describe how study data will be shared with NDA and the research community</a>. The NDA has provided a <a href="https://s3.amazonaws.com/nda.nih.gov/Documents/Sample_NDA_InfCon_Language.docx" target="_blank" rel="noopener">plain-language description</a> of the NDA as an example when creating informed consent language.</p><p>NIMH also expects these&nbsp;<a href="https://www.nimh.nih.gov/funding/managing-your-grant/human-subjects-research-issues" target="_blank" rel="noopener">additional points to be considered in human subjects research</a>.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3022, 339, 1540, '<p>Describe how compliance with this Plan will be monitored and managed, frequency of oversight, and by&nbsp;whom at your institution (e.g., titles, roles).</p><p>&nbsp;</p>', '', '<p><strong>NIH</strong> <strong>Guidance</strong><br>This element refers to oversight by the funded institution, rather than by NIH. The DMS Policy does not create any expectations about who will be responsible for Plan oversight at the institution.</p><p><strong>Additional guidance from DMPTool&nbsp;</strong><br>Please confer with your Office of Sponsored Programs, Office of Research, etc., about any additional oversight considerations. Oversight of human data with the NIMH Data Archive may have specific factors to address. All NIMH applicants should consult local campus departments and policies about oversight.</p>', '<p>All example DMSPs can be found <a href="https://www.nimh.nih.gov/funding/managing-your-grant/nimh-data-management-and-sharing-for-applicants-and-awardees" target="_blank" rel="noopener">at the end of the NIMH data sharing page</a> or in the <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">sample plans section of the sharing.nih.gov site</a>.</p><ul><li>For human clinical and/or MRI data, please refer to the <a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_MRI_and_Clinical_Assessments_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">clinical imaging example</a> on the NIMH site, also known as<a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener"> Sample Plan A </a>on the NIH sharing site.</li><li>For human genomic data, consider the&nbsp;<a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Human_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan B</a> on the NIH sharing site.</li><li>For genomic data from a non-human source, consider the&nbsp;<a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Mouse_Genomic_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">non-human genomics example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan C</a> on the NIH sharing site.</li><li>For secondary data analysis, consider the&nbsp;<a href="https://www.nimh.nih.gov/sites/default/files/documents/funding/managing-your-grant/resource-sharing-docs/Secondary_Data_Analysis_Template_v3_%28no_hyperlinks%29_2022.docx" target="_blank" rel="noopener">secondary data example</a> on the NIMH site, also known as <a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/writing-a-data-management-and-sharing-plan#sample-plans" target="_blank" rel="noopener">Sample Plan D</a> on the NIH sharing site.</li></ul>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3023, 339, 1541, '<p>The Data Management and Sharing Plan must propose a schedule to validate the quality of the data being uploaded so these data are compliant with the data dictionary or other standards that are being used.&nbsp;</p>', '', '<p><strong>NIMH Guidance</strong></p><p>All NIMH DMS Plans must propose a schedule to validate the quality of the data being uploaded so these data are compliant with the data dictionary or other standards that are being used. In cases where a data dictionary has been defined in the <a href="https://nda.nih.gov/vt/" target="_blank" rel="noopener">NIMH Data Archive, the NDA Data Validation and Uploading Tool</a> should be used to ensure your data files are harmonized to the NDA Data Dictionary. Compliance with the approved data management and sharing plan will become a term and condition in the Notice of Award and will be monitored by the NIMH throughout the duration of the award.</p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3065, 346, 1562, '<p>Will an NIH-supported repository be selected for data subject to GDS?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 3, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3072, 346, 1562, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>What types of persistent identifiers/ indexing methods will be used for data releases, to enable findability and citation of shared datasets?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 10, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3071, 346, 1562, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>Explain if data sharing timelines will <strong>not</strong> meet expectations of the DMS or other policies, if applicable.</p>', '', '<p>NIH Guidance: NIH encourages scientific data to be shared as soon as possible, and no later than the time of an associated publication or end of the performance period, whichever comes first. NIH also encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 9, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3070, 346, 1562, '<p><strong>Element 3: Standards</strong></p><p>List data or metadata standards or common data elements that will be used applicable to each data type shared. Write N/A if no existing standards.</p>', '', '<p>NIH Guidance: Data standards refer to community-accepted methods of organizing, documenting, and formatting data to aid in data aggregation, sharing, and reuse.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 8, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3069, 346, 1562, '<p><strong>Element 2: Tools, Software, Code&nbsp;</strong></p><p>Describe how researchers can access the tools, software, and/or code listed above. Describe if &ldquo;Other.&rdquo;</p>', '', '<p>Specify how needed tools can be accessed.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Open source", "value": "Open source", "selected": 0}, {"label": "Available for a fee", "value": "Available for a fee", "selected": 0}, {"label": "Restricted availability from a specific source ", "value": "Restricted availability from a specific source ", "selected": 0}, {"label": "Other", "value": "Other", "selected": 0}], "attributes": {"multiple": 0}}', 0, 7, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3068, 346, 1562, '<p><strong>Element 2: Tools, Software, Code</strong></p><p>Describe the tools, software, and/or code that are needed to access or manipulate shared scientific data to support replication or reuse, if any.</p>', '', '<p>NIH Guidance: Indicate names of the specialized tools needed to access or manipulate each shared respective data type, if any.&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 6, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3067, 346, 1562, '<p><strong>Element 1: Data Type &nbsp;&nbsp;</strong></p><p><span style="color: rgb(224, 62, 45); font-size: 14pt;">Describe DMS Plan Elements 1-6 in the section below with free text. To maximize the use of structured information in writing a Plan (e.g., data types, repositories) with a drop-down menu option, please proceed to the adjacent tab labeled &ldquo;Research Outputs."</span></p><p>Will all scientific data generated by the research project be shared in a data repository that makes data available to the larger research community? &nbsp;If No, explain the rationale that determines which scientific data will not be shared in the comment area below. &nbsp; &nbsp; &nbsp;&nbsp;</p>', '', '<p>NIH guidance: Scientific Data is defined as data commonly accepted in the scientific community as of sufficient quality to validate and replicate research findings, regardless of whether the data are used to support scholarly publications.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 5, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3066, 346, 1562, '<p>Has an <a href="https://sharing.nih.gov/genomic-data-sharing-policy/institutional-certifications/completing-an-institutional-certification-form" target="_blank" rel="noopener">Institutional Certification (IC)</a> been submitted with the application or Just-In-Time that meets GDS criteria?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 4, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
