INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3076, 346, 1562, '<p><strong>Element 6: Compliance</strong></p><p>Describe how compliance with the Plan will be monitored and managed, frequency of oversight, and by whom.</p>', '', '<p>NIH Guidance: Indicate the data management and sharing lead name and contact information; the frequency and methods that your institution will provide oversight and by whom (e.g., titles, roles and responsibilities).&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 14, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO questions (id, templateId, sectionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, isDirty, createdById, created, modifiedById, modified) VALUES (3075, 346, 1562, '<p><strong>Element 5: Access, Distribution or Reuse Considerations</strong></p><p>What type of access will secondary users utilize to access the shared data? Describe if &ldquo;Other.&rdquo;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Open access", "value": "Open access", "selected": 0}, {"label": "Managed access", "value": "Managed access", "selected": 0}, {"label": "Controlled access", "value": "Controlled access", "selected": 0}, {"label": "Data Enclave", "value": "Data Enclave", "selected": 0}, {"label": "Other", "value": "Other", "selected": 0}], "attributes": {"multiple": 0}}', 0, 13, 0, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
