INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (85, 16, 85, 2574, '<p>Investigators seeking $500,000 or more in direct costs in any year should include a description of how final research data will be shared, or explain why data sharing is not possible.</p>', '', '<p>Help with a data sharing plan:<br>The precise content of the data-sharing plan will vary, depending on the data being collected and how the investigator is planning to share the data. Consider the following: </p><ul><li>When will you make the data available?</li><li>What file formats will you use for your data, and why?</li><li>What transformations will be necessary to prepare data for preservation/data sharing?</li><li>What metadata/documentation will be submitted alongside the data?</li><li>Will a data-sharing agreement will be required? What will the agreement state?</li><li>What are your plans for providing access to your data?</li><li>Which archive/repository/central database have you identified as a place to deposit data?</li></ul><br><a href="http://grants.nih.gov/grants/sharing.htm">NIH data sharing guidance and resources</a><br><a href="http://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm">NIH data sharing policy and implementation</a><br><a href="http://grants.nih.gov/grants/sharing_key_elements_data_sharing_plan.pdf">NIH Key Elements in Preparing a Data Sharing Plan (PDF)</a><br><a href="http://grants.nih.gov/grants/sharing_data_standards_guide.doc">NIH Data Standards and Common Data Elements Resource Guide (.doc)</a><br><a href="http://www.nlm.nih.gov/NIHbmic/nih_data_sharing_repositories.html">NIH Data Sharing Repositories</a><br><a href="http://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947">NIH sharing policy statement</a><br><a href="http://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm">Guidance</a><p></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (470, 85, 391, 2568, '<p>Explain whether the research being considered for funding involves human data, non-human data, or both. &#8232;</p>', '', '<p><p>Information to be included in this section:<ul><li>Type of data being collected: human, non-human, or both human &amp; non-human.</li><li>Type of genomic data to be shared: sequence, transcriptomic, epigenomic, and/or gene expression.</li><li>Level of the genomic data to be shared: Individual-level, aggregate-level, or both.</li><li>Relevant associated data to be shared: phenotype or exposure.</li><li>Information needed to interpret the data: study protocols, survey tools, data collection instruments, data dictionary,   software (including version), codebook, pipeline metadata, etc.  This information should be provided with unrestricted access for all data levels. </li></ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf" target="_blank">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf" target="_blank">NIH Genomic Data Sharing Policy (PDF)</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/" target="_blank">NIH Genomic Data Sharing FAQ</a></li></ul></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (471, 85, 392, 2569, '<p>Identify the data repositories to which the data will be submitted, and for human data, whether the data will be available through unrestricted or controlled-access. &#8232;</p>', '', '<p><p>For human genomic data, investigators are expected to register all studies in the database of Genotypes and Phenotypes (dbGaP) by the time data cleaning and quality control measures begin in addition to submitting the data to the relevant NIH-designated data repository (e.g., dbGaP, Gene Expression Omnibus (GEO), Sequence Read Archive (SRA), the Cancer Genomics Hub) after registration. â€¨</p><p>Non-human data may be made available through any widely used data repository, whether NIH- funded or not, such as GEO, SRA, Trace Archive, Array Express, Mouse Genome Informatics, WormBase, the Zebrafish Model Organism Database, GenBank, European Nucleotide Archive, or DNA Data Bank of Japan. </p><p>Data in <strong>unrestricted-access</strong> repositories (e.g., The 1000 Genomes Project) are publicly available to anyone.â€¨ <strong>Controlled-access</strong> data (e.g., data in dbGaP) are made available for secondary research only after investigators have obtained appropriate approval to use the requested data for their proposed project.â€¨</p><ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf" target="_blank">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf" target="_blank">NIH Genomic Data Sharing Policy (PDF)</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/" target="_blank">NIH Genomic Data Sharing FAQ</a></li></ul><ul><li><a href="http://www.1000genomes.org/" target="_blank">The 1000 Genomes Project</a></li><li><a href="http://www.ncbi.nlm.nih.gov/gap" target="_blank">Database of Genotypes and Phenotypes (dbGaP)</a></li></ul></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (472, 85, 393, 2570, '<p>Provide a timeline for sharing data in a timely manner.</p>', '', '<p><p>In general, NIH will release human genomic data no later than six months after the data have been submitted to NIH-designated data repositories and cleaned, or at the time of acceptance of the first publication, whichever occurs first, without restrictions on publication or other dissemination of research findings. â€¨Investigators should make non-human genomic data publicly available no later than the date of initial publication. However, availability before publication may be expected for certain data, projects (e.g., data from projects with broad utility as a resource for the scientific community such as microbial population-based genomic studies), or by the funding NIH IC. â€¨</p><p><strong>Note:</strong> The <a href="http://gds.nih.gov/pdf/supplemental_info_GDS_Policy.pdf">Supplemental Information to the GDS Policy</a> provides expectations for the timelines of data submission and release based on the level of data processing, and additional information about the data levels.  </p><ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf" target="_blank">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf" target="_blank">NIH Genomic Data Sharing Policy (PDF)</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/" target="_blank">NIH Genomic Data Sharing FAQ</a></li></ul></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (473, 85, 394, 2571, '<p>State whether an Institutional Review Board (IRB) or analogous review body has reviewed the genomic data sharing aspects of your project, or provide a timeline for such review. &#8232;</p>', '', '<p><p>IRB review of the investigatorâ€™s proposal for data submission is an element of the Institutional Certification which assures that the proposal for data submission and sharing is appropriate. Please keep in mind that an Institutional Certification is generally required for extramural investigators prior to NIH grant award along with other Just- in-Time information or finalization of a contract. For NIH intramural investigators, an Institutional Certification memorandum should be completed and sent from the SD, or delegate, to the IC Genomic Program Administrator (GPA) before research is begun, whenever possible. â€¨</p><p><a href="http://gds.nih.gov/pdf/PTC_for_IRBs_and_Institutions.pdf">Points to Consider</a> for Institutions and Institutional Review Boards in Developing Institutional Certifications for Submitting Human Data under the Genomic Data Sharing Policy.</p><ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf" target="_blank">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf" target="_blank">NIH Genomic Data Sharing Policy (PDF)</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/" target="_blank">NIH Genomic Data Sharing FAQ</a></li></ul><ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/GDS_Points_to_Consider_for_Institutions_and_IRBs.pdf" target="_blank">NIH Points to Consider for IRBs</a></li></ul></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (474, 85, 395, 2572, '<p>Describe the appropriate use of the data.</p>', '', '<p>NIH-GDS: Appropriate uses of the data:<br></p><p>Under the GDS Policy, data is expected to be shared for broad research purposes. If such use of the data is not appropriate, as expressed in informed consent documents of the research participants whose data are included in the dataset, any limitations on the data use should be described in the Institutional Certification. NIH provides standard language (see Links tab for URL) to guide the development of data use limitations. &#8232;</p><br><a href="https://gds.nih.gov/pdf/GDS_Policy_Guidance_Grant_App_Contract_Proposals.pdf">NIH Guidance on Developing Genomic Data Sharing Plans</a><br><a href="https://gds.nih.gov/PDF/NIH_GDS_Policy.pdf">Genomic Data Sharing Policy (PDF)</a><br><a href="https://gds.nih.gov/03policy2.html%20">Genomic Data Sharing Policy (Web)</a><br><a href="https://gds.nih.gov/pdf/GDS_Policy_Guidance_Grant_App_Contract_Proposals.pdf">Guidance</a><br><a href="https://gds.nih.gov/pdf/standard_data_use_limitations.pdf">NIH GDS Standard Data Use Limits</a><p></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (475, 85, 396, 2573, '<p>Explain why in the genomic data sharing plan and describe an alternative mechanism for data sharing .&#8232;</p>', '', '<p>NIH-GDS: Request for an exception to submission:<br></p><p>If submission of human data generated in the study would not be appropriate because the Institutional Certification (see Links tab for URL) criteria cannot be met, the investigator should explain why in the genomic data sharing plan and describe an alternative mechanism for data sharing. If the funding IC grants an exception to submission, the research will be registered in dbGaP and the reason for the exception and the alternative sharing plan will be described. For NIH intramural studies, the NIH Deputy Director for Intramural Research will make the final decision on the exception request, after the IC has made its determination. &#8232;</p><br><a href="https://gds.nih.gov/pdf/GDS_Policy_Guidance_Grant_App_Contract_Proposals.pdf">NIH Guidance on Developing Genomic Data Sharing Plans</a><br><a href="https://gds.nih.gov/PDF/NIH_GDS_Policy.pdf">Genomic Data Sharing Policy (PDF)</a><br><a href="https://gds.nih.gov/03policy2.html%20">Genomic Data Sharing Policy (Web)</a><br><a href="https://gds.nih.gov/pdf/GDS_Policy_Guidance_Grant_App_Contract_Proposals.pdf">Guidance</a><br><a href="https://gds.nih.gov/Institutional_Certifications.html">Institutional Certification</a><p></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1244, 180, 856, 2568, '<p>The GDS Policy applies to all NIH-funded research that generates large-scale human or non-human genomic data as well as the use of these data for subsequent research.&nbsp; Large-scale data include genome-wide association studies (GWAS), single nucleotide polymorphisms (SNP) arrays, and genome sequence, transcriptomic, metagenomic, epigenomic, and gene expression data, irrespective of funding level and funding mechanisms (e.g. grant, contract, cooperative agreement, or intramural support). NIH Institute or Centers (IC) may expect submission of data from smaller scale research projects based on the state of the science, the programmatic priorities of the IC funding the research, and the utility of the data for the research community.</p>', '', '<p>&nbsp;</p><p><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf">NIH Genomic Data Sharing Policy. August 27, 2014. https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/">NIH Office of Science Policy. NIH Genomic Data Sharing. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Office of Science Policy. Genomic Data Sharing FAQ. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf">Supplemental Information to the NIH Genomic Data Sharing Policy. https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf</a></p><p><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html">NIH Genomic Data Sharing Policy. August 27, 2014. https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/scientific-data-management/">NIH Office of Science Policy. Scientific Data Management. https://osp.od.nih.gov/scientific-sharing/scientific-data-management/</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1245, 180, 857, 2569, '<p>Identify the data repositories to which the data will be submitted, and for human data, whether the data will be available through unrestricted or controlled-access.&nbsp;</p><p>Investigators should register all studies with human genomic data that fall within the scope of the GDS Policy in dbGaP by the time that data cleaning and quality control measures begin. After registration in dbGaP, investigators should submit the data to the relevant NIH-designated data repository (e.g., dbGaP, GEO, SRA, the Cancer Genomics Hub). NIH-designated data respositiories need not be the exclusive source for facilitating the sharing of genomic data, that is, investigators may also elect to submit data to a non-NIH-designated data respository in addition to an NIH-designated data respository. However, investigators should ensure that appropriate data security measures are in place, and that confidentiality, privacy, and data use measures are consistent with the GDS Policy.</p><p>Non-human data may be made available through any widely used data repository, whether NIH- funded or not, such as GEO, SRA, Trace Archive, Array Express, Mouse Genome Informatics, WormBase, the Zebrafish Model Organism Database, GenBank, European Nucleotide Archive, or DNA Data Bank of Japan.</p>', '', '<p><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf">NIH Genomic Data Sharing Policy. August 27, 2014. https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/">NIH Office of Science Policy. NIH Genomic Data Sharing. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Office of Science Policy. Genomic Data Sharing FAQ. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf">Supplemental Information to the NIH Genomic Data Sharing Policy. https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf</a></p><p><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html">NIH Genomic Data Sharing Policy. August 27, 2014. https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/scientific-data-management/">NIH Office of Science Policy. Scientific Data Management. https://osp.od.nih.gov/scientific-sharing/scientific-data-management/</a></p><p><a href="http://www.ncbi.nlm.nih.gov/gap">Database of Genotypes and Phenotypes (dbGaP)</a></p><p><a href="https://www.ncbi.nlm.nih.gov/geo/">NCBI GEO Gene Expressioin Omnibus.https://www.ncbi.nlm.nih.gov/geo/</a></p><p><a href="https://www.ncbi.nlm.nih.gov/sra">NCBI SRA Sequence Read Archive. https://www.ncbi.nlm.nih.gov/sra</a></p><p><a href="https://gdc.cancer.gov/">NIH National Cancer Institute. Genomic Data Commons. https://gdc.cancer.gov/</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1246, 180, 858, 2570, '<p>Investigators should submit large-scale genomic data as well as relevant associated data (e.g. phenotype and exposure data) to an NIH-designated data repository in a timely manner. Investigators should also submit any information necessary to interpret the submitted genomic data, such as study protocols, data instruments and survey tools. Genomic data undergo different levels of data processing, which provides the basis for NIHs expectations for data submission and timelines for the release of the data for access by investigators.&nbsp; These expectations and timelines are provided in the Supplemental Information. In general, NIH will release data submitted to NIH-designated data repositories no later than six months after the initial data submissions begins, or at the time of acceptance of the first publication, whichever occurs first, without restrictions on publication or other dissemination.</p>', '', '<p><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf">Supplemental Information to the NIH Genomic Data Sharing Policy. https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf">NIH Genomic Data Sharing Policy. August 27, 2014. https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/">NIH Office of Science Policy. NIH Genomic Data Sharing. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Office of Science Policy. Genomic Data Sharing FAQ. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/</a></p><p><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html">NIH Genomic Data Sharing Policy. August 27, 2014. https://grants.nih.gov/grants/guide/notice-files/NOT-OD-14-124.html</a></p><p><a href="https://osp.od.nih.gov/scientific-sharing/scientific-data-management/">NIH Office of Science Policy. Scientific Data Management. https://osp.od.nih.gov/scientific-sharing/scientific-data-management</a></p><p>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1247, 180, 859, 2571, '<p>Respect for, and protection of the interests of, research participants are fundamental to NIHs stewardship of human genomic data.&nbsp; The informed consent under which the data or samples were collected is the basis for the submitting institution to determine the appropriateness of data submission to NIH-designated data repositories, and whether the data should be available through unrestricted or controlled access.&nbsp;</p><p>For research that falls within the scope of the GDS Policy, submitting institutions, through their Institutional Review Boards (IRBs), privacy boards, or equivalent bodies, are to review the informed consent materials to determine whether it is appropriate for data to be shared for secondary research use. Specific considerations may vary with the type of study and whether the data are obtained through prospective or retrospective data collections.&nbsp; NIH provides additional information on issues related to the respect for research participant interests its "<em>Points to Consider for IRB"s and Institutions in their Review of Data Submission Plans for Institutional Certifications</em>" (updated in 2016 to "<em>Points to Consider for Institutions and Institutional Review Boards in Submission and Secondary&nbsp;Use of Human Genomic Data under the National Institutes of Health Genomic Data Sharing Policy").</em></p>', '', '<p><a href="https://osp.od.nih.gov/scientific-sharing/researchers-institutional-certifications/">NIH. Office of Science Policy, Researchers &amp; Institutional Certifications. https://osp.od.nih.gov/scientific-sharing/researchers-institutional-certifications/</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/GDS_Points_to_Consider_for_Institutions_and_IRBs.pdf">Points to Consider for Institutions and Institutional Review Boards in Submission and Secondary Use of Human Genomic Data under the NIH Genomic Data Sharing Policy, May 23, 2016 https://osp.od.nih.gov/wp-content/uploads/GDS_Points_to_Consider_for_Institutions_and_IRBs.pdf</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/standard_data_use_limitations.pdf">NIH Standard Data Use Limitations. https://osp.od.nih.gov/wp-content/uploads/standard_data_use_limitations.pdf</a></p><p><a href="https://osp.od.nih.gov/wp-content/uploads/GDS-Policy_Guidance_Grant-App-Contract_Proposals.pdf">NIH Genomic Data Sharing Policy for Human and Non-Human Data:<br />Guidance for Institutions Submitting Grant Applications and Contract Proposals&nbsp;https://osp.od.nhttps://osp.od.nih.gov/wp-content/uploads/GDS-Policy_Guidance_Grant-App-Contract_Proposals.pdfih.gov/wp-content/uploads/GDS-Policy_Guidance_Grant-App-Contract_Proposals.pdf</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1248, 180, 860, 2573, '<p>In cases where data submission to an NIH-designated data repository is not appropriate, that is, the Institutional Certification criteria cannot be met, investigators should provide a justification for any data submission exceptions requested in the funding application or proposal.&nbsp; The funding IC may grant an exception to submitting relevant data to NIH, and the investigator would be expected to develop an alternate plan to share data through other mechanisms.&nbsp;</p>', '', '<p>&nbsp;</p><p><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Office of Science Policy. Genomic Data Sharing FAQ. https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1671, 199, 1022, 2574, '<p>Investigators seeking $500,000 or more in direct costs in any year should include a description of how final research data will be shared, or explain why data sharing is not possible.</p>', '', '<p><span style="font-size: 10pt;"><strong><span style="font-family: Helvetica, sans-serif; color: black;">Proposed Text:</span></strong></span></p><p><span style="font-size: 10.0pt; font-family: "Helvetica",sans-serif; color: black;">"Grantees should note that, under the NIH Grants Policy Statement, they are required to keep the data for 3 years following closeout of a grant or contract agreement. NIH expects the timely release and sharing of data to be no later than the acceptance for publication of the main findings from the final dataset.&nbsp;</span></p><p><span style="font-size: 10.0pt; font-family: \'Helvetica\',sans-serif; color: black;">&nbsp;</span><span style="font-size: 10.0pt; font-family: \'Helvetica\',sans-serif; color: black;">It is the responsibility of the investigators, their Institutional Review Board (IRB), and their institution to protect the rights of subjects and the confidentiality of the data. Prior to sharing, data should be redacted to strip all identifiers, and effective strategies should be adopted to minimize risks of unauthorized disclosure of personal identifiers. Researchers who are planning clinical trials and intend to share the resulting data should think carefully about the study design, the informed consent documents, and the structure of the resulting dataset prior to the initiation of the study. Investigators who are working for or who are themselves covered entities under the Health Insurance Portability and Accountability Act (HIPAA) must consider issues related to the Privacy Rule.&nbsp;See linked guidance for more information regarding proprietary data.</span></p><p><span style="font-size: 10.0pt; font-family: \'Helvetica\',sans-serif; color: black;">Proper documentation is needed to ensure that others can use the dataset and to prevent misuse, misinterpretation, and confusion. It is appropriate for scientific authors to acknowledge the source of data upon which their manuscript is based" (NIH Data Sharing Policy and Implementation Guidance)."</span></p><p><strong>Original Text:</strong></p><p>Help with a data sharing plan:<br />The precise content of the data-sharing plan will vary, depending on the data being collected and how the investigator is planning to share the data. Consider the following:</p><ul><li>When will you make the data available?</li><li>What file formats will you use for your data, and why?</li><li>What transformations will be necessary to prepare data for preservation/data sharing?</li><li>What metadata/documentation will be submitted alongside the data?</li><li>Will a data-sharing agreement will be required? What will the agreement state?</li><li>What are your plans for providing access to your data?</li><li>Which archive/repository/central database have you identified as a place to deposit data?</li></ul><p><a href="https://grants.nih.gov/policy/sharing.htm">NIH Sharing Policies and Related Guidance on NIH-Funded Research Resouces. https://grants.nih.gov/policy/sharing.htm</a></p><p><a href="https://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm">NIH Data Sharing Policy and Implementation Guidance,Updated: March 5, 2003. https://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm</a></p><p><a href="http://grants.nih.gov/grants/sharing_key_elements_data_sharing_plan.pdf">NIH Key Elements to Consider in Preparing a Data Sharing Plan Under NIH Extramural Support, December 2009 (PDF)</a></p><p><a href="http://grants.nih.gov/grants/sharing_data_standards_guide.doc">NIH Data Standards and Common Data Elements Resource Guide&nbsp; (.doc)</a></p><p><a href="https://www.nlm.nih.gov/cde/">NIH Common Data Element (CDE) Resource Portal. https://www.nlm.nih.gov/cde/</a></p><p><a href="http://www.nlm.nih.gov/NIHbmic/nih_data_sharing_repositories.html">NIH Data Sharing Repositories</a></p><p><a href="http://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947">NIH sharing policy statement</a></p><p><a href="https://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947">NIH Grants Policy Statement 10/13. https://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1895, 207, 1122, 2574, '<p>Investigators seeking $500,000 or more in direct costs in any year should include a description of how final research data will be shared, or explain why data sharing is not possible.</p>', '', '<p>The precise content of the data-sharing plan will vary, depending on the data being collected and how the investigator is planning to share the data. Consider the following:</p><ul><li>When will you make the data available?</li><li>What file formats will you use for your data, and why?</li><li>What transformations will be necessary to prepare data for preservation/data sharing?</li><li>What metadata/documentation will be submitted alongside the data?</li><li>Will a data-sharing agreement will be required? What will the agreement state?</li><li>What are your plans for providing access to your data?</li><li>Which archive/repository/central database have you identified as a place to deposit data?</li></ul><p><a href="https://grants.nih.gov/policy/sharing.htm">NIH Sharing Policies and Related Guidance on NIH-Funded Research Resouces. https://grants.nih.gov/policy/sharing.htm</a></p><p><a href="https://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm">NIH Data Sharing Policy and Implementation Guidance,Updated: March 5, 2003. https://grants.nih.gov/grants/policy/data_sharing/data_sharing_guidance.htm</a></p><p><a href="http://grants.nih.gov/grants/sharing_key_elements_data_sharing_plan.pdf">NIH Key Elements to Consider in Preparing a Data Sharing Plan Under NIH Extramural Support, December 2009 (PDF)</a></p><p><a href="http://grants.nih.gov/grants/sharing_data_standards_guide.doc">NIH Data Standards and Common Data Elements Resource Guide&nbsp; (.doc)</a></p><p><a href="https://www.nlm.nih.gov/cde/">NIH Common Data Element (CDE) Resource Portal. https://www.nlm.nih.gov/cde/</a></p><p><a href="https://grants.nih.gov/policy/clinical-trials/single-irb-policy-multi-site-research.htm">NIH Single IRB Policy for Multi-site Research 2018. https://grants.nih.gov/policy/clinical-trials/single-irb-policy-multi-site-research.htm</a></p><p><a href="http://www.nlm.nih.gov/NIHbmic/nih_data_sharing_repositories.html">NIH Data Sharing Repositories</a></p><p><a href="http://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947">NIH sharing policy statement</a></p><p><a href="https://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947">NIH Grants Policy Statement 10/13. https://grants.nih.gov/grants/policy/nihgps_2013/nihgps_ch8.htm#_Toc271264947</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2451, 253, 1426, 2568, '<p>The GDS Policy applies to all NIH-funded research that generates large-scale human or non-human genomic data as well as the use of these data for subsequent research.&nbsp; Large-scale data include genome-wide association studies (GWAS), single nucleotide polymorphisms (SNP) arrays, and genome sequence, transcriptomic, metagenomic, epigenomic, and gene expression data, irrespective of funding level and funding mechanisms (e.g. grant, contract, cooperative agreement, or intramural support). NIH Institute or Centers (IC) may expect submission of data from smaller scale research projects based on the state of the science, the programmatic priorities of the IC funding the research, and the utility of the data for the research community.</p>', '', '<ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf">NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Genomic Data Sharing FAQ</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/">NIH Office of Science Policy. NIH Genomic Data Sharing.</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/scientific-data-management/">NIH Office of Science Policy. Scientific Data Management.</a></li></ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2452, 253, 1427, 2569, '<p>Identify the data repositories to which the data will be submitted, and for human data, whether the data will be available through unrestricted or controlled-access.&nbsp;</p><p>Investigators should register all studies with human genomic data that fall within the scope of the GDS Policy in dbGaP by the time that data cleaning and quality control measures begin. After registration in dbGaP, investigators should submit the data to the relevant NIH-designated data repository (e.g., dbGaP, GEO, SRA, the Cancer Genomics Hub). NIH-designated data respositiories need not be the exclusive source for facilitating the sharing of genomic data, that is, investigators may also elect to submit data to a non-NIH-designated data respository in addition to an NIH-designated data respository. However, investigators should ensure that appropriate data security measures are in place, and that confidentiality, privacy, and data use measures are consistent with the GDS Policy.</p><p>Non-human data may be made available through any widely used data repository, whether NIH- funded or not, such as GEO, SRA, Trace Archive, Array Express, Mouse Genome Informatics, WormBase, the Zebrafish Model Organism Database, GenBank, European Nucleotide Archive, or DNA Data Bank of Japan.</p>', '', '<ul><li><a href="https://osp.od.nih.gov/wp-content/uploads/NIH_GDS_Policy.pdf">NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing-faqs/">NIH Genomic Data Sharing FAQ</a></li><li><a href="https://osp.od.nih.gov/wp-content/uploads/Supplemental_Info_GDS_Policy.pdf">Supplemental Information to the NIH Genomic Data Sharing Policy</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/genomic-data-sharing/">NIH Office of Science Policy. NIH Genomic Data Sharing.</a></li><li><a href="https://osp.od.nih.gov/scientific-sharing/scientific-data-management/">NIH Office of Science Policy. Scientific Data Management.</a></li><li><a href="http://www.ncbi.nlm.nih.gov/gap">Database of Genotypes and Phenotypes (dbGaP)</a></li><li><a href="https://www.ncbi.nlm.nih.gov/geo/">NCBI GEO Gene Expressioin Omnibus</a></li><li><a href="https://www.ncbi.nlm.nih.gov/sra">NCBI SRA Sequence Read Archive</a></li><li><a href="https://gdc.cancer.gov/">NIH National Cancer Institute. Genomic Data Commons.</a></li></ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
