INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8651, 698, 4416, 2573, 'Investigators should indicate if a study should be designated as &ldquo;sensitive&rdquo; or &ldquo;not sensitive&rdquo; for the purposes of access to GSR. This designation should be confirmed in the Institutional Certification Form.', '', '<ul><li>For more information about GSR, please consult "<a title="What are Genomic Summary Results &amp; How Can &ldquo;GSR&rdquo; Inform Research and Clinical Care?&quot;" href="https://sharing.nih.gov/sites/default/files/flmngr/What_are_Genomic_Summary_Results.pdf">What are Genomic Summary Results &amp; How Can &ldquo;GSR&rdquo; Inform Research and Clinical Care?"</a></li><li><a title="NIH Genomic Data Sharing Policy (NOT-OD-14-124)" href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html">NIH Genomic Data Sharing Policy (NOT-OD-14-124)</a></li></ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8667, 704, 4428, 3533, '<span id="page108R_mcid6" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 266.74px; font-size: 18.4px; font-family: sans-serif;" role="presentation">T</span><span dir="ltr" style="left: 131.44px; top: 266.74px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07328);" role="presentation">ypes and amount of scientific data expected to be generated in the project</span></span>: <span id="page108R_mcid7" class="markedContent"><em><span dir="ltr" style="left: 120.04px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995628);" role="presentation">Summarize the types and</span> <span dir="ltr" style="left: 333.083px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997451);" role="presentation">estimated</span> <span dir="ltr" style="left: 417.683px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995701);" role="presentation">amount of scientific data expected to be generated in the project</span></em><span dir="ltr" style="left: 938.6px; top: 287.94px; font-size: 18.4px; font-family: sans-serif;" role="presentation"><em>.</em><br /></span></span><br />Describe data in general terms that address the type and amount/size of scientific data expected to be collected and used in the project (e.g., 256-channel EEG data and fMRI images from ~50 research participants). Descriptions may indicate the data modality (e.g., imaging, genomic, mobile, survey), level of aggregation (e.g., individual, aggregated, summarized), and/or the degree of data processing that has occurred (i.e., how raw or processed the data will be)', '', '<strong>NIH Guidance</strong><br /><p><span style="font-weight: 400;">The final DMS Policy has </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/about-data-management-and-sharing-policy/research-covered-under-the-data-management-sharing-policy#after"><span style="font-weight: 400;">specific definitions for what Scientific Data is, and what proposals are considered to be producing scientific data</span></a><span style="font-weight: 400;">.</span></p><p><span style="font-weight: 400;">Per the </span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-21-013.html"><span style="font-weight: 400;">Policy</span></a><span style="font-weight: 400;">, &ldquo;Even those scientific data not used to support a publication are considered scientific data and within the final DMS Policy&rsquo;s scope. We understand that a lack of publication does not necessarily mean that the findings are null or negative; however, indicating that scientific data are defined independent of publication is sufficient to cover data underlying null or negative findings.&rdquo;</span></p><br /><strong>Additional Guidance<br /></strong>Research projects vary widely in the types of data produced. In this section, you will describe the categories, amounts, and degree of processing of your data.<strong><br /></strong>', 'This project will produce _________ [Data type, e.g., imaging, sequencing, experimental measurements] data generated/obtained from __________ [e.g., instrument, method, survey, experiment, data repository].&nbsp; Data will be collected from&nbsp; ___ [number] of research participants/specimens/experiments, generating ___ [number] datasets totaling approximately ___ [amount of data] in size. The following data files will be used or produced in the course of the project: ______ [list input data files, intermediate files, and final, post-processed files]. Raw data will be transformed by ____ [analysis, method] and the subsequent processed dataset used for statistical analysis. To protect research participant identities, ___________ [e.g., individual, aggregated, summarized] data will be made available for sharing.', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8668, 704, 4428, 3534, '<span id="page108R_mcid8" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">S</span><span dir="ltr" style="left: 132.24px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0986);" role="presentation">cientific data</span> <span dir="ltr" style="left: 249.633px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09141);" role="presentation">that</span> <span dir="ltr" style="left: 288.233px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.06656);" role="presentation">will be preserved and shared, and</span> <span dir="ltr" style="left: 587.917px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08099);" role="presentation">the</span> <span dir="ltr" style="left: 620.517px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08772);" role="presentation">rationale for</span> <span dir="ltr" style="left: 731.567px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09897);" role="presentation">doing so</span><span dir="ltr" style="left: 807.967px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">:<em> <span id="page108R_mcid9" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 372.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996025);" role="presentation">Describe which scientific data from the project will be preserved and shared and provide the rationale </span><span dir="ltr" style="left: 120.04px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.00294);" role="presentation">for</span> <span dir="ltr" style="left: 146.64px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995537);" role="presentation">this decision.</span></span></em><br /></span></span>', '', 'NIH does not anticipate that researchers will preserve and share all scientific data generated in a study. Researchers should decide which scientific data to preserve and share based on ethical, legal, and technical factors that may affect the extent to which scientific data are preserved and shared. Provide the rationale for these decisions.', 'Based on _______ [ethical, legal, technical] considerations, the following data produced in the course of the project will be preserved and shared: ____ [list]<strong> OR</strong> All data produced in the course of the project will be preserved and shared.', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8669, 704, 4428, 3535, '<p><span style="font-weight: 400;">Metadata, other relevant data, and associated documentation: Briefly list the metadata, other relevant data, and any associated documentation (e.g., study protocols and data collection instruments) that will be made accessible to facilitate interpretation of the scientific data.</span></p>', '', '<p><strong>NIH Guidance<br /></strong><span style="font-weight: 400;">In addition to the documentation examples, consider metadata that will provide additional information intended to make scientific data interpretable and reusable (e.g., date, independent sample and variable construction and description, methodology, data provenance, data transformations, any intermediate or descriptive observational variables).</span></p><p><span style="font-weight: 400;">&nbsp;</span></p>', '<span style="font-weight: 400;">To facilitate interpretation of the data, ______ [e.g., metadata, documentation, protocols, data collection instruments] will be shared and associated with the relevant datasets.</span>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8670, 704, 4429, 3536, '<p><span style="font-weight: 400;">State whether specialized tools, software, and/or code are needed to access or manipulate shared scientific data, and if so, provide the name(s) of the needed tool(s) and software and specify how they can be accessed.</span></p><span id="page108R_mcid13" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 688.423px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997943);" role="presentation"><br /></span></span>', '', '<strong>Additional Guidance</strong><br /><p><span style="font-weight: 400;">Tool(s) and software should be identified; plans should specify how the tools can be accessed (e.g., open source and freely available, generally available for a fee in the marketplace, available only from the research team). When known, the longevity or period of time for which custom or proprietary tools will be available should be addressed.</span></p><p><span style="font-weight: 400;">In addition, file formats in which data are saved in a digital format can be divided into two general categories.</span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Proprietary - The specification of the data encoding format is not released or restricted in some way. Proprietary formats can only be easily opened and manipulated by particular software tools.&nbsp;</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Open - The specification of the data encoding format which can be used and implemented by anyone. Open formats can often be easily opened and manipulated by a large number of software tools.</span></li></ul>', '<p><strong><em>If no specialized tools are needed to access or manipulate the data:</em></strong></p><p>_____ [Data type - Imaging data, survey data, etc] data will be made available in _____ [csv, txt, dicom, etc] format and will not require the use of specialized tools to be accessed or manipulated.&nbsp;</p><p><strong><em>If specialized tools are needed to access or manipulate the data:</em></strong></p><p>_____ [Data type] data will be made available in _____ format, which requires the use of specialized tools, such as _____ [include list of tools] to be accessed and manipulated.</p><span style="font-weight: 400;">These tools will be shared openly via ____.</span>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8671, 704, 4430, 3537, 'State what common data standards will be applied to the scientific data and associated metadata to enable interoperability of datasets and resources, and provide the name(s) of the data standards that will be applied and describe how these data standards will be applied to the scientific data generated by the research proposed in this project. If applicable, indicate that no consensus standards exist', '', '<strong>NIH Guidance</strong><br />While many scientific fields have developed and adopted common data standards, others have not. In such cases, the Plan may indicate that no consensus data standards exist for the scientific data and metadata to be generated, preserved, and shared.<br /><br /><strong>Additional Guidance&nbsp;</strong><br /><p><span style="font-weight: 400;">A </span><em><span style="font-weight: 400;">standard</span></em><span style="font-weight: 400;"> specifies how exactly data and related materials should be stored, organized, and described. In the context of research data, the term typically refers to the use of specific and well-defined formats, schemas, vocabularies, and ontologies in the description and organization of data. However, for researchers within a community where more formal standards have not been well established, it can also be interpreted more broadly to refer to the adoption of the same (or similar) data management-related activities or strategies by different researchers and across different projects.</span></p><p><span style="font-weight: 400;">It is possible that your work will employ multiple formal standards or a mix of formal standards and other data management strategies. You should be as specific as possible when describing the standards used for each type of data included in your proposal.</span></p>', '<p><span style="font-weight: 400;">To facilitate their efficient use, all of our data and materials will be structured and described using the following standards:</span></p><p><strong><em>If there are formal data standards for some/all of the data:</em></strong><span style="font-weight: 400;">&nbsp;</span></p><p><span style="font-weight: 400;">Whenever possible, we will use ______ [common data elements, standardized survey instruments, etc] to structure and organize our data.</span></p><p><span style="font-weight: 400;">Our ____ data will be structured and described using the ____ standard, which has been widely adopted in the ____ community. [Add additional information about this standard, if applicable - e.g. implementation in data repositories, utility in combining/reusing datasets]</span></p><p><strong><em>If there are not formal standards:</em></strong><span style="font-weight: 400;">&nbsp;</span></p><p><span style="font-weight: 400;">Formal standards for ____ data have not yet been widely adopted. However, our data and other materials will be structured and described according to best practices.&nbsp;</span></p><p><span style="font-weight: 400;">Data will be stored in common and open formats, such as ____ for our ____ data. Information needed to make use of this data [e.g. the meaning of variable names, codes, information about missing data, other metadata etc] will be recorded in ____ [data dictionaries/codebooks] that will be accessible to the research team and will subsequently be shared alongside final datasets.&nbsp;</span></p><p><span style="font-weight: 400;">Information about our research process, including the details of our analysis pipeline will be maintained contemporaneously, using ____ [lab notebooks, protocols, etc]. This information will be accessible to all members of the research team and will be shared alongside our data.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8672, 704, 4431, 3538, '<strong><span id="page108R_mcid18" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0786);" role="presentation">Repository where scientific data and</span> <span dir="ltr" style="left: 444.083px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07312);" role="presentation">metadata will be archived: </span></span></strong><span id="page108R_mcid19" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996547);" role="presentation">Provide the name of the repository(ies</span><span dir="ltr" style="left: 430.083px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995392);" role="presentation">) where scientific data and metadata arising from the project will </span><span dir="ltr" style="left: 120.04px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996628);" role="presentation">be archived;</span> <span dir="ltr" style="left: 225.233px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999802);" role="presentation">see</span></span><a title=" Selecting a Data Repository" href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span id="page108R_mcid20" class="markedContent"> <span dir="ltr" style="left: 259.833px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995819);" role="presentation">Selecting a Data Repository</span></span></a><span id="page108R_mcid21" class="markedContent"><span dir="ltr" style="left: 487.083px; top: 983.673px; font-size: 18.4px; font-family: sans-serif;" role="presentation">)</span></span>', '', '<strong>NIH Guidance</strong><br />NIH has provided additional information to assist in selecting suitable repositories for scientific data resulting from funded research: <a title="NOT-OD-21-016." href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html">NOT-OD-21-016.</a><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data."><span style="font-weight: 400;">Genomic data has further guidance and considerations</span></a><span style="font-weight: 400;"> to address in the Plan.</span></p><p><strong>Additional Guidance</strong></p><p><span style="font-weight: 400;">See </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016</span></a><span style="font-weight: 400;"> and </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span style="font-weight: 400;">other guidance on selecting a repository</span></a><span style="font-weight: 400;">, for details on repository considerations. In brief, the first consideration (option 1) goes to whether the FOA or Institute specifies a repository, in which case that repository must be used. The next priority (Option 2A) goes to approved </span><a href="https://www.nlm.nih.gov/NIHbmic/domain_specific_repositories.html"><span style="font-weight: 400;">Open Domain-Specific Data Sharing Repositories</span></a><span style="font-weight: 400;">). If neither of those considerations fit, consider (Option 2B) other potentially suitable options: PubMed Central attachments, </span><a href="https://www.nlm.nih.gov/NIHbmic/generalist_repositories.html"><span style="font-weight: 400;">approved generalist repositories</span></a><span style="font-weight: 400;">, or your organization&rsquo;s institutional repository.</span></p>', '<p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ [</span><span style="font-weight: 400;">Add appropriate NIH-supported data repositories]</span>OR<span style="font-weight: 400;"> ________ </span><span style="font-weight: 400;">[Add appropriate subject or disease repositories]</span><strong><br /><em><br />Sample Language for Dryad Data Repository</em></strong></p><p>Dataset(s) resulting from this research will be shared via the generalist repository Dryad, which provides metadata, persistent identifiers (i.e., DOIs), and long-term access. Dryad is the institutional data repository supported by the University of California and all data is shared under a CC0 waiver, which makes the dataset(s) publicly available. Data will be made available as soon as possible or at the time of associated publication. Dryad datasets are backed up to Merritt, the UC&rsquo;s CoreTrustSeal-certified digital repository, for long-term storage and accessibility. Procedures in place to ensure dataset preservation include storage of data files in multiple geographic locations, regular audits for fixity and authenticity, and succession plans in the event of repository closure.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8673, 704, 4431, 3539, '<strong><span id="page108R_mcid22" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09491);" role="presentation">How scientifi</span><span dir="ltr" style="left: 233.233px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08073);" role="presentation">c data will be findable and identifiable: </span></span></strong><span id="page108R_mcid23" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1068.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99607);" role="presentation">Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier or </span><span dir="ltr" style="left: 120.04px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99633);" role="presentation">other standard indexing tool</span><span dir="ltr" style="left: 346.283px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">s</span><span dir="ltr" style="left: 355.483px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">Unique Persistent Identifiers: The repository assigns datasets a citable, unique persistent identifier, such as a digital object identifier (DOI) or accession number, to support data discovery, reporting, and research assessment. The identifier points to a persistent landing page that remains accessible even if the dataset is de-accessioned or no longer available.</span></p>', '<span style="font-weight: 400;">The _________ [Insert</span><span style="font-weight: 400;"> repository name] </span><span style="font-weight: 400;">provides metadata, persistent identifiers (i.e.,</span><span style="font-weight: 400;"> insert whether DOI, handles, other)</span><span style="font-weight: 400;">, and long-term access. This repository is supported by ________[</span><span style="font-weight: 400;">Insert funder/organization]</span><span style="font-weight: 400;"> and dataset(s) are available under a _______ [</span><span style="font-weight: 400;">Insert license information</span><span style="font-weight: 400;">] </span><strong>OR</strong><span style="font-weight: 400;"> through a request process __________ </span><span style="font-weight: 400;">[Insert information about request process]</span><span style="font-weight: 400;">.</span>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8674, 704, 4431, 3540, '<strong>When and how long the scientific data will be made available: </strong>Describe when the scientific data will be made available to other users (i.e., no later than time of an associated publication or end of the performance period, whichever comes first) and for how long data will be available.', '', '<strong>NIH Guidance&nbsp;</strong><br />NIH encourages scientific data be shared as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first. Researchers are encouraged to consider relevant requirements and expectations (e.g., data repository policies, award record retention requirements, journal policies) as guidance for the minimum time frame scientific data should be made available. NIH encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public. Identify any differences in timelines for different subsets of scientific data to be shared.<br /><br /><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations."><span style="font-weight: 400;">Genomic data has further guidance on release </span></a><span style="font-weight: 400;">expectations and timelines.</span><span style="font-weight: 400;"><br /></span>', '<p><span style="font-weight: 400;">Data will be made available as soon as possible or at the time of associated publication.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8675, 704, 4432, 3541, '<strong>Factors affecting subsequent access, distribution, or reuse of scientific data</strong>: NIH expects that in drafting Plans, researchers maximize the appropriate sharing of scientific data. Describe and justify any applicable factors or data use limitations affecting subsequent access, distribution, or reuse of scientific data related to informed consent, privacy and confidentiality protections, and any other considerations that may limit the extent of data sharing. See <a title="Frequently Asked Questions" href="https://sharing.nih.gov/faqs#/data-management-and-sharing-policy.htm">Frequently Asked Questions</a> for examples of justifiable reasons for limiting sharing of data.', '', '<strong><strong>NIH Guidance<br /></strong></strong><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing"><span style="font-weight: 400;">Genomic data may have further considerations</span></a><span style="font-weight: 400;"> to address.&nbsp; The NIH now expects a single data-sharing plan at the time of funding application to satisfy both the Genomic Data Sharing (</span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html"><span style="font-weight: 400;">GDS</span></a><span style="font-weight: 400;">) Policy and the DMS Policy (per </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-198.html"><span style="font-weight: 400;">NOT-OD-22-198</span></a><span style="font-weight: 400;">).</span></p><strong>Additional Guidance&nbsp;<br /></strong><p><span style="font-weight: 400;">Some data may require extra preparation before they can be shared. This is the section to describe what legal, ethical, or technical issues will require limiting the sharing of your data. Examples may include existing legal limits such as data licenses or use agreements, issues of proprietary IP development, technical limits about the size or structure of the data, or ethical issues for human subjects privacy. <br /></span></p><p><span style="font-weight: 400;">Key issues in justification of human subjects data specifically may be informed consent (e.g., disease-specific limitations, particular communities&rsquo; concerns) or privacy and confidentiality protections (i.e., de-identification, Certificates of Confidentiality, and other protective measures). Specific steps for human subjects data preparation can be addressed in the protections for privacy subquestion below.</span></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8676, 704, 4432, 3542, '<span id="page1R_mcid7" class="markedContent"><strong><span dir="ltr" style="left: 120.04px; top: 388.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08402);" role="presentation">Whether access to scientific data will be controlled: </span></strong><span dir="ltr" style="left: 120.04px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.9977);" role="presentation">State whether</span> <span dir="ltr" style="left: 238.433px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999898);" role="presentation">acc</span><span dir="ltr" style="left: 267.033px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995605);" role="presentation">ess to the scientific data</span> <span dir="ltr" style="left: 467.883px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.990262);" role="presentation">will</span> <span dir="ltr" style="left: 498.283px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995661);" role="presentation">be controlled (i.e., made available by a data repository </span><span dir="ltr" style="left: 120.04px; top: 430.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996894);" role="presentation">only after approval)</span><span dir="ltr" style="left: 277.033px; top: 430.59px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>Additional guidance</strong></p><p><span style="font-weight: 400;">Check the repository you intend to use to find out more about whether and how the repository supports controlled access.</span></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8677, 704, 4432, 3543, '<strong>Protections for privacy, rights, and confidentiality of human research participants:</strong><br />If generating scientific data derived from humans, describe how the privacy, rights, and confidentiality of human research participants will be protected (e.g., through de-identification, Certificates of Confidentiality, and other protective measures).', '', '<strong>Additional Guidance</strong><br /><p><span style="font-weight: 400;">Certain kinds of data, especially human subjects data, require extra preparation before they can be shared to ensure participant privacy. In this section, you will describe your approach to preparing human subjects data for sharing and note any additional restrictions or policies that will impact access to your data. If you are working with human subjects you should also describe how you will address data management and sharing in your informed consent process. You will also need to describe your methods for ensuring privacy and confidentiality, including how you will de-identify your data. If you have decided that a controlled access repository (where researchers must apply to access data) is a better fit for your data than an open repository, you should describe the repositorys access procedures. Finally, if there are any other laws, policies, or existing agreements that impact your ability to share your data they should be described here.<br /><br />Issues to consider: <br /></span></p><ul><li class="Normal">Any restrictions imposed by federal, Tribal, or state laws, regulations, or policies, or existing or anticipated agreements (e.g., with third-party funders, with partners, with Health Insurance Portability and Accountability Act (HIPAA) covered entities that provide Protected Health Information under a data use agreement, through licensing limitations attached to materials needed to conduct the research).</li><li class="Normal">Any other considerations that may limit the extent of data sharing.</li></ul>', '<p><strong><em>For researchers working with human subjects data</em></strong></p><p>In order to ensure participant consent for data sharing, IRB paperwork and informed consent documents will include language describing plans for data management and sharing data, describing the motivation for sharing, and explaining that personal identifying information will be removed.</p><p>To protect participant privacy and confidentiality, shared data will be de-identified using the ______ method. [Describe de-identification method, noting any other applicable laws or policies such as HIPAA].<br /><br /></p><p><strong><em>For researchers selecting controlled access repositories</em></strong>&nbsp;</p><p>Given the sensitive nature of the dataset, de-identified human subjects data will be made available in ________ data repository, which restricts access to the data to qualified investigators with an appropriate research question who sign a data use agreement. [Describe data repository access methods and security measures]. &nbsp;</p><p>&nbsp;</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8678, 704, 4433, 3544, 'Describe how compliance with this Plan will be monitored and managed, frequency of oversight, and by whom at your institution (e.g., titles, roles).', '', '<p><strong>Additional Guidance</strong><span style="font-weight: 400;">:</span></p><p><span style="font-weight: 400;">Describe how and by whom compliance with this Plan will be managed.&nbsp;If roles will include the addition of study personnel for data management oversight, see NIH&rsquo;s&nbsp;</span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-015.html"><span style="font-weight: 400;">supplementary guidance on allowable costs for data management and sharing</span></a><span style="font-weight: 400;">. Budget considerations are not addressed in this section but instead, you will request funds towards DMS costs as a line item in the budget form, and provide a brief summary of the DMS Plan and a description of the requested DMS costs in the budget justification.</span></p>', '<p><span style="font-weight: 400;">The following individuals [or just the position titles if unknown] will be responsible for data collection, management, storage, retention, and dissemination of project data, including updating and revising the Data Management and Sharing Plan when necessary.</span><span style="font-weight: 400;">&nbsp;</span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Name, Position Title, Host Institution, ORCID, email</span></li></ul><br /><p><strong><em>Sample Language for budgeting requirements&nbsp;</em></strong></p><br /><p>This project includes the following costs associated with data management and sharing.</p><p>For data curation and the development of related documentation, the project is requesting $______. These funds will allow us to prepare data for sharing including de-identification of data, the incorporation of metadata to ensure discoverability and the data transfer process to ______repository for preservation and access.&nbsp; An additional cost of $ ______ is required to cover data deposit fees for ______ repository, which will cover ______ years of hosting.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8718, 708, 4443, 3533, '<span id="page108R_mcid6" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 266.74px; font-size: 18.4px; font-family: sans-serif;" role="presentation">T</span><span dir="ltr" style="left: 131.44px; top: 266.74px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07328);" role="presentation">ypes and amount of scientific data expected to be generated in the project</span></span>: <span id="page108R_mcid7" class="markedContent"><em><span dir="ltr" style="left: 120.04px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995628);" role="presentation">Summarize the types and</span> <span dir="ltr" style="left: 333.083px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997451);" role="presentation">estimated</span> <span dir="ltr" style="left: 417.683px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995701);" role="presentation">amount of scientific data expected to be generated in the project</span></em><span dir="ltr" style="left: 938.6px; top: 287.94px; font-size: 18.4px; font-family: sans-serif;" role="presentation"><em>.</em><br /></span></span><br />Describe data in general terms that address the type and amount/size of scientific data expected to be collected and used in the project (e.g., 256-channel EEG data and fMRI images from ~50 research participants). Descriptions may indicate the data modality (e.g., imaging, genomic, mobile, survey), level of aggregation (e.g., individual, aggregated, summarized), and/or the degree of data processing that has occurred (i.e., how raw or processed the data will be)', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">The final DMS Policy has </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/about-data-management-and-sharing-policy/research-covered-under-the-data-management-sharing-policy#after"><span style="font-weight: 400;">specific definitions for what Scientific Data is, and what proposals are considered to be producing scientific data</span></a></p><p><span style="font-weight: 400;">Per the </span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-21-013.html"><span style="font-weight: 400;">Policy</span></a><span style="font-weight: 400;">, &ldquo;Even those scientific data not used to support a publication are considered scientific data and within the final DMS Policy&rsquo;s scope. We understand that a lack of publication does not necessarily mean that the findings are null or negative; however, indicating that scientific data are defined independent of publication is sufficient to cover data underlying null or negative findings.&rdquo;</span></p><p><strong>NIH Genomic Data Sharing (GDS) Policy Considerations</strong></p><p>Check if your research is subject to NIH GDS (Genomic Data Sharing) policy using <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/does-gds-apply-to-my-research">this criteria</a> and list those data and the levels of processing here.&nbsp;</p>Individual NIH Institutes and Centers (IC) may have additional expectations or requirements for genomic data sharing as well. Please check the <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/gds-policy-expectations-by-nih-institute-and-center">IC-specific genomic data sharing requirements</a>.<strong><br /></strong>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">This project will produce _________ [<em>Data type, e.g., imaging, sequencing, experimental measurements</em>] data generated/obtained from __________ [<em>Data modality, e.g., instrument, method, survey, experiment, data source</em>].&nbsp; Data will be collected from&nbsp; ___ [<em>number</em>] of research participants/specimens/experiments, generating ___ [<em>number</em>] datasets totaling approximately ___ [<em>amount of data</em>] in size. The following data files will be used or produced in the course of the project: ______ [<em>list input data files, intermediate files, and final, post-processed files</em>]. Raw data will be transformed by ____ [<em>analysis, method</em>], and the subsequent processed dataset used for statistical analysis. To protect research participant identities, ___________ [<em>e.g., individual, aggregated, summarized</em>] data will be made available for sharing.</span></p><p><strong>If working with human subjects, consider adding</strong><span style="font-weight: 400;">: Data collection will be performed at clinical sites in the ____ [<em>location</em>] area(s) with ____ [<em>population(s) being studied; i.e., T2 diabetes</em>].&nbsp;</span></p><p><strong>Sample answer from DMPTool: Basic sciences data</strong></p><p><span style="font-weight: 400;">In this proposed project, data will be generated via the following methods: cell culture, light microscopy, confocal microscopy, real-time quantitative polymerase chain reaction (PCR), and stereological counting techniques. This data will be collected from a minimum of 3 independent experiments, with each independent experiment consisting of 3 groups, Wild-type (Rest+/+), heterozygous (Rest+/&ndash;), and homozygous (Rest&ndash;/&ndash;) from both embryonic stem (ES) cells and the corresponding neural stem/progenitor (NS/P) cells. The total size of the data collected is projected to be 300 GB.</span></p><p><span style="font-weight: 400;">We expect to generate the following data file types and formats during this project: Carl Zeiss microscopic image file (.CZI), images (.TIFF), tabular (.CSV), and Affymetrix GeneChip files (.CEL).</span></p><p><span style="font-weight: 400;">Raw data files will be analyzed to generate CSV files containing counts of cell type, total number of stem cells, and to enable statistical analysis.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8719, 708, 4443, 3534, '<span id="page108R_mcid8" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">S</span><span dir="ltr" style="left: 132.24px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0986);" role="presentation">cientific data</span> <span dir="ltr" style="left: 249.633px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09141);" role="presentation">that</span> <span dir="ltr" style="left: 288.233px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.06656);" role="presentation">will be preserved and shared, and</span> <span dir="ltr" style="left: 587.917px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08099);" role="presentation">the</span> <span dir="ltr" style="left: 620.517px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08772);" role="presentation">rationale for</span> <span dir="ltr" style="left: 731.567px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09897);" role="presentation">doing so</span><span dir="ltr" style="left: 807.967px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">:<em> <span id="page108R_mcid9" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 372.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996025);" role="presentation">Describe which scientific data from the project will be preserved and shared and provide the rationale </span><span dir="ltr" style="left: 120.04px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.00294);" role="presentation">for</span> <span dir="ltr" style="left: 146.64px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995537);" role="presentation">this decision.</span></span></em><br /></span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">NIH does not anticipate that researchers will preserve and share all scientific data generated in a study. Researchers should decide which scientific data to preserve and share based on ethical, legal, and technical factors that may affect the extent to which scientific data are preserved and shared. Provide the rationale for these decisions.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">If human subjects data will be collected and only de-identified subsets are to be shared, consider specific de-identification approaches that fit the population and purposes. Guidance on protecting privacy is at&nbsp; </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html"><span style="font-weight: 400;">NOT-OD-22-213</span></a></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">If you are generating genomic data, follow specific sharing requirements (data submission and release expectation) under the NIH GDS policy (</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations"><span style="font-weight: 400;">five levels of processing and associated expectations for data submission and release</span></a><span style="font-weight: 400;">).</span></p>', '<p><span style="font-weight: 400;">Based on _______ [<em>ethical, legal, technical</em>] considerations, only the following data produced in the course of the project will be preserved and shared: ____ [<em>list subsets of the data to be shared</em>]&nbsp;</span></p><p><em><span style="font-weight: 400;">OR</span></em><strong>&nbsp;</strong></p><p><span style="font-weight: 400;">All data produced in the course of the project will be preserved and shared.&nbsp;</span></p><p><strong>Addendum to DMPTool fill-in-the-blank prompt: Add the following for working with human data</strong><span style="font-weight: 400;">:</span></p><p><span style="font-weight: 400;">The final dataset will include _______[<em>e.g., self-reported demographic and behavioral data from interviews with participants and laboratory data from blood and urine specimens provided</em>]. We will share de-identified individual-participant level (IPD) data. Appropriate measures such as&nbsp; _______ [<em>describe specific de-identification practices to be used</em>] will be used for data de-identification and sharing, and informed consent forms will reflect those plans.</span></p><p><strong>Sample answer from DMPTool: Minimal answer when most data will be shared openly</strong></p><p><span style="font-weight: 400;">In this proposed project, the cleaned, item-level spreadsheet data for all variables will be shared openly, along with example quantifications and transformations from initial raw data. Final files used to generate specific analyses to answer the Specific Aims and related results will also be shared. The rationale for sharing only cleaned data is to foster ease of data reuse.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8720, 708, 4443, 3535, '<p><span style="font-weight: 400;">Metadata, other relevant data, and associated documentation: Briefly list the metadata, other relevant data, and any associated documentation (e.g., study protocols and data collection instruments) that will be made accessible to facilitate interpretation of the scientific data.</span></p>', '', '<p><strong>NIH Guidance<br /></strong><span style="font-weight: 400;">In addition to the documentation examples, consider metadata that will provide additional information intended to make scientific data interpretable and reusable (e.g., date, independent sample and variable construction and description, methodology, data provenance, data transformations, any intermediate or descriptive observational variables).</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt basics</strong></p><p><span style="font-weight: 400;">To facilitate interpretation of the data, ______<em> </em>[<em>e.g., data dictionary, metadata, documentation, statistical analysis plans, bench protocols, data collection instruments</em>] will be created, shared, and associated with the relevant datasets.</span></p><p><strong>DMPTool fill-in-the-blank prompt addendum: Add relevant parts of the following if working with clinical trials</strong></p><p><span style="font-weight: 400;">In addition to ______ [<em>individual participant data (IPD) dataset being shared by restricted access and/or aggregate data being shared openly</em>], the researcher will share the ______ [<em>describe any other elements of the final data package not already addressed</em>]. Documentation and support materials will be compatible with the clinicaltrials.gov Protocol Registration Data Elements.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">To facilitate the interpretation and reuse of the data, a README file and data dictionary will be generated and deposited into a repository along with all shared datasets. The README file will include method description, instrument settings, RRIDs of resources such as antibodies, model organisms, cell lines, plasmids, and other tools (e.g., software, databases, services), and Protocol DOIs issued from protocols.io. The data dictionary will define and describe all variables in the dataset.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8721, 708, 4444, 3536, '<p><span style="font-weight: 400;">State whether specialized tools, software, and/or code are needed to access or manipulate shared scientific data, and if so, provide the name(s) of the needed tool(s) and software and specify how they can be accessed.</span></p><span id="page108R_mcid13" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 688.423px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997943);" role="presentation"><br /></span></span>', '', '<strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">Tool(s) and software should be identified, then plans should specify how the tools can be accessed (e.g., open source and freely available, generally available for a fee in the marketplace, available only from the research team). When known, the longevity or period of time for which custom or proprietary tools will be available should be addressed.</span></p><p><span style="font-weight: 400;">In addition, file formats in which data are saved in a digital format can be divided into two general categories.</span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Proprietary - The specification of the data encoding format is not released or restricted in some way. Proprietary formats can only be easily opened and manipulated by particular software tools.&nbsp;</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Open - The specification of the data encoding format which can be used and implemented by anyone. Open formats can often be easily opened and manipulated by a large number of software tools.</span></li></ul>', '<p><strong>DMPTool fill-in-the-blank prompt: If no specialized tools are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type - Imaging data, survey data, etc.</em>] data will be made available in _____ [<em>csv, txt, dicom, etc</em>.] format and will not require the use of specialized tools to be accessed or manipulated.&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt: If specialized tools (open source or proprietary) are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type&nbsp; - Imaging data, survey data, etc.</em>] data will be made available in _____ format, which requires the use of specialized tools, such as _____ [<em>include list of tools</em>] to be accessed and manipulated.</span></p><p><span style="font-weight: 400;">These tools will be shared openly via ____. <br /><em>OR<br /></em> These tools are fee-based, proprietary software. Alternative access to the data will be provided by [<em>describe the strategy for other sites to see or work with the data - potential strategies include committing to provide links to file viewers, or exporting files to a nonproprietary format for limited use and reuse</em>].</span></p><p><strong>Sample answer from DMPtool: Animal studies with computational modeling</strong></p><p><span style="font-weight: 400;">The raw data generated via the confocal microscope is in the Carl Zeiss (.czi) file format. Zeiss software or Fiji ImageJ is required to access the raw data. The raw data generated via the Affymetrix Mouse Genome 430 2.0 Array is in the .CEL format. Statistical programs such as MATLAB or R can be used to analyze the raw data present in the CEL file.</span></p><p><span style="font-weight: 400;">Fiji ImageJ is open-source software that can be downloaded freely online. Links to this or other open-source viewers will be included with the documentation for the shared dataset. Matlab is available for purchase from Mathworks. R is a free software environment for statistical computing and graphics. RStudio is a free R development environment that runs on most operating systems. R Scripts produced through the course of the research will be made publicly available on the lab&rsquo;s GitHub repository, and will be provided as Supplementary files for any publications through a Zenodo-GitHub link. Code will be available no later than when a publication has been submitted. </span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8722, 708, 4445, 3537, 'State what common data standards will be applied to the scientific data and associated metadata to enable interoperability of datasets and resources, and provide the name(s) of the data standards that will be applied and describe how these data standards will be applied to the scientific data generated by the research proposed in this project. If applicable, indicate that no consensus standards exist', '', '<strong>NIH Guidance</strong><br />While many scientific fields have developed and adopted common data standards, others have not. In such cases, the Plan may indicate that no consensus data standards exist for the scientific data and metadata to be generated, preserved, and shared.<br /><br /><strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">A </span><em><span style="font-weight: 400;">standard</span></em><span style="font-weight: 400;"> specifies how exactly data and related materials should be stored, organized, and described. In the context of research data, the term typically refers to the use of specific and well-defined formats, schemas, vocabularies, and ontologies in the description and organization of data. However, for researchers within a community where more formal standards have not been well established, it can also be interpreted more broadly to refer to the adoption of the same (or similar) data management-related activities or strategies by different researchers and across different projects.</span></p><p><span style="font-weight: 400;">It is possible that your work will employ multiple formal standards or a mix of formal standards and other data management strategies. You should be as specific as possible when describing the standards used for each type of data included in your proposal.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Data will be stored in common and open formats, such as ____ for our ____ data. Information needed to make use of this data [<em>e.g., the meaning of variable names, codes, information about missing data, other metadata, etc.</em>] along with references to the sources of those standardized names and metadata items will be included wherever applicable.</span></p><p><strong>Addendum DMPTool fill-in-the-blank for if there <em>are</em> formal data standards for some/all of the data:&nbsp;</strong></p><p><span style="font-weight: 400;">Whenever possible, we will use ______ [<em>common data elements, standardized survey instruments, etc</em>.] to structure and organize our data.</span></p><p><span style="font-weight: 400;">Our ____ data will be structured and described using the ____ standard, which has been widely adopted in the ____ community. [<em>Add additional information about this standard, if applicable - e.g., implementation in data repositories, utility in combining/reusing datasets</em>]</span></p><p><strong>Addendum DMPTool fill-in-the-blank prompt for if there </strong><strong><em>are no</em></strong><strong> formal standards:&nbsp;</strong></p><p><span style="font-weight: 400;">Formal standards for ____ data have not yet been widely adopted. However, our data and other materials will be structured and described according to best practices which are as follows: [<em>list appropriate best practices</em>].</span></p><p><strong>Sample answer from DMPTool</strong></p><p><span style="font-weight: 400;">In accordance with FAIR Principles for data, we will use open file formats (e.g. JPEG, MP4, CSV, TXT, PDF, HTML, etc.) and persistent unique identifiers (PIDs) such as RRIDs for resources (e.g., organisms, plasmids, antibodies, cell lines, software tools, and databases) and DOIs for protocols using protocols.io. The bioimaging community has not yet agreed on a single standard data format that is generated by all acquisition systems, but we will use OME-Files for data that will be preserved and shared.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8723, 708, 4446, 3538, '<strong><span id="page108R_mcid18" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0786);" role="presentation">Repository where scientific data and</span> <span dir="ltr" style="left: 444.083px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07312);" role="presentation">metadata will be archived: </span></span></strong><span id="page108R_mcid19" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996547);" role="presentation">Provide the name of the repository(ies</span><span dir="ltr" style="left: 430.083px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995392);" role="presentation">) where scientific data and metadata arising from the project will </span><span dir="ltr" style="left: 120.04px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996628);" role="presentation">be archived;</span> <span dir="ltr" style="left: 225.233px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999802);" role="presentation">see</span></span><a title=" Selecting a Data Repository" href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span id="page108R_mcid20" class="markedContent"> <span dir="ltr" style="left: 259.833px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995819);" role="presentation">Selecting a Data Repository</span></span></a><span id="page108R_mcid21" class="markedContent"><span dir="ltr" style="left: 487.083px; top: 983.673px; font-size: 18.4px; font-family: sans-serif;" role="presentation">)</span></span>', '', '<strong>NIH Guidance</strong><p><span style="font-weight: 400;">NIH has provided additional information to assist in selecting suitable repositories for scientific data resulting from funded research: </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016.</span></a></p><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data."><span style="font-weight: 400;">Genomic data has further guidance and considerations</span></a><span style="font-weight: 400;"> to address in the Plan.</span></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">See </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016</span></a><span style="font-weight: 400;"> and </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span style="font-weight: 400;">other guidance on selecting a repository</span></a><span style="font-weight: 400;"> for details on repository considerations. In brief, first consideration (option 1) goes to whether the FOA or Institute specifies a repository, in which case that repository must be used. Next priority (Option 2A) goes to approved </span><a href="https://www.nlm.nih.gov/NIHbmic/domain_specific_repositories.html"><span style="font-weight: 400;">Open Domain-Specific Data Sharing Repositories</span></a><span style="font-weight: 400;">). If neither of those considerations fit, consider (Option 2B) other potentially suitable options: PubMed Central attachments, </span><a href="https://www.nlm.nih.gov/NIHbmic/generalist_repositories.html"><span style="font-weight: 400;">approved generalist repositories</span></a><span style="font-weight: 400;">, or your organization&rsquo;s institutional repository.</span></p><p><strong>NIH GDS Policy Considerations</strong></p><span style="font-weight: 400;">If your research is subject to GDS policy, please refer to recommended repositories on the &ldquo;</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data"><span style="font-weight: 400;">Where to Submit Genomic Data</span></a><span style="font-weight: 400;">&rdquo; page on the NIH sharing site.</span>', '<p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ <em>[</em></span><span style="font-weight: 400;">A<em>dd appropriate NIH-supported data repositories</em>]</span><em>OR</em><span style="font-weight: 400;"> ________ </span><span style="font-weight: 400;">[<em>Add appropriate subject or disease repositories</em>]</span><strong><br /><em><br /></em></strong></p><p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ [<em>Add appropriate NIH-supported data repositories</em>] OR ________ [<em>Add appropriate discipline- or data-specific repository, generalist repository, or your institutional data repository</em>]</span></p><p><strong>Sample answer from DMPTool: Minimal information, imaging study</strong></p><p><span style="font-weight: 400;">Imaging data will be deposited into NCI&rsquo;s Imaging Data Commons. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into Zenodo.</span></p><p><strong>Sample answer from DMPTool: Minimal information, clinical study</strong></p><p><span style="font-weight: 400;">Aggregate clinical trials data from all arms of the study will be available in clinicaltrials.gov, along with related metadata. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into the National Addiction &amp; HIV Data Archive Program (NAHDAP) repository.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8724, 708, 4446, 3539, '<strong><span id="page108R_mcid22" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09491);" role="presentation">How scientifi</span><span dir="ltr" style="left: 233.233px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08073);" role="presentation">c data will be findable and identifiable: </span></span></strong><span id="page108R_mcid23" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1068.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99607);" role="presentation">Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier or </span><span dir="ltr" style="left: 120.04px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99633);" role="presentation">other standard indexing tool</span><span dir="ltr" style="left: 346.283px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">s</span><span dir="ltr" style="left: 355.483px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">Unique Persistent Identifiers: The repository assigns datasets a citable, unique persistent identifier, such as a digital object identifier (DOI) or accession number, to support data discovery, reporting, and research assessment. The identifier points to a persistent landing page that remains accessible even if the dataset is de-accessioned or no longer available.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [insert whether DOI, handles, other], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available under a _______ [<em>Insert license information</em>]&nbsp;</span></p><p><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [<em>insert whether DOI, handles, other</em>], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available&nbsp; through a request process __________ [<em>Insert information about request process</em>].</span></p><p><strong>Sample answer by DMPTool: Minimal information</strong></p><p><span style="font-weight: 400;">[Repository Name] provides searchable study-level metadata for dataset discovery. [Repository] assigns DOIs as persistent identifiers, and has a robust preservation plan to ensure long-term access. Data will be discoverable online through standard web search of the study-level metadata as well as the persistent pointer from the DOI to the dataset.</span></p><p><strong>Sample answer by DMPTool: Vivli clinical trials data repository</strong></p><p><span style="font-weight: 400;">Vivli provides access to data and documentation through study-level metadata specific to clinical trials description, long-term preservation and access, and Vivli-issued DOIs. In addition to DOIs, Vivli records are cross-searchable by the clinicaltrials.gov registration ID. Access request processes are described in detail with each data record.</span></p><p><strong>Sample answer by DMPTool: Expanded identifier discovery information</strong></p><p><span style="font-weight: 400;">We will use Persistent Unique Identifiers (PIDs) to improve data findability across all dissemination outputs. PIDs used will include ORCID iDs for people, DOIs for outputs (e.g., datasets, protocols), Research Resource IDentifiers (RRIDs) for resources, and Research Organization Registry (ROR) IDs and funder IDs for places, as much as possible to make data identifiable and findable. We will also use indexed metadata, such as MeSH terms with a unique URL to make scientific data easily findable. We will keep our ORCID Records up to date with DOIs for our datasets and publications, ROR, and funder IDs to increase findability.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8725, 708, 4446, 3540, '<strong>When and how long the scientific data will be made available: </strong>Describe when the scientific data will be made available to other users (i.e., no later than time of an associated publication or end of the performance period, whichever comes first) and for how long data will be available.', '', '<strong>NIH Guidance <br /></strong>NIH encourages scientific data be shared as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first. Researchers are encouraged to consider relevant requirements and expectations (e.g., data repository policies, award record retention requirements, journal policies) as guidance for the minimum time frame scientific data should be made available. NIH encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public. Identify any differences in timelines for different subsets of scientific data to be shared.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong><br /></strong><span style="font-weight: 400;"><br />&nbsp;<a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations.">Genomic data has further guidance on release </a>expectations and timelines. &nbsp; <br /></span>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Shared data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of _____[<em>duration</em>] years after the end of the funding period.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">All scientific data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of 10 years after the funding period.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8726, 708, 4447, 3541, '<strong>Factors affecting subsequent access, distribution, or reuse of scientific data</strong>: NIH expects that in drafting Plans, researchers maximize the appropriate sharing of scientific data. Describe and justify any applicable factors or data use limitations affecting subsequent access, distribution, or reuse of scientific data related to informed consent, privacy and confidentiality protections, and any other considerations that may limit the extent of data sharing. See <a title="Frequently Asked Questions" href="https://sharing.nih.gov/faqs#/data-management-and-sharing-policy.htm">Frequently Asked Questions</a> for examples of justifiable reasons for limiting sharing of data.', '', '<strong><strong>NIH Guidance</strong></strong><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing"><span style="font-weight: 400;">Genomic data may have further considerations</span></a><span style="font-weight: 400;"> to address.&nbsp; The NIH now expects a single data sharing plan at time of funding application satisfies both the Genomic Data Sharing (</span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html"><span style="font-weight: 400;">GDS</span></a><span style="font-weight: 400;">) Policy and the DMS Policy (per </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-198.html"><span style="font-weight: 400;">NOT-OD-22-198</span></a><span style="font-weight: 400;">).</span></p><p><strong>Additional DMPTool&nbsp; Guidance&nbsp;</strong><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Some data may require extra preparation before they can be shared. This is the section to describe what legal, ethical, or technical issues will require limiting the sharing of your data. Examples may include existing legal limits such as data licenses or use agreements, issues of proprietary IP development, technical limits about the size or structure of the data, or ethical issues for human subjects privacy.&nbsp;</span></p><p><span style="font-weight: 400;">Key issues in justification of human subjects data specifically may be informed consent (e.g., disease-specific limitations, particular communities&rsquo; concerns) or privacy and confidentiality protections (i.e., de-identification, Certificates of Confidentiality, and other protective measures). Specific steps for human subjects data preparation can be addressed in the protections for privacy subquestion below.</span><span style="font-weight: 400;"><br /><br /></span></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">How to access genomic data varies depending on which repository you selected. Please refer to the </span><a href="https://sharing.nih.gov/accessing-data/accessing-genomic-data/accessing-genomic-data-from-nih-repositories"><span style="font-weight: 400;">Accessing Genomic Data from NIH Repositories page</span></a><span style="font-weight: 400;"> on the NIH sharing site.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">There are no anticipated factors or limitations that will affect the access, distribution or reuse of the scientific data generated by the proposal.&nbsp;<br /><br /></span><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">Due to _______ [<em>ethical/legal/technical considerations</em>], access/distribution/reuse of the resulting scientific data will be limited and approved/monitored by ________ [<em>describe the approach to limiting access/distribution/reuse</em>].</span><span style="font-weight: 400;"><br /></span></p><p><strong>Sample answer by DMPTool for animal studies</strong></p><p><span style="font-weight: 400;">To address safety and security concerns related to capturing and distributing pictures or video of vertebrate research animals, access and distribution of behavioral video files generated in our lab during brain inactivation studies of non-human primates will be limited as described and justified in the IACUC protocol governing the project and in compliance with the "Image Recordings of Research Animals" Standard Operating Procedure at our institution. There are no other factors that will impact access, distribution, and reuse for all other scientific data generated by this study.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8727, 708, 4447, 3542, '<span id="page1R_mcid7" class="markedContent"><strong><span dir="ltr" style="left: 120.04px; top: 388.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08402);" role="presentation">Whether access to scientific data will be controlled: </span></strong><span dir="ltr" style="left: 120.04px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.9977);" role="presentation">State whether</span> <span dir="ltr" style="left: 238.433px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999898);" role="presentation">acc</span><span dir="ltr" style="left: 267.033px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995605);" role="presentation">ess to the scientific data</span> <span dir="ltr" style="left: 467.883px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.990262);" role="presentation">will</span> <span dir="ltr" style="left: 498.283px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995661);" role="presentation">be controlled (i.e., made available by a data repository </span><span dir="ltr" style="left: 120.04px; top: 430.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996894);" role="presentation">only after approval)</span><span dir="ltr" style="left: 277.033px; top: 430.59px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>Additional DMPTool guidance</strong></p><p><span style="font-weight: 400;">Check the repository you intend to use to find out more about whether and how the repository supports controlled access.</span></p>', '<p><strong>Researchers who are not using controlled access repositories can skip this section or state:</strong></p><p><span style="font-weight: 400;">Controlled access will not be used. The data that is shared will be shared by unrestricted download.</span></p><p><strong>DMPTool fill-in-the-blank prompts for researchers selecting controlled access repositories&nbsp;</strong></p><p><span style="font-weight: 400;">Given the sensitive nature of the dataset, data will be made available in ________ data repository, which restricts access to the data to ______ [describe restriction, e.g. to qualified investigators with an appropriate research question and approved data use agreement (DUA)]. Data can be accessed by _____ [describe data repository access methods and measures].&nbsp;&nbsp;</span></p><p><strong>Sample answer from DMPTool for clinical trials data sharing in the Vivli repository</strong></p><p><span style="font-weight: 400;">Data will be available by controlled access only. To access data arising from this project, users must complete the Vivli data request form and sign the Vivli Data Use Agreement (DUA), which limits subsequent use to the terms of the approved request and requires that users maintain data security, and refrain from any attempts to re-identify research participants or engage in any unauthorized uses of the data. To get access to the data, the user must submit a valid scientific question, include a statistical analysis plan, and complete all required fields on the Vivli data request form. Vivli will review the data request for completeness.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8728, 708, 4447, 3543, '<strong>Protections for privacy, rights, and confidentiality of human research participants:</strong><br />If generating scientific data derived from humans, describe how the privacy, rights, and confidentiality of human research participants will be protected (e.g., through de-identification, Certificates of Confidentiality, and other protective measures).', '', '<p><strong>Additional DMPTool Guidance</strong></p><p><span style="font-weight: 400;">Certain kinds of data, especially human subjects data, require extra preparation before they can be shared to ensure participant privacy. In this section, you will describe your approach to preparing human subjects data for sharing and note any additional restrictions or policies that will impact access to your data. If you are working with human subjects you should also describe how you will address data management and sharing in your informed consent process. You will also need to describe your methods for ensuring privacy and confidentiality, including how you will de-identify your data. If you have decided that a controlled access repository (where researchers must apply to access data) is a better fit for your data than an open repository, you should describe the repositorys access procedures. Finally, if there are any other laws, policies, or existing agreements that impact your ability to share your data, they should be described here.<br /></span><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Issues to consider:</span><span style="font-weight: 400;"><br /></span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any restrictions imposed by federal, Tribal, or state laws, regulations, or policies, or existing or anticipated agreements (e.g., with third-party funders, with partners, with Health Insurance Portability and Accountability Act (HIPAA) covered entities that provide Protected Health Information under a data use agreement, through licensing limitations attached to materials needed to conduct the research).</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any other considerations that may limit the extent of data sharing.</span></li></ul>', '<p><span style="font-weight: 400;">This subsection applies to studies involving human research participants. Other studies can generally skip question 5.3&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt for human subjects data</strong></p><p><span style="font-weight: 400;">In order to ensure participant consent for data sharing, IRB paperwork and informed consent documents will include language describing plans for data management and sharing of data, describing the motivation for sharing, and explaining that personal identifying information will be removed.</span></p><p><span style="font-weight: 400;">To protect participant privacy and confidentiality, shared data will be de-identified using the ______ methods [<em>describe de-identification method, noting any other applicable laws or policies such as HIPAA</em>].</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8729, 708, 4448, 3544, 'Describe how compliance with this Plan will be monitored and managed, frequency of oversight, and by whom at your institution (e.g., titles, roles).', '', '<p><strong>Additional DMPTool Guidance</strong><span style="font-weight: 400;">:</span></p><p><span style="font-weight: 400;">Describe how and by whom compliance with this Plan will be managed.&nbsp;If oversight and roles will include the addition of study personnel for oversight of data management and sharing, </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/budgeting-for-data-management-sharing#requesting-&amp;-justifying-costs-for-data-management-and-sharing"><span style="font-weight: 400;">describe reasonable, allowable personnel costs in the budget justification</span></a><span style="font-weight: 400;"> rather than the DMS Plan.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt&nbsp;</strong></p><p><span style="font-weight: 400;">Lead PI ____[<em>name</em>]___, ORCID: __[<em>ORCID ID</em>]___, will be responsible for the day-to-day oversight of lab/team data management activities and data sharing. Broader issues of DMS Plan compliance oversight and reporting will be handled by the PI and Co-I team as part of general [campus(es)] stewardship, reporting, and compliance processes.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8730, 709, 4449, 3533, '<span id="page108R_mcid6" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 266.74px; font-size: 18.4px; font-family: sans-serif;" role="presentation">T</span><span dir="ltr" style="left: 131.44px; top: 266.74px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07328);" role="presentation">ypes and amount of scientific data expected to be generated in the project</span></span>: <span id="page108R_mcid7" class="markedContent"><em><span dir="ltr" style="left: 120.04px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995628);" role="presentation">Summarize the types and</span> <span dir="ltr" style="left: 333.083px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997451);" role="presentation">estimated</span> <span dir="ltr" style="left: 417.683px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995701);" role="presentation">amount of scientific data expected to be generated in the project</span></em><span dir="ltr" style="left: 938.6px; top: 287.94px; font-size: 18.4px; font-family: sans-serif;" role="presentation"><em>.</em><br /></span></span><br />Describe data in general terms that address the type and amount/size of scientific data expected to be collected and used in the project (e.g., 256-channel EEG data and fMRI images from ~50 research participants). Descriptions may indicate the data modality (e.g., imaging, genomic, mobile, survey), level of aggregation (e.g., individual, aggregated, summarized), and/or the degree of data processing that has occurred (i.e., how raw or processed the data will be)', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">The final DMS Policy has </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/about-data-management-and-sharing-policy/research-covered-under-the-data-management-sharing-policy#after"><span style="font-weight: 400;">specific definitions for what Scientific Data is, and what proposals are considered to be producing scientific data</span></a></p><p><span style="font-weight: 400;">Per the </span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-21-013.html"><span style="font-weight: 400;">Policy</span></a><span style="font-weight: 400;">, &ldquo;Even those scientific data not used to support a publication are considered scientific data and within the final DMS Policy&rsquo;s scope. We understand that a lack of publication does not necessarily mean that the findings are null or negative; however, indicating that scientific data are defined independent of publication is sufficient to cover data underlying null or negative findings.&rdquo;</span></p><p><strong>NIH Genomic Data Sharing (GDS) Policy Considerations</strong></p><p>Check if your research is subject to NIH GDS (Genomic Data Sharing) policy using <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/does-gds-apply-to-my-research">this criteria</a> and list those data and the levels of processing here.&nbsp;</p>Individual NIH Institutes and Centers (IC) may have additional expectations or requirements for genomic data sharing as well. Please check the <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/gds-policy-expectations-by-nih-institute-and-center">IC-specific genomic data sharing requirements</a>.<strong><br /></strong>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">This project will produce _________ [<em>Data type, e.g., imaging, sequencing, experimental measurements</em>] data generated/obtained from __________ [<em>Data modality, e.g., instrument, method, survey, experiment, data source</em>].&nbsp; Data will be collected from&nbsp; ___ [<em>number</em>] of research participants/specimens/experiments, generating ___ [<em>number</em>] datasets totaling approximately ___ [<em>amount of data</em>] in size. The following data files will be used or produced in the course of the project: ______ [<em>list input data files, intermediate files, and final, post-processed files</em>]. Raw data will be transformed by ____ [<em>analysis, method</em>], and the subsequent processed dataset used for statistical analysis. To protect research participant identities, ___________ [<em>e.g., individual, aggregated, summarized</em>] data will be made available for sharing.</span></p><p><strong>If working with human subjects, consider adding</strong><span style="font-weight: 400;">: Data collection will be performed at clinical sites in the ____ [<em>location</em>] area(s) with ____ [<em>population(s) being studied; i.e., T2 diabetes</em>].&nbsp;</span></p><p><strong>Sample answer from DMPTool: Basic sciences data</strong></p><p><span style="font-weight: 400;">In this proposed project, data will be generated via the following methods: cell culture, light microscopy, confocal microscopy, real-time quantitative polymerase chain reaction (PCR), and stereological counting techniques. This data will be collected from a minimum of 3 independent experiments, with each independent experiment consisting of 3 groups, Wild-type (Rest+/+), heterozygous (Rest+/&ndash;), and homozygous (Rest&ndash;/&ndash;) from both embryonic stem (ES) cells and the corresponding neural stem/progenitor (NS/P) cells. The total size of the data collected is projected to be 300 GB.</span></p><p><span style="font-weight: 400;">We expect to generate the following data file types and formats during this project: Carl Zeiss microscopic image file (.CZI), images (.TIFF), tabular (.CSV), and Affymetrix GeneChip files (.CEL).</span></p><p><span style="font-weight: 400;">Raw data files will be analyzed to generate CSV files containing counts of cell type, total number of stem cells, and to enable statistical analysis.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8731, 709, 4449, 3534, '<span id="page108R_mcid8" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">S</span><span dir="ltr" style="left: 132.24px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0986);" role="presentation">cientific data</span> <span dir="ltr" style="left: 249.633px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09141);" role="presentation">that</span> <span dir="ltr" style="left: 288.233px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.06656);" role="presentation">will be preserved and shared, and</span> <span dir="ltr" style="left: 587.917px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08099);" role="presentation">the</span> <span dir="ltr" style="left: 620.517px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08772);" role="presentation">rationale for</span> <span dir="ltr" style="left: 731.567px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09897);" role="presentation">doing so</span><span dir="ltr" style="left: 807.967px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">:<em> <span id="page108R_mcid9" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 372.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996025);" role="presentation">Describe which scientific data from the project will be preserved and shared and provide the rationale </span><span dir="ltr" style="left: 120.04px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.00294);" role="presentation">for</span> <span dir="ltr" style="left: 146.64px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995537);" role="presentation">this decision.</span></span></em><br /></span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">NIH does not anticipate that researchers will preserve and share all scientific data generated in a study. Researchers should decide which scientific data to preserve and share based on ethical, legal, and technical factors that may affect the extent to which scientific data are preserved and shared. Provide the rationale for these decisions.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">If human subjects data will be collected and only de-identified subsets are to be shared, consider specific de-identification approaches that fit the population and purposes. Guidance on protecting privacy is at&nbsp; </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html"><span style="font-weight: 400;">NOT-OD-22-213</span></a></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">If you are generating genomic data, follow specific sharing requirements (data submission and release expectation) under the NIH GDS policy (</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations"><span style="font-weight: 400;">five levels of processing and associated expectations for data submission and release</span></a><span style="font-weight: 400;">).</span></p>', '<p><span style="font-weight: 400;">Based on _______ [<em>ethical, legal, technical</em>] considerations, only the following data produced in the course of the project will be preserved and shared: ____ [<em>list subsets of the data to be shared</em>]&nbsp;</span></p><p><em><span style="font-weight: 400;">OR</span></em><strong>&nbsp;</strong></p><p><span style="font-weight: 400;">All data produced in the course of the project will be preserved and shared.&nbsp;</span></p><p><strong>Addendum to DMPTool fill-in-the-blank prompt: Add the following for working with human data</strong><span style="font-weight: 400;">:</span></p><p><span style="font-weight: 400;">The final dataset will include _______[<em>e.g., self-reported demographic and behavioral data from interviews with participants and laboratory data from blood and urine specimens provided</em>]. We will share de-identified individual-participant level (IPD) data. Appropriate measures such as&nbsp; _______ [<em>describe specific de-identification practices to be used</em>] will be used for data de-identification and sharing, and informed consent forms will reflect those plans.</span></p><p><strong>Sample answer from DMPTool: Minimal answer when most data will be shared openly</strong></p><p><span style="font-weight: 400;">In this proposed project, the cleaned, item-level spreadsheet data for all variables will be shared openly, along with example quantifications and transformations from initial raw data. Final files used to generate specific analyses to answer the Specific Aims and related results will also be shared. The rationale for sharing only cleaned data is to foster ease of data reuse.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8732, 709, 4449, 3535, '<p><span style="font-weight: 400;">Metadata, other relevant data, and associated documentation: Briefly list the metadata, other relevant data, and any associated documentation (e.g., study protocols and data collection instruments) that will be made accessible to facilitate interpretation of the scientific data.</span></p>', '', '<p><strong>NIH Guidance<br /></strong><span style="font-weight: 400;">In addition to the documentation examples, consider metadata that will provide additional information intended to make scientific data interpretable and reusable (e.g., date, independent sample and variable construction and description, methodology, data provenance, data transformations, any intermediate or descriptive observational variables).</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt basics</strong></p><p><span style="font-weight: 400;">To facilitate interpretation of the data, ______<em> </em>[<em>e.g., data dictionary, metadata, documentation, statistical analysis plans, bench protocols, data collection instruments</em>] will be created, shared, and associated with the relevant datasets.</span></p><p><strong>DMPTool fill-in-the-blank prompt addendum: Add relevant parts of the following if working with clinical trials</strong></p><p><span style="font-weight: 400;">In addition to ______ [<em>individual participant data (IPD) dataset being shared by restricted access and/or aggregate data being shared openly</em>], the researcher will share the ______ [<em>describe any other elements of the final data package not already addressed</em>]. Documentation and support materials will be compatible with the clinicaltrials.gov Protocol Registration Data Elements.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">To facilitate the interpretation and reuse of the data, a README file and data dictionary will be generated and deposited into a repository along with all shared datasets. The README file will include method description, instrument settings, RRIDs of resources such as antibodies, model organisms, cell lines, plasmids, and other tools (e.g., software, databases, services), and Protocol DOIs issued from protocols.io. The data dictionary will define and describe all variables in the dataset.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8733, 709, 4450, 3536, '<p><span style="font-weight: 400;">State whether specialized tools, software, and/or code are needed to access or manipulate shared scientific data, and if so, provide the name(s) of the needed tool(s) and software and specify how they can be accessed.</span></p><span id="page108R_mcid13" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 688.423px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997943);" role="presentation"><br /></span></span>', '', '<strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">Tool(s) and software should be identified, then plans should specify how the tools can be accessed (e.g., open source and freely available, generally available for a fee in the marketplace, available only from the research team). When known, the longevity or period of time for which custom or proprietary tools will be available should be addressed.</span></p><p><span style="font-weight: 400;">In addition, file formats in which data are saved in a digital format can be divided into two general categories.</span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Proprietary - The specification of the data encoding format is not released or restricted in some way. Proprietary formats can only be easily opened and manipulated by particular software tools.&nbsp;</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Open - The specification of the data encoding format which can be used and implemented by anyone. Open formats can often be easily opened and manipulated by a large number of software tools.</span></li></ul>', '<p><strong>DMPTool fill-in-the-blank prompt: If no specialized tools are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type - Imaging data, survey data, etc.</em>] data will be made available in _____ [<em>csv, txt, dicom, etc</em>.] format and will not require the use of specialized tools to be accessed or manipulated.&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt: If specialized tools (open source or proprietary) are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type&nbsp; - Imaging data, survey data, etc.</em>] data will be made available in _____ format, which requires the use of specialized tools, such as _____ [<em>include list of tools</em>] to be accessed and manipulated.</span></p><p><span style="font-weight: 400;">These tools will be shared openly via ____. <br /><em>OR<br /></em> These tools are fee-based, proprietary software. Alternative access to the data will be provided by [<em>describe the strategy for other sites to see or work with the data - potential strategies include committing to provide links to file viewers, or exporting files to a nonproprietary format for limited use and reuse</em>].</span></p><p><strong>Sample answer from DMPtool: Animal studies with computational modeling</strong></p><p><span style="font-weight: 400;">The raw data generated via the confocal microscope is in the Carl Zeiss (.czi) file format. Zeiss software or Fiji ImageJ is required to access the raw data. The raw data generated via the Affymetrix Mouse Genome 430 2.0 Array is in the .CEL format. Statistical programs such as MATLAB or R can be used to analyze the raw data present in the CEL file.</span></p><p><span style="font-weight: 400;">Fiji ImageJ is open-source software that can be downloaded freely online. Links to this or other open-source viewers will be included with the documentation for the shared dataset. Matlab is available for purchase from Mathworks. R is a free software environment for statistical computing and graphics. RStudio is a free R development environment that runs on most operating systems. R Scripts produced through the course of the research will be made publicly available on the lab&rsquo;s GitHub repository, and will be provided as Supplementary files for any publications through a Zenodo-GitHub link. Code will be available no later than when a publication has been submitted. </span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8734, 709, 4451, 3537, 'State what common data standards will be applied to the scientific data and associated metadata to enable interoperability of datasets and resources, and provide the name(s) of the data standards that will be applied and describe how these data standards will be applied to the scientific data generated by the research proposed in this project. If applicable, indicate that no consensus standards exist', '', '<strong>NIH Guidance</strong><br />While many scientific fields have developed and adopted common data standards, others have not. In such cases, the Plan may indicate that no consensus data standards exist for the scientific data and metadata to be generated, preserved, and shared.<br /><br /><strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">A </span><em><span style="font-weight: 400;">standard</span></em><span style="font-weight: 400;"> specifies how exactly data and related materials should be stored, organized, and described. In the context of research data, the term typically refers to the use of specific and well-defined formats, schemas, vocabularies, and ontologies in the description and organization of data. However, for researchers within a community where more formal standards have not been well established, it can also be interpreted more broadly to refer to the adoption of the same (or similar) data management-related activities or strategies by different researchers and across different projects.</span></p><p><span style="font-weight: 400;">It is possible that your work will employ multiple formal standards or a mix of formal standards and other data management strategies. You should be as specific as possible when describing the standards used for each type of data included in your proposal.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Data will be stored in common and open formats, such as ____ for our ____ data. Information needed to make use of this data [<em>e.g., the meaning of variable names, codes, information about missing data, other metadata, etc.</em>] along with references to the sources of those standardized names and metadata items will be included wherever applicable.</span></p><p><strong>Addendum DMPTool fill-in-the-blank for if there <em>are</em> formal data standards for some/all of the data:&nbsp;</strong></p><p><span style="font-weight: 400;">Whenever possible, we will use ______ [<em>common data elements, standardized survey instruments, etc</em>.] to structure and organize our data.</span></p><p><span style="font-weight: 400;">Our ____ data will be structured and described using the ____ standard, which has been widely adopted in the ____ community. [<em>Add additional information about this standard, if applicable - e.g., implementation in data repositories, utility in combining/reusing datasets</em>]</span></p><p><strong>Addendum DMPTool fill-in-the-blank prompt for if there </strong><strong><em>are no</em></strong><strong> formal standards:&nbsp;</strong></p><p><span style="font-weight: 400;">Formal standards for ____ data have not yet been widely adopted. However, our data and other materials will be structured and described according to best practices which are as follows: [<em>list appropriate best practices</em>].</span></p><p><strong>Sample answer from DMPTool</strong></p><p><span style="font-weight: 400;">In accordance with FAIR Principles for data, we will use open file formats (e.g. JPEG, MP4, CSV, TXT, PDF, HTML, etc.) and persistent unique identifiers (PIDs) such as RRIDs for resources (e.g., organisms, plasmids, antibodies, cell lines, software tools, and databases) and DOIs for protocols using protocols.io. The bioimaging community has not yet agreed on a single standard data format that is generated by all acquisition systems, but we will use OME-Files for data that will be preserved and shared.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8735, 709, 4452, 3538, '<strong><span id="page108R_mcid18" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0786);" role="presentation">Repository where scientific data and</span> <span dir="ltr" style="left: 444.083px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07312);" role="presentation">metadata will be archived: </span></span></strong><span id="page108R_mcid19" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996547);" role="presentation">Provide the name of the repository(ies</span><span dir="ltr" style="left: 430.083px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995392);" role="presentation">) where scientific data and metadata arising from the project will </span><span dir="ltr" style="left: 120.04px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996628);" role="presentation">be archived;</span> <span dir="ltr" style="left: 225.233px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999802);" role="presentation">see</span></span><a title=" Selecting a Data Repository" href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span id="page108R_mcid20" class="markedContent"> <span dir="ltr" style="left: 259.833px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995819);" role="presentation">Selecting a Data Repository</span></span></a><span id="page108R_mcid21" class="markedContent"><span dir="ltr" style="left: 487.083px; top: 983.673px; font-size: 18.4px; font-family: sans-serif;" role="presentation">)</span></span>', '', '<strong>NIH Guidance</strong><p><span style="font-weight: 400;">NIH has provided additional information to assist in selecting suitable repositories for scientific data resulting from funded research: </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016.</span></a></p><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data."><span style="font-weight: 400;">Genomic data has further guidance and considerations</span></a><span style="font-weight: 400;"> to address in the Plan.</span></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">See </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016</span></a><span style="font-weight: 400;"> and </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span style="font-weight: 400;">other guidance on selecting a repository</span></a><span style="font-weight: 400;"> for details on repository considerations. In brief, first consideration (option 1) goes to whether the FOA or Institute specifies a repository, in which case that repository must be used. Next priority (Option 2A) goes to approved </span><a href="https://www.nlm.nih.gov/NIHbmic/domain_specific_repositories.html"><span style="font-weight: 400;">Open Domain-Specific Data Sharing Repositories</span></a><span style="font-weight: 400;">). If neither of those considerations fit, consider (Option 2B) other potentially suitable options: PubMed Central attachments, </span><a href="https://www.nlm.nih.gov/NIHbmic/generalist_repositories.html"><span style="font-weight: 400;">approved generalist repositories</span></a><span style="font-weight: 400;">, or your organization&rsquo;s institutional repository.</span></p><p><strong>NIH GDS Policy Considerations</strong></p><span style="font-weight: 400;">If your research is subject to GDS policy, please refer to recommended repositories on the &ldquo;</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data"><span style="font-weight: 400;">Where to Submit Genomic Data</span></a><span style="font-weight: 400;">&rdquo; page on the NIH sharing site.</span>', '<p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ <em>[</em></span><span style="font-weight: 400;">A<em>dd appropriate NIH-supported data repositories</em>]</span><em>OR</em><span style="font-weight: 400;"> ________ </span><span style="font-weight: 400;">[<em>Add appropriate subject or disease repositories</em>]</span><strong><br /><em><br /></em></strong></p><p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ [<em>Add appropriate NIH-supported data repositories</em>] OR ________ [<em>Add appropriate discipline- or data-specific repository, generalist repository, or your institutional data repository</em>]</span></p><p><strong>Sample answer from DMPTool: Minimal information, imaging study</strong></p><p><span style="font-weight: 400;">Imaging data will be deposited into NCI&rsquo;s Imaging Data Commons. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into Zenodo.</span></p><p><strong>Sample answer from DMPTool: Minimal information, clinical study</strong></p><p><span style="font-weight: 400;">Aggregate clinical trials data from all arms of the study will be available in clinicaltrials.gov, along with related metadata. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into the National Addiction &amp; HIV Data Archive Program (NAHDAP) repository.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8736, 709, 4452, 3539, '<strong><span id="page108R_mcid22" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09491);" role="presentation">How scientifi</span><span dir="ltr" style="left: 233.233px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08073);" role="presentation">c data will be findable and identifiable: </span></span></strong><span id="page108R_mcid23" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1068.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99607);" role="presentation">Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier or </span><span dir="ltr" style="left: 120.04px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99633);" role="presentation">other standard indexing tool</span><span dir="ltr" style="left: 346.283px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">s</span><span dir="ltr" style="left: 355.483px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">Unique Persistent Identifiers: The repository assigns datasets a citable, unique persistent identifier, such as a digital object identifier (DOI) or accession number, to support data discovery, reporting, and research assessment. The identifier points to a persistent landing page that remains accessible even if the dataset is de-accessioned or no longer available.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [insert whether DOI, handles, other], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available under a _______ [<em>Insert license information</em>]&nbsp;</span></p><p><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [<em>insert whether DOI, handles, other</em>], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available&nbsp; through a request process __________ [<em>Insert information about request process</em>].</span></p><p><strong>Sample answer by DMPTool: Minimal information</strong></p><p><span style="font-weight: 400;">[Repository Name] provides searchable study-level metadata for dataset discovery. [Repository] assigns DOIs as persistent identifiers, and has a robust preservation plan to ensure long-term access. Data will be discoverable online through standard web search of the study-level metadata as well as the persistent pointer from the DOI to the dataset.</span></p><p><strong>Sample answer by DMPTool: Vivli clinical trials data repository</strong></p><p><span style="font-weight: 400;">Vivli provides access to data and documentation through study-level metadata specific to clinical trials description, long-term preservation and access, and Vivli-issued DOIs. In addition to DOIs, Vivli records are cross-searchable by the clinicaltrials.gov registration ID. Access request processes are described in detail with each data record.</span></p><p><strong>Sample answer by DMPTool: Expanded identifier discovery information</strong></p><p><span style="font-weight: 400;">We will use Persistent Unique Identifiers (PIDs) to improve data findability across all dissemination outputs. PIDs used will include ORCID iDs for people, DOIs for outputs (e.g., datasets, protocols), Research Resource IDentifiers (RRIDs) for resources, and Research Organization Registry (ROR) IDs and funder IDs for places, as much as possible to make data identifiable and findable. We will also use indexed metadata, such as MeSH terms with a unique URL to make scientific data easily findable. We will keep our ORCID Records up to date with DOIs for our datasets and publications, ROR, and funder IDs to increase findability.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8737, 709, 4452, 3540, '<strong>When and how long the scientific data will be made available: </strong>Describe when the scientific data will be made available to other users (i.e., no later than time of an associated publication or end of the performance period, whichever comes first) and for how long data will be available.', '', '<strong>NIH Guidance <br /></strong>NIH encourages scientific data be shared as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first. Researchers are encouraged to consider relevant requirements and expectations (e.g., data repository policies, award record retention requirements, journal policies) as guidance for the minimum time frame scientific data should be made available. NIH encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public. Identify any differences in timelines for different subsets of scientific data to be shared.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong><br /></strong><span style="font-weight: 400;"><br />&nbsp;<a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations.">Genomic data has further guidance on release </a>expectations and timelines. &nbsp; <br /></span>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Shared data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of _____[<em>duration</em>] years after the end of the funding period.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">All scientific data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of 10 years after the funding period.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8738, 709, 4453, 3541, '<strong>Factors affecting subsequent access, distribution, or reuse of scientific data</strong>: NIH expects that in drafting Plans, researchers maximize the appropriate sharing of scientific data. Describe and justify any applicable factors or data use limitations affecting subsequent access, distribution, or reuse of scientific data related to informed consent, privacy and confidentiality protections, and any other considerations that may limit the extent of data sharing. See <a title="Frequently Asked Questions" href="https://sharing.nih.gov/faqs#/data-management-and-sharing-policy.htm">Frequently Asked Questions</a> for examples of justifiable reasons for limiting sharing of data.', '', '<strong><strong>NIH Guidance</strong></strong><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing"><span style="font-weight: 400;">Genomic data may have further considerations</span></a><span style="font-weight: 400;"> to address.&nbsp; The NIH now expects a single data sharing plan at time of funding application satisfies both the Genomic Data Sharing (</span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html"><span style="font-weight: 400;">GDS</span></a><span style="font-weight: 400;">) Policy and the DMS Policy (per </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-198.html"><span style="font-weight: 400;">NOT-OD-22-198</span></a><span style="font-weight: 400;">).</span></p><p><strong>Additional DMPTool&nbsp; Guidance&nbsp;</strong><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Some data may require extra preparation before they can be shared. This is the section to describe what legal, ethical, or technical issues will require limiting the sharing of your data. Examples may include existing legal limits such as data licenses or use agreements, issues of proprietary IP development, technical limits about the size or structure of the data, or ethical issues for human subjects privacy.&nbsp;</span></p><p><span style="font-weight: 400;">Key issues in justification of human subjects data specifically may be informed consent (e.g., disease-specific limitations, particular communities&rsquo; concerns) or privacy and confidentiality protections (i.e., de-identification, Certificates of Confidentiality, and other protective measures). Specific steps for human subjects data preparation can be addressed in the protections for privacy subquestion below.</span><span style="font-weight: 400;"><br /><br /></span></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">How to access genomic data varies depending on which repository you selected. Please refer to the </span><a href="https://sharing.nih.gov/accessing-data/accessing-genomic-data/accessing-genomic-data-from-nih-repositories"><span style="font-weight: 400;">Accessing Genomic Data from NIH Repositories page</span></a><span style="font-weight: 400;"> on the NIH sharing site.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">There are no anticipated factors or limitations that will affect the access, distribution or reuse of the scientific data generated by the proposal.&nbsp;<br /><br /></span><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">Due to _______ [<em>ethical/legal/technical considerations</em>], access/distribution/reuse of the resulting scientific data will be limited and approved/monitored by ________ [<em>describe the approach to limiting access/distribution/reuse</em>].</span><span style="font-weight: 400;"><br /></span></p><p><strong>Sample answer by DMPTool for animal studies</strong></p><p><span style="font-weight: 400;">To address safety and security concerns related to capturing and distributing pictures or video of vertebrate research animals, access and distribution of behavioral video files generated in our lab during brain inactivation studies of non-human primates will be limited as described and justified in the IACUC protocol governing the project and in compliance with the "Image Recordings of Research Animals" Standard Operating Procedure at our institution. There are no other factors that will impact access, distribution, and reuse for all other scientific data generated by this study.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8739, 709, 4453, 3542, '<span id="page1R_mcid7" class="markedContent"><strong><span dir="ltr" style="left: 120.04px; top: 388.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08402);" role="presentation">Whether access to scientific data will be controlled: </span></strong><span dir="ltr" style="left: 120.04px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.9977);" role="presentation">State whether</span> <span dir="ltr" style="left: 238.433px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999898);" role="presentation">acc</span><span dir="ltr" style="left: 267.033px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995605);" role="presentation">ess to the scientific data</span> <span dir="ltr" style="left: 467.883px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.990262);" role="presentation">will</span> <span dir="ltr" style="left: 498.283px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995661);" role="presentation">be controlled (i.e., made available by a data repository </span><span dir="ltr" style="left: 120.04px; top: 430.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996894);" role="presentation">only after approval)</span><span dir="ltr" style="left: 277.033px; top: 430.59px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>Additional DMPTool guidance</strong></p><p><span style="font-weight: 400;">Check the repository you intend to use to find out more about whether and how the repository supports controlled access.</span></p>', '<p><strong>Researchers who are not using controlled access repositories can skip this section or state:</strong></p><p><span style="font-weight: 400;">Controlled access will not be used. The data that is shared will be shared by unrestricted download.</span></p><p><strong>DMPTool fill-in-the-blank prompts for researchers selecting controlled access repositories&nbsp;</strong></p><p><span style="font-weight: 400;">Given the sensitive nature of the dataset, data will be made available in ________ data repository, which restricts access to the data to ______ [describe restriction, e.g. to qualified investigators with an appropriate research question and approved data use agreement (DUA)]. Data can be accessed by _____ [describe data repository access methods and measures].&nbsp;&nbsp;</span></p><p><strong>Sample answer from DMPTool for clinical trials data sharing in the Vivli repository</strong></p><p><span style="font-weight: 400;">Data will be available by controlled access only. To access data arising from this project, users must complete the Vivli data request form and sign the Vivli Data Use Agreement (DUA), which limits subsequent use to the terms of the approved request and requires that users maintain data security, and refrain from any attempts to re-identify research participants or engage in any unauthorized uses of the data. To get access to the data, the user must submit a valid scientific question, include a statistical analysis plan, and complete all required fields on the Vivli data request form. Vivli will review the data request for completeness.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8740, 709, 4453, 3543, '<strong>Protections for privacy, rights, and confidentiality of human research participants:</strong><br />If generating scientific data derived from humans, describe how the privacy, rights, and confidentiality of human research participants will be protected (e.g., through de-identification, Certificates of Confidentiality, and other protective measures).', '', '<p><strong>Additional DMPTool Guidance</strong></p><p><span style="font-weight: 400;">Certain kinds of data, especially human subjects data, require extra preparation before they can be shared to ensure participant privacy. In this section, you will describe your approach to preparing human subjects data for sharing and note any additional restrictions or policies that will impact access to your data. If you are working with human subjects you should also describe how you will address data management and sharing in your informed consent process. You will also need to describe your methods for ensuring privacy and confidentiality, including how you will de-identify your data. If you have decided that a controlled access repository (where researchers must apply to access data) is a better fit for your data than an open repository, you should describe the repositorys access procedures. Finally, if there are any other laws, policies, or existing agreements that impact your ability to share your data, they should be described here.<br /></span><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Issues to consider:</span><span style="font-weight: 400;"><br /></span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any restrictions imposed by federal, Tribal, or state laws, regulations, or policies, or existing or anticipated agreements (e.g., with third-party funders, with partners, with Health Insurance Portability and Accountability Act (HIPAA) covered entities that provide Protected Health Information under a data use agreement, through licensing limitations attached to materials needed to conduct the research).</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any other considerations that may limit the extent of data sharing.</span></li></ul>', '<p><span style="font-weight: 400;">This subsection applies to studies involving human research participants. Other studies can generally skip question 5.3&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt for human subjects data</strong></p><p><span style="font-weight: 400;">In order to ensure participant consent for data sharing, IRB paperwork and informed consent documents will include language describing plans for data management and sharing of data, describing the motivation for sharing, and explaining that personal identifying information will be removed.</span></p><p><span style="font-weight: 400;">To protect participant privacy and confidentiality, shared data will be de-identified using the ______ methods [<em>describe de-identification method, noting any other applicable laws or policies such as HIPAA</em>].</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8741, 709, 4454, 3544, 'Describe how compliance with this Plan will be monitored and managed, frequency of oversight, and by whom at your institution (e.g., titles, roles).', '', '<div><strong>NIH</strong></div><div>This element refers to oversight by the funded institution, rather than by NIH. The DMS Policy does not create any expectations about who will be responsible for Plan oversight at the institution.<br /><strong><br />Additional DMPTool Guidance</strong><span style="font-weight: 400;">:</span></div><p><span style="font-weight: 400;">Describe how and by whom compliance with this Plan will be managed.&nbsp;If oversight and roles will include the addition of study personnel for oversight of data management and sharing, </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/budgeting-for-data-management-sharing#requesting-&amp;-justifying-costs-for-data-management-and-sharing"><span style="font-weight: 400;">describe reasonable, allowable personnel costs in the budget justification</span></a><span style="font-weight: 400;"> rather than the DMS Plan.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt&nbsp;</strong></p><p><span style="font-weight: 400;">Lead PI ____[<em>name</em>]___, ORCID: __[<em>ORCID ID</em>]___, will be responsible for the day-to-day oversight of lab/team data management activities and data sharing. Broader issues of DMS Plan compliance oversight and reporting will be handled by the PI and Co-I team as part of general [campus(es)] stewardship, reporting, and compliance processes.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8768, 713, 4467, 3533, '<span id="page108R_mcid6" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 266.74px; font-size: 18.4px; font-family: sans-serif;" role="presentation">T</span><span dir="ltr" style="left: 131.44px; top: 266.74px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07328);" role="presentation">ypes and amount of scientific data expected to be generated in the project</span></span>: <span id="page108R_mcid7" class="markedContent"><em><span dir="ltr" style="left: 120.04px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995628);" role="presentation">Summarize the types and</span> <span dir="ltr" style="left: 333.083px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997451);" role="presentation">estimated</span> <span dir="ltr" style="left: 417.683px; top: 287.94px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995701);" role="presentation">amount of scientific data expected to be generated in the project</span></em><span dir="ltr" style="left: 938.6px; top: 287.94px; font-size: 18.4px; font-family: sans-serif;" role="presentation"><em>.</em><br /></span></span><br />Describe data in general terms that address the type and amount/size of scientific data expected to be collected and used in the project (e.g., 256-channel EEG data and fMRI images from ~50 research participants). Descriptions may indicate the data modality (e.g., imaging, genomic, mobile, survey), level of aggregation (e.g., individual, aggregated, summarized), and/or the degree of data processing that has occurred (i.e., how raw or processed the data will be)', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">The final DMS Policy has </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/about-data-management-and-sharing-policy/research-covered-under-the-data-management-sharing-policy#after"><span style="font-weight: 400;">specific definitions for what Scientific Data is, and what proposals are considered to be producing scientific data</span></a></p><p><span style="font-weight: 400;">Per the </span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-21-013.html"><span style="font-weight: 400;">Policy</span></a><span style="font-weight: 400;">, &ldquo;Even those scientific data not used to support a publication are considered scientific data and within the final DMS Policy&rsquo;s scope. We understand that a lack of publication does not necessarily mean that the findings are null or negative; however, indicating that scientific data are defined independent of publication is sufficient to cover data underlying null or negative findings.&rdquo;</span></p><p><strong>NIH Genomic Data Sharing (GDS) Policy Considerations</strong></p><p>Check if your research is subject to NIH GDS (Genomic Data Sharing) policy using <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/does-gds-apply-to-my-research">this criteria</a> and list those data and the levels of processing here.&nbsp;</p>Individual NIH Institutes and Centers (IC) may have additional expectations or requirements for genomic data sharing as well. Please check the <a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing/gds-policy-expectations-by-nih-institute-and-center">IC-specific genomic data sharing requirements</a>.<strong><br /></strong>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">This project will produce _________ [<em>Data type, e.g., imaging, sequencing, experimental measurements</em>] data generated/obtained from __________ [<em>Data modality, e.g., instrument, method, survey, experiment, data source</em>].&nbsp; Data will be collected from&nbsp; ___ [<em>number</em>] of research participants/specimens/experiments, generating ___ [<em>number</em>] datasets totaling approximately ___ [<em>amount of data</em>] in size. The following data files will be used or produced in the course of the project: ______ [<em>list input data files, intermediate files, and final, post-processed files</em>]. Raw data will be transformed by ____ [<em>analysis, method</em>], and the subsequent processed dataset used for statistical analysis. To protect research participant identities, ___________ [<em>e.g., individual, aggregated, summarized</em>] data will be made available for sharing.</span></p><p><strong>If working with human subjects, consider adding</strong><span style="font-weight: 400;">: Data collection will be performed at clinical sites in the ____ [<em>location</em>] area(s) with ____ [<em>population(s) being studied; i.e., T2 diabetes</em>].&nbsp;</span></p><p><strong>Sample answer from DMPTool: Basic sciences data</strong></p><p><span style="font-weight: 400;">In this proposed project, data will be generated via the following methods: cell culture, light microscopy, confocal microscopy, real-time quantitative polymerase chain reaction (PCR), and stereological counting techniques. This data will be collected from a minimum of 3 independent experiments, with each independent experiment consisting of 3 groups, Wild-type (Rest+/+), heterozygous (Rest+/&ndash;), and homozygous (Rest&ndash;/&ndash;) from both embryonic stem (ES) cells and the corresponding neural stem/progenitor (NS/P) cells. The total size of the data collected is projected to be 300 GB.</span></p><p><span style="font-weight: 400;">We expect to generate the following data file types and formats during this project: Carl Zeiss microscopic image file (.CZI), images (.TIFF), tabular (.CSV), and Affymetrix GeneChip files (.CEL).</span></p><p><span style="font-weight: 400;">Raw data files will be analyzed to generate CSV files containing counts of cell type, total number of stem cells, and to enable statistical analysis.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8769, 713, 4467, 3534, '<span id="page108R_mcid8" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">S</span><span dir="ltr" style="left: 132.24px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0986);" role="presentation">cientific data</span> <span dir="ltr" style="left: 249.633px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09141);" role="presentation">that</span> <span dir="ltr" style="left: 288.233px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.06656);" role="presentation">will be preserved and shared, and</span> <span dir="ltr" style="left: 587.917px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08099);" role="presentation">the</span> <span dir="ltr" style="left: 620.517px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08772);" role="presentation">rationale for</span> <span dir="ltr" style="left: 731.567px; top: 351.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09897);" role="presentation">doing so</span><span dir="ltr" style="left: 807.967px; top: 351.14px; font-size: 18.4px; font-family: sans-serif;" role="presentation">:<em> <span id="page108R_mcid9" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 372.14px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996025);" role="presentation">Describe which scientific data from the project will be preserved and shared and provide the rationale </span><span dir="ltr" style="left: 120.04px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.00294);" role="presentation">for</span> <span dir="ltr" style="left: 146.64px; top: 393.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995537);" role="presentation">this decision.</span></span></em><br /></span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">NIH does not anticipate that researchers will preserve and share all scientific data generated in a study. Researchers should decide which scientific data to preserve and share based on ethical, legal, and technical factors that may affect the extent to which scientific data are preserved and shared. Provide the rationale for these decisions.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">If human subjects data will be collected and only de-identified subsets are to be shared, consider specific de-identification approaches that fit the population and purposes. Guidance on protecting privacy is at&nbsp; </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-213.html"><span style="font-weight: 400;">NOT-OD-22-213</span></a></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">If you are generating genomic data, follow specific sharing requirements (data submission and release expectation) under the NIH GDS policy (</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations"><span style="font-weight: 400;">five levels of processing and associated expectations for data submission and release</span></a><span style="font-weight: 400;">).</span></p>', '<p><span style="font-weight: 400;">Based on _______ [<em>ethical, legal, technical</em>] considerations, only the following data produced in the course of the project will be preserved and shared: ____ [<em>list subsets of the data to be shared</em>]&nbsp;</span></p><p><em><span style="font-weight: 400;">OR</span></em><strong>&nbsp;</strong></p><p><span style="font-weight: 400;">All data produced in the course of the project will be preserved and shared.&nbsp;</span></p><p><strong>Addendum to DMPTool fill-in-the-blank prompt: Add the following for working with human data</strong><span style="font-weight: 400;">:</span></p><p><span style="font-weight: 400;">The final dataset will include _______[<em>e.g., self-reported demographic and behavioral data from interviews with participants and laboratory data from blood and urine specimens provided</em>]. We will share de-identified individual-participant level (IPD) data. Appropriate measures such as&nbsp; _______ [<em>describe specific de-identification practices to be used</em>] will be used for data de-identification and sharing, and informed consent forms will reflect those plans.</span></p><p><strong>Sample answer from DMPTool: Minimal answer when most data will be shared openly</strong></p><p><span style="font-weight: 400;">In this proposed project, the cleaned, item-level spreadsheet data for all variables will be shared openly, along with example quantifications and transformations from initial raw data. Final files used to generate specific analyses to answer the Specific Aims and related results will also be shared. The rationale for sharing only cleaned data is to foster ease of data reuse.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8770, 713, 4467, 3535, '<p><span style="font-weight: 400;">Metadata, other relevant data, and associated documentation: Briefly list the metadata, other relevant data, and any associated documentation (e.g., study protocols and data collection instruments) that will be made accessible to facilitate interpretation of the scientific data.</span></p>', '', '<p><strong>NIH Guidance<br /></strong><span style="font-weight: 400;">In addition to the documentation examples, consider metadata that will provide additional information intended to make scientific data interpretable and reusable (e.g., date, independent sample and variable construction and description, methodology, data provenance, data transformations, any intermediate or descriptive observational variables).</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt basics</strong></p><p><span style="font-weight: 400;">To facilitate interpretation of the data, ______<em> </em>[<em>e.g., data dictionary, metadata, documentation, statistical analysis plans, bench protocols, data collection instruments</em>] will be created, shared, and associated with the relevant datasets.</span></p><p><strong>DMPTool fill-in-the-blank prompt addendum: Add relevant parts of the following if working with clinical trials</strong></p><p><span style="font-weight: 400;">In addition to ______ [<em>individual participant data (IPD) dataset being shared by restricted access and/or aggregate data being shared openly</em>], the researcher will share the ______ [<em>describe any other elements of the final data package not already addressed</em>]. Documentation and support materials will be compatible with the clinicaltrials.gov Protocol Registration Data Elements.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">To facilitate the interpretation and reuse of the data, a README file and data dictionary will be generated and deposited into a repository along with all shared datasets. The README file will include method description, instrument settings, RRIDs of resources such as antibodies, model organisms, cell lines, plasmids, and other tools (e.g., software, databases, services), and Protocol DOIs issued from protocols.io. The data dictionary will define and describe all variables in the dataset.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8771, 713, 4468, 3536, '<p><span style="font-weight: 400;">State whether specialized tools, software, and/or code are needed to access or manipulate shared scientific data, and if so, provide the name(s) of the needed tool(s) and software and specify how they can be accessed.</span></p><span id="page108R_mcid13" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 688.423px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.997943);" role="presentation"><br /></span></span>', '', '<strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">Tool(s) and software should be identified, then plans should specify how the tools can be accessed (e.g., open source and freely available, generally available for a fee in the marketplace, available only from the research team). When known, the longevity or period of time for which custom or proprietary tools will be available should be addressed.</span></p><p><span style="font-weight: 400;">In addition, file formats in which data are saved in a digital format can be divided into two general categories.</span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Proprietary - The specification of the data encoding format is not released or restricted in some way. Proprietary formats can only be easily opened and manipulated by particular software tools.&nbsp;</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Open - The specification of the data encoding format which can be used and implemented by anyone. Open formats can often be easily opened and manipulated by a large number of software tools.</span></li></ul>', '<p><strong>DMPTool fill-in-the-blank prompt: If no specialized tools are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type - Imaging data, survey data, etc.</em>] data will be made available in _____ [<em>csv, txt, dicom, etc</em>.] format and will not require the use of specialized tools to be accessed or manipulated.&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt: If specialized tools (open source or proprietary) are needed to access or manipulate the data:</strong></p><p><span style="font-weight: 400;">_____ [<em>Data type&nbsp; - Imaging data, survey data, etc.</em>] data will be made available in _____ format, which requires the use of specialized tools, such as _____ [<em>include list of tools</em>] to be accessed and manipulated.</span></p><p><span style="font-weight: 400;">These tools will be shared openly via ____. <br /><em>OR<br /></em> These tools are fee-based, proprietary software. Alternative access to the data will be provided by [<em>describe the strategy for other sites to see or work with the data - potential strategies include committing to provide links to file viewers, or exporting files to a nonproprietary format for limited use and reuse</em>].</span></p><p><strong>Sample answer from DMPtool: Animal studies with computational modeling</strong></p><p><span style="font-weight: 400;">The raw data generated via the confocal microscope is in the Carl Zeiss (.czi) file format. Zeiss software or Fiji ImageJ is required to access the raw data. The raw data generated via the Affymetrix Mouse Genome 430 2.0 Array is in the .CEL format. Statistical programs such as MATLAB or R can be used to analyze the raw data present in the CEL file.</span></p><p><span style="font-weight: 400;">Fiji ImageJ is open-source software that can be downloaded freely online. Links to this or other open-source viewers will be included with the documentation for the shared dataset. Matlab is available for purchase from Mathworks. R is a free software environment for statistical computing and graphics. RStudio is a free R development environment that runs on most operating systems. R Scripts produced through the course of the research will be made publicly available on the lab&rsquo;s GitHub repository, and will be provided as Supplementary files for any publications through a Zenodo-GitHub link. Code will be available no later than when a publication has been submitted. </span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8772, 713, 4469, 3537, 'State what common data standards will be applied to the scientific data and associated metadata to enable interoperability of datasets and resources, and provide the name(s) of the data standards that will be applied and describe how these data standards will be applied to the scientific data generated by the research proposed in this project. If applicable, indicate that no consensus standards exist', '', '<strong>NIH Guidance</strong><br />While many scientific fields have developed and adopted common data standards, others have not. In such cases, the Plan may indicate that no consensus data standards exist for the scientific data and metadata to be generated, preserved, and shared.<br /><br /><strong>Additional Guidance from DMPTool</strong><br /><p><span style="font-weight: 400;">A </span><em><span style="font-weight: 400;">standard</span></em><span style="font-weight: 400;"> specifies how exactly data and related materials should be stored, organized, and described. In the context of research data, the term typically refers to the use of specific and well-defined formats, schemas, vocabularies, and ontologies in the description and organization of data. However, for researchers within a community where more formal standards have not been well established, it can also be interpreted more broadly to refer to the adoption of the same (or similar) data management-related activities or strategies by different researchers and across different projects.</span></p><p><span style="font-weight: 400;">It is possible that your work will employ multiple formal standards or a mix of formal standards and other data management strategies. You should be as specific as possible when describing the standards used for each type of data included in your proposal.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Data will be stored in common and open formats, such as ____ for our ____ data. Information needed to make use of this data [<em>e.g., the meaning of variable names, codes, information about missing data, other metadata, etc.</em>] along with references to the sources of those standardized names and metadata items will be included wherever applicable.</span></p><p><strong>Addendum DMPTool fill-in-the-blank for if there <em>are</em> formal data standards for some/all of the data:&nbsp;</strong></p><p><span style="font-weight: 400;">Whenever possible, we will use ______ [<em>common data elements, standardized survey instruments, etc</em>.] to structure and organize our data.</span></p><p><span style="font-weight: 400;">Our ____ data will be structured and described using the ____ standard, which has been widely adopted in the ____ community. [<em>Add additional information about this standard, if applicable - e.g., implementation in data repositories, utility in combining/reusing datasets</em>]</span></p><p><strong>Addendum DMPTool fill-in-the-blank prompt for if there </strong><strong><em>are no</em></strong><strong> formal standards:&nbsp;</strong></p><p><span style="font-weight: 400;">Formal standards for ____ data have not yet been widely adopted. However, our data and other materials will be structured and described according to best practices which are as follows: [<em>list appropriate best practices</em>].</span></p><p><strong>Sample answer from DMPTool</strong></p><p><span style="font-weight: 400;">In accordance with FAIR Principles for data, we will use open file formats (e.g. JPEG, MP4, CSV, TXT, PDF, HTML, etc.) and persistent unique identifiers (PIDs) such as RRIDs for resources (e.g., organisms, plasmids, antibodies, cell lines, software tools, and databases) and DOIs for protocols using protocols.io. The bioimaging community has not yet agreed on a single standard data format that is generated by all acquisition systems, but we will use OME-Files for data that will be preserved and shared.&nbsp;</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8773, 713, 4470, 3538, '<strong><span id="page108R_mcid18" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.0786);" role="presentation">Repository where scientific data and</span> <span dir="ltr" style="left: 444.083px; top: 941.473px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.07312);" role="presentation">metadata will be archived: </span></span></strong><span id="page108R_mcid19" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996547);" role="presentation">Provide the name of the repository(ies</span><span dir="ltr" style="left: 430.083px; top: 962.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995392);" role="presentation">) where scientific data and metadata arising from the project will </span><span dir="ltr" style="left: 120.04px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996628);" role="presentation">be archived;</span> <span dir="ltr" style="left: 225.233px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999802);" role="presentation">see</span></span><a title=" Selecting a Data Repository" href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span id="page108R_mcid20" class="markedContent"> <span dir="ltr" style="left: 259.833px; top: 983.673px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995819);" role="presentation">Selecting a Data Repository</span></span></a><span id="page108R_mcid21" class="markedContent"><span dir="ltr" style="left: 487.083px; top: 983.673px; font-size: 18.4px; font-family: sans-serif;" role="presentation">)</span></span>', '', '<strong>NIH Guidance</strong><p><span style="font-weight: 400;">NIH has provided additional information to assist in selecting suitable repositories for scientific data resulting from funded research: </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016.</span></a></p><p><strong>Additional Guidance from DMPTool</strong></p><p><span style="font-weight: 400;">See </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-21-016.html"><span style="font-weight: 400;">NOT-OD-21-016</span></a><span style="font-weight: 400;"> and </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/sharing-scientific-data/selecting-a-data-repository"><span style="font-weight: 400;">other guidance on selecting a repository</span></a><span style="font-weight: 400;"> for details on repository considerations. In brief, first consideration (option 1) goes to whether the FOA or Institute specifies a repository, in which case that repository must be used. Next priority (Option 2A) goes to approved </span><a href="https://www.nlm.nih.gov/NIHbmic/domain_specific_repositories.html"><span style="font-weight: 400;">Open Domain-Specific Data Sharing Repositories</span></a><span style="font-weight: 400;">). If neither of those considerations fit, consider (Option 2B) other potentially suitable options: PubMed Central attachments, </span><a href="https://www.nlm.nih.gov/NIHbmic/generalist_repositories.html"><span style="font-weight: 400;">approved generalist repositories</span></a><span style="font-weight: 400;">, or your organization&rsquo;s institutional repository.</span></p><p><strong>NIH GDS Policy Considerations</strong></p><span style="font-weight: 400;">If your research is subject to GDS policy, please refer to recommended repositories on the &ldquo;</span><a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/where-to-submit-genomic-data"><span style="font-weight: 400;">Where to Submit Genomic Data</span></a><span style="font-weight: 400;">&rdquo; page on the NIH sharing site.</span>', '<p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ <em>[</em></span><span style="font-weight: 400;">A<em>dd appropriate NIH-supported data repositories</em>]</span><em>OR</em><span style="font-weight: 400;"> ________ </span><span style="font-weight: 400;">[<em>Add appropriate subject or disease repositories</em>]</span><strong><br /><em><br /></em></strong></p><p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">All dataset(s) that can be shared will be deposited in _________ [<em>Add appropriate NIH-supported data repositories</em>] OR ________ [<em>Add appropriate discipline- or data-specific repository, generalist repository, or your institutional data repository</em>]</span></p><p><strong>Sample answer from DMPTool: Minimal information, imaging study</strong></p><p><span style="font-weight: 400;">Imaging data will be deposited into NCI&rsquo;s Imaging Data Commons. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into Zenodo.</span></p><p><strong>Sample answer from DMPTool: Minimal information, clinical study</strong></p><p><span style="font-weight: 400;">Aggregate clinical trials data from all arms of the study will be available in clinicaltrials.gov, along with related metadata. All other data described above in the &ldquo;data to be shared&rdquo; section will be deposited into the National Addiction &amp; HIV Data Archive Program (NAHDAP) repository.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8774, 713, 4470, 3539, '<strong><span id="page108R_mcid22" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.09491);" role="presentation">How scientifi</span><span dir="ltr" style="left: 233.233px; top: 1046.87px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08073);" role="presentation">c data will be findable and identifiable: </span></span></strong><span id="page108R_mcid23" class="markedContent"><span dir="ltr" style="left: 120.04px; top: 1068.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99607);" role="presentation">Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier or </span><span dir="ltr" style="left: 120.04px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.99633);" role="presentation">other standard indexing tool</span><span dir="ltr" style="left: 346.283px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">s</span><span dir="ltr" style="left: 355.483px; top: 1089.07px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>NIH Guidance</strong></p><p><span style="font-weight: 400;">Unique Persistent Identifiers: The repository assigns datasets a citable, unique persistent identifier, such as a digital object identifier (DOI) or accession number, to support data discovery, reporting, and research assessment. The identifier points to a persistent landing page that remains accessible even if the dataset is de-accessioned or no longer available.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [insert whether DOI, handles, other], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available under a _______ [<em>Insert license information</em>]&nbsp;</span></p><p><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">The _________ [<em>repository name</em>] provides metadata, persistent identifiers [<em>insert whether DOI, handles, other</em>], and long-term access. This repository is supported by ________[<em>Insert funder/organization</em>] and dataset(s) are available&nbsp; through a request process __________ [<em>Insert information about request process</em>].</span></p><p><strong>Sample answer by DMPTool: Minimal information</strong></p><p><span style="font-weight: 400;">[Repository Name] provides searchable study-level metadata for dataset discovery. [Repository] assigns DOIs as persistent identifiers, and has a robust preservation plan to ensure long-term access. Data will be discoverable online through standard web search of the study-level metadata as well as the persistent pointer from the DOI to the dataset.</span></p><p><strong>Sample answer by DMPTool: Vivli clinical trials data repository</strong></p><p><span style="font-weight: 400;">Vivli provides access to data and documentation through study-level metadata specific to clinical trials description, long-term preservation and access, and Vivli-issued DOIs. In addition to DOIs, Vivli records are cross-searchable by the clinicaltrials.gov registration ID. Access request processes are described in detail with each data record.</span></p><p><strong>Sample answer by DMPTool: Expanded identifier discovery information</strong></p><p><span style="font-weight: 400;">We will use Persistent Unique Identifiers (PIDs) to improve data findability across all dissemination outputs. PIDs used will include ORCID iDs for people, DOIs for outputs (e.g., datasets, protocols), Research Resource IDentifiers (RRIDs) for resources, and Research Organization Registry (ROR) IDs and funder IDs for places, as much as possible to make data identifiable and findable. We will also use indexed metadata, such as MeSH terms with a unique URL to make scientific data easily findable. We will keep our ORCID Records up to date with DOIs for our datasets and publications, ROR, and funder IDs to increase findability.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8775, 713, 4470, 3540, '<strong>When and how long the scientific data will be made available: </strong>Describe when the scientific data will be made available to other users (i.e., no later than time of an associated publication or end of the performance period, whichever comes first) and for how long data will be available.', '', '<strong>NIH Guidance <br /></strong>NIH encourages scientific data be shared as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first. Researchers are encouraged to consider relevant requirements and expectations (e.g., data repository policies, award record retention requirements, journal policies) as guidance for the minimum time frame scientific data should be made available. NIH encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public. Identify any differences in timelines for different subsets of scientific data to be shared.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<strong><br /></strong><span style="font-weight: 400;"><br /><a title="&nbsp;Genomic data has further guidance on release expectations and timelines. " href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations">Genomic data has further guidance on release expectations and timelines. &nbsp;</a> <br /></span>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">Shared data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of _____[<em>duration</em>] years after the end of the funding period.</span></p><p><strong>Sample answer by DMPTool</strong></p><p><span style="font-weight: 400;">All scientific data generated from this project will be made available as soon as possible, and no later than the time of publication or the end of the funding period, whichever comes first. The duration of preservation and sharing of the data will be a minimum of 10 years after the funding period.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8776, 713, 4471, 3541, '<strong>Factors affecting subsequent access, distribution, or reuse of scientific data</strong>: NIH expects that in drafting Plans, researchers maximize the appropriate sharing of scientific data. Describe and justify any applicable factors or data use limitations affecting subsequent access, distribution, or reuse of scientific data related to informed consent, privacy and confidentiality protections, and any other considerations that may limit the extent of data sharing. See <a title="Frequently Asked Questions" href="https://sharing.nih.gov/faqs#/data-management-and-sharing-policy.htm">Frequently Asked Questions</a> for examples of justifiable reasons for limiting sharing of data.', '', '<strong><strong>NIH Guidance</strong></strong><p><a href="https://sharing.nih.gov/genomic-data-sharing-policy/about-genomic-data-sharing"><span style="font-weight: 400;">Genomic data may have further considerations</span></a><span style="font-weight: 400;"> to address.&nbsp; The NIH now expects a single data sharing plan at time of funding application satisfies both the Genomic Data Sharing (</span><a href="https://grants.nih.gov/grants/guide/notice-files/not-od-14-124.html"><span style="font-weight: 400;">GDS</span></a><span style="font-weight: 400;">) Policy and the DMS Policy (per </span><a href="https://grants.nih.gov/grants/guide/notice-files/NOT-OD-22-198.html"><span style="font-weight: 400;">NOT-OD-22-198</span></a><span style="font-weight: 400;">).</span></p><p><strong>Additional DMPTool&nbsp; Guidance&nbsp;</strong><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Some data may require extra preparation before they can be shared. This is the section to describe what legal, ethical, or technical issues will require limiting the sharing of your data. Examples may include existing legal limits such as data licenses or use agreements, issues of proprietary IP development, technical limits about the size or structure of the data, or ethical issues for human subjects privacy.&nbsp;</span></p><p><span style="font-weight: 400;">Key issues in justification of human subjects data specifically may be informed consent (e.g., disease-specific limitations, particular communities&rsquo; concerns) or privacy and confidentiality protections (i.e., de-identification, Certificates of Confidentiality, and other protective measures). Specific steps for human subjects data preparation can be addressed in the protections for privacy subquestion below.</span><span style="font-weight: 400;"><br /><br /></span></p><p><strong>NIH GDS Policy Considerations</strong></p><p><span style="font-weight: 400;">How to access genomic data varies depending on which repository you selected. Please refer to the </span><a href="https://sharing.nih.gov/accessing-data/accessing-genomic-data/accessing-genomic-data-from-nih-repositories"><span style="font-weight: 400;">Accessing Genomic Data from NIH Repositories page</span></a><span style="font-weight: 400;"> on the NIH sharing site.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt</strong></p><p><span style="font-weight: 400;">There are no anticipated factors or limitations that will affect the access, distribution or reuse of the scientific data generated by the proposal.&nbsp;<br /><br /></span><span style="font-weight: 400;">OR</span></p><p><span style="font-weight: 400;">Due to _______ [<em>ethical/legal/technical considerations</em>], access/distribution/reuse of the resulting scientific data will be limited and approved/monitored by ________ [<em>describe the approach to limiting access/distribution/reuse</em>].</span><span style="font-weight: 400;"><br /></span></p><p><strong>Sample answer by DMPTool for animal studies</strong></p><p><span style="font-weight: 400;">To address safety and security concerns related to capturing and distributing pictures or video of vertebrate research animals, access and distribution of behavioral video files generated in our lab during brain inactivation studies of non-human primates will be limited as described and justified in the IACUC protocol governing the project and in compliance with the "Image Recordings of Research Animals" Standard Operating Procedure at our institution. There are no other factors that will impact access, distribution, and reuse for all other scientific data generated by this study.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8777, 713, 4471, 3542, '<span id="page1R_mcid7" class="markedContent"><strong><span dir="ltr" style="left: 120.04px; top: 388.34px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(1.08402);" role="presentation">Whether access to scientific data will be controlled: </span></strong><span dir="ltr" style="left: 120.04px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.9977);" role="presentation">State whether</span> <span dir="ltr" style="left: 238.433px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.999898);" role="presentation">acc</span><span dir="ltr" style="left: 267.033px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995605);" role="presentation">ess to the scientific data</span> <span dir="ltr" style="left: 467.883px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.990262);" role="presentation">will</span> <span dir="ltr" style="left: 498.283px; top: 409.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.995661);" role="presentation">be controlled (i.e., made available by a data repository </span><span dir="ltr" style="left: 120.04px; top: 430.59px; font-size: 18.4px; font-family: sans-serif; transform: scaleX(0.996894);" role="presentation">only after approval)</span><span dir="ltr" style="left: 277.033px; top: 430.59px; font-size: 18.4px; font-family: sans-serif;" role="presentation">.</span></span>', '', '<p><strong>Additional DMPTool guidance</strong></p><p><span style="font-weight: 400;">Check the repository you intend to use to find out more about whether and how the repository supports controlled access.</span></p>', '<p><strong>Researchers who are not using controlled access repositories can skip this section or state:</strong></p><p><span style="font-weight: 400;">Controlled access will not be used. The data that is shared will be shared by unrestricted download.</span></p><p><strong>DMPTool fill-in-the-blank prompts for researchers selecting controlled access repositories&nbsp;</strong></p><p><span style="font-weight: 400;">Given the sensitive nature of the dataset, data will be made available in ________ data repository, which restricts access to the data to ______ [describe restriction, e.g. to qualified investigators with an appropriate research question and approved data use agreement (DUA)]. Data can be accessed by _____ [describe data repository access methods and measures].&nbsp;&nbsp;</span></p><p><strong>Sample answer from DMPTool for clinical trials data sharing in the Vivli repository</strong></p><p><span style="font-weight: 400;">Data will be available by controlled access only. To access data arising from this project, users must complete the Vivli data request form and sign the Vivli Data Use Agreement (DUA), which limits subsequent use to the terms of the approved request and requires that users maintain data security, and refrain from any attempts to re-identify research participants or engage in any unauthorized uses of the data. To get access to the data, the user must submit a valid scientific question, include a statistical analysis plan, and complete all required fields on the Vivli data request form. Vivli will review the data request for completeness.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8778, 713, 4471, 3543, '<strong>Protections for privacy, rights, and confidentiality of human research participants:</strong><br />If generating scientific data derived from humans, describe how the privacy, rights, and confidentiality of human research participants will be protected (e.g., through de-identification, Certificates of Confidentiality, and other protective measures).', '', '<p><strong>Additional DMPTool Guidance</strong></p><p><span style="font-weight: 400;">Certain kinds of data, especially human subjects data, require extra preparation before they can be shared to ensure participant privacy. In this section, you will describe your approach to preparing human subjects data for sharing and note any additional restrictions or policies that will impact access to your data. If you are working with human subjects you should also describe how you will address data management and sharing in your informed consent process. You will also need to describe your methods for ensuring privacy and confidentiality, including how you will de-identify your data. If you have decided that a controlled access repository (where researchers must apply to access data) is a better fit for your data than an open repository, you should describe the repositorys access procedures. Finally, if there are any other laws, policies, or existing agreements that impact your ability to share your data, they should be described here.<br /></span><span style="font-weight: 400;"><br /></span><span style="font-weight: 400;">Issues to consider:</span><span style="font-weight: 400;"><br /></span></p><ul><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any restrictions imposed by federal, Tribal, or state laws, regulations, or policies, or existing or anticipated agreements (e.g., with third-party funders, with partners, with Health Insurance Portability and Accountability Act (HIPAA) covered entities that provide Protected Health Information under a data use agreement, through licensing limitations attached to materials needed to conduct the research).</span></li><li style="font-weight: 400;" aria-level="1"><span style="font-weight: 400;">Any other considerations that may limit the extent of data sharing.</span></li></ul>', '<p><span style="font-weight: 400;">This subsection applies to studies involving human research participants. Other studies can generally skip question 5.3&nbsp;</span></p><p><strong>DMPTool fill-in-the-blank prompt for human subjects data</strong></p><p><span style="font-weight: 400;">In order to ensure participant consent for data sharing, IRB paperwork and informed consent documents will include language describing plans for data management and sharing of data, describing the motivation for sharing, and explaining that personal identifying information will be removed.</span></p><p><span style="font-weight: 400;">To protect participant privacy and confidentiality, shared data will be de-identified using the ______ methods [<em>describe de-identification method, noting any other applicable laws or policies such as HIPAA</em>].</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (8779, 713, 4472, 3544, 'Describe how compliance with this Plan will be monitored and managed, frequency of oversight, and by whom at your institution (e.g., titles, roles).', '', '<div><strong>NIH</strong></div><div>This element refers to oversight by the funded institution, rather than by NIH. The DMS Policy does not create any expectations about who will be responsible for Plan oversight at the institution.<br /><strong><br />Additional DMPTool Guidance</strong><span style="font-weight: 400;">:</span></div><p><span style="font-weight: 400;">Describe how and by whom compliance with this Plan will be managed.&nbsp;If oversight and roles will include the addition of study personnel for oversight of data management and sharing, </span><a href="https://sharing.nih.gov/data-management-and-sharing-policy/planning-and-budgeting-for-data-management-and-sharing/budgeting-for-data-management-sharing#requesting-&amp;-justifying-costs-for-data-management-and-sharing"><span style="font-weight: 400;">describe reasonable, allowable personnel costs in the budget justification</span></a><span style="font-weight: 400;"> rather than the DMS Plan.</span></p>', '<p><strong>DMPTool fill-in-the-blank prompt&nbsp;</strong></p><p><span style="font-weight: 400;">Lead PI ____[<em>name</em>]___, ORCID: __[<em>ORCID ID</em>]___, will be responsible for the day-to-day oversight of lab/team data management activities and data sharing. Broader issues of DMS Plan compliance oversight and reporting will be handled by the PI and Co-I team as part of general [campus(es)] stewardship, reporting, and compliance processes.</span></p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
