INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9223, 748, 4676, 2700, '<p>Are there any limitations or restrictions on the sharing and/or secondary use of the collected data? Restrictions may be based on informed consent under which the data were collected or specific legal, regulatory, or policy requirements.</p><p>If yes, provide justification, including a description of the data use limitations and the institutional entity or other entity that approved the limitations in the additional comments area below.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 3, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
