INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9230, 748, 4678, 2707, '<p>&nbsp;&nbsp;</p><table style="border-collapse: collapse; width: 100%; height: 90px;" border="1"><colgroup><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"><col style="width: 12.4538%;"></colgroup><tbody><tr style="height: 72px;"><td style="height: 72px;"><strong>Data Type</strong></td><td style="height: 72px;"><strong>Brief Description</strong></td><td style="height: 72px;"><strong>Organism, Model, or Other Sources</strong></td><td style="height: 72px;"><strong>Amount of Data</strong></td><td style="height: 72px;"><strong>Standards</strong></td><td style="height: 72px;"><strong>Shared Formats</strong></td><td style="height: 72px;"><strong>Data Repository</strong></td><td style="height: 72px;"><strong>Data Access Type</strong></td></tr><tr style="height: 18px;"><td style="height: 18px;"><em>Define each data type add additional rows to describe multiple data types</em></td><td style="height: 18px;"><em>Summarize how the data of this type will be managed and prepared for sharing</em></td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;"><em>Projected number of participants or samples from which the data will be generated or other appropriate metrics to describe the scale of the data</em></td><td style="height: 18px;"><em>List the standards that will be applied to the scientific data and associated metadata if standards exist</em></td><td style="height: 18px;"><em>Formats of data to be submitted to the data repository</em></td><td style="height: 18px;"><em>Name the repository where scientific data and metadata will be preserved and shared</em></td><td style="height: 18px;"><em>Examples include open, registered, controlled or enclave&nbsp;</em></td></tr></tbody></table>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9231, 748, 4679, 2708, '<p>Use this section to plan for data submission to and sharing from the repositor(ies) listed in the data type section. Consider publication timelines, performance period, and data repository review and release timelines when planning data submissions and communicate through Plan updates if there are major changes to planned timelines. Shared scientific data should be made accessible as soon as possible, and no later than time of an associated publication or end of the performance period, whichever comes first.</p><p>&nbsp;</p><table style="border-collapse: collapse; width: 100%; height: 36px;" border="1"><colgroup><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.3045%;"><col style="width: 14.1732%;"></colgroup><tbody><tr style="height: 18px;"><td style="height: 18px;"><strong>Data Repository</strong></td><td style="height: 18px;"><strong>Expected number and frequency of submissions</strong><br><strong>&nbsp;</strong></td><td style="height: 18px;"><strong>Projected timeline for first submission to repository</strong></td><td style="height: 18px;"><strong>Projected timeline for last submission to repository</strong></td><td style="height: 18px;"><strong>Target timelines for release</strong></td><td style="height: 18px;"><strong>Type of persistent IDs that will be used for data releases, to enable findability and citation of shared datasets</strong></td><td style="height: 18px;"><strong>If you will be contributing data to a dataset that is already registered with a data repository, provide that ID</strong></td></tr><tr style="height: 18px;"><td style="height: 18px;"><em>Name the data repository described in the Data Type section. Add additional rows as necessary.</em></td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;">&nbsp;</td><td style="height: 18px;"><em>Releases associated with data underlying publications, other scheduled releases, and remaining scientific data by the end of the performance period.&nbsp;</em></td><td style="height: 18px;"><em>Samples include dataset-level digital object identifier (DOI), accession number, globally unique identifier.</em></td><td style="height: 18px;">&nbsp;</td></tr></tbody></table>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9232, 748, 4680, 2709, '<p>Briefly describe the tools, software, and/or code.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
