INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9343, 754, 4699, 3066, '<p>Has an <a href="https://sharing.nih.gov/genomic-data-sharing-policy/institutional-certifications/completing-an-institutional-certification-form" target="_blank" rel="noopener">Institutional Certification (IC)</a> been submitted with the application or Just-In-Time that meets GDS criteria?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 4, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9344, 754, 4699, 3067, '<p><strong>Element 1: Data Type &nbsp;&nbsp;</strong></p><p>Will all scientific data generated by the research project be shared in a data repository that makes data available to the larger research community? &nbsp;If No, explain the rationale that determines which scientific data will not be shared in the comment area below. &nbsp; &nbsp; &nbsp;&nbsp;</p>', '', '<p>NIH guidance: Scientific Data is defined as data commonly accepted in the scientific community as of sufficient quality to validate and replicate research findings, regardless of whether the data are used to support scholarly publications.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 5, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9345, 754, 4699, 3068, '<p><strong>Element 2: Tools, Software, Code</strong></p><p>Describe the tools, software, and/or code that are needed to access or manipulate shared scientific data to support replication or reuse, if any.</p>', '', '<p>NIH Guidance: Indicate names of the specialized tools needed to access or manipulate each shared respective data type, if any.&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 6, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9346, 754, 4699, 3069, '<p><strong>Element 2: Tools, Software, Code&nbsp;</strong></p><p>Describe how researchers can access the tools, software, and/or code listed above. Describe if &ldquo;Other.&rdquo;</p>', '', '<p>Specify how needed tools can be accessed.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Open source", "value": "Open source", "selected": 0}, {"label": "Available for a fee", "value": "Available for a fee", "selected": 0}, {"label": "Restricted availability from a specific source ", "value": "Restricted availability from a specific source ", "selected": 0}, {"label": "Other", "value": "Other", "selected": 0}], "attributes": {"multiple": 0}}', 0, 7, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9347, 754, 4699, 3070, '<p><strong>Element 3: Standards</strong></p><p>List data or metadata standards or common data elements that will be used applicable to each data type shared. Write N/A if no existing standards.</p>', '', '<p>NIH Guidance: Data standards refer to community-accepted methods of organizing, documenting, and formatting data to aid in data aggregation, sharing, and reuse.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 8, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9348, 754, 4699, 3071, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>Explain how data sharing timelines will meet expectations of the DMS or other applicable policies.</p>', '', '<p>NIH Guidance: NIH encourages scientific data to be shared as soon as possible, and no later than the time of an associated publication or end of the performance period, whichever comes first. NIH also encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 9, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9349, 754, 4699, 3072, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>What types of persistent identifiers/ indexing methods will be used for data releases, to enable findability and citation of shared datasets?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 10, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9350, 754, 4699, 3073, '<p><strong>Element 5: Access, Distribution or Reuse Considerations</strong></p><p>Describe any limitations or factors affecting subsequent access, distribution, or reuse of this data.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 11, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
