INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (9353, 754, 4699, 3076, '<p><strong>Element 6: Compliance</strong></p><p>Describe how compliance with the Plan will be monitored and managed, frequency of oversight, and by whom.</p>', '', '<p>NIH Guidance: Indicate the data management and sharing lead name and contact information; the frequency and methods that your institution will provide oversight and by whom (e.g., titles, roles and responsibilities).&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 14, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
