INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10321, 825, 5334, 3067, '<p><strong>Element 1: Data Type &nbsp;&nbsp;</strong></p><p><span style="color: rgb(224, 62, 45); font-size: 14pt;">Describe DMS Plan Elements 1-6 in the section below with free text. To maximize the use of structured information in writing a Plan (e.g., data types, repositories) with a drop-down menu option, please proceed to the adjacent tab labeled &ldquo;Research Outputs."</span></p><p>Will all scientific data generated by the research project be shared in a data repository that makes data available to the larger research community? &nbsp;If No, explain the rationale that determines which scientific data will not be shared in the comment area below. &nbsp; &nbsp; &nbsp;&nbsp;</p>', '', '<p>NIH guidance: Scientific Data is defined as data commonly accepted in the scientific community as of sufficient quality to validate and replicate research findings, regardless of whether the data are used to support scholarly publications.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 5, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
