INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10324, 825, 5334, 3070, '<p><strong>Element 3: Standards</strong></p><p>List data or metadata standards or common data elements that will be used applicable to each data type shared. Write N/A if no existing standards.</p>', '', '<p>NIH Guidance: Data standards refer to community-accepted methods of organizing, documenting, and formatting data to aid in data aggregation, sharing, and reuse.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 8, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10325, 825, 5334, 3071, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>Explain how data sharing timelines will meet expectations of the DMS or other applicable policies.</p>', '', '<p>NIH Guidance: NIH encourages scientific data to be shared as soon as possible, and no later than the time of an associated publication or end of the performance period, whichever comes first. NIH also encourages researchers to make scientific data available for as long as they anticipate it being useful for the larger research community, institutions, and/or the broader public.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 9, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10326, 825, 5334, 3072, '<p><strong>Element 4: Data Preservation, Access, and Timelines</strong></p><p>What types of persistent identifiers/ indexing methods will be used for data releases, to enable findability and citation of shared datasets?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 10, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10327, 825, 5334, 3073, '<p><strong>Element 5: Access, Distribution or Reuse Considerations</strong></p><p>Describe any limitations or factors affecting subsequent access, distribution, or reuse of this data.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 11, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
