INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10329, 825, 5334, 3075, '<p><strong>Element 5: Access, Distribution or Reuse Considerations</strong></p><p>What type of access will secondary users utilize to access the shared data? Describe if &ldquo;Other.&rdquo;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Open access", "value": "Open access", "selected": 0}, {"label": "Managed access", "value": "Managed access", "selected": 0}, {"label": "Controlled access", "value": "Controlled access", "selected": 0}, {"label": "Data Enclave", "value": "Data Enclave", "selected": 0}, {"label": "Other", "value": "Other", "selected": 0}], "attributes": {"multiple": 0}}', 0, 13, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
