INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10333, 825, 5335, 3079, '<p>If additional policies apply (e.g., Clinical Trials Access Policy, FOA-specific requirements), describe additional information required to meet the policy.</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
