INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10549, 845, 5438, 3064, '<p>Will the datasets be shared according to <a href="https://sharing.nih.gov/genomic-data-sharing-policy/submitting-genomic-data/data-submission-and-release-expectations#genomic-data-submission-and-release-expectations">GDS policy</a> but no later than the time of publication or end of the project, whichever is sooner?</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "selectBox", "options": [{"label": "Yes", "value": "Yes", "selected": 0}, {"label": "No", "value": "No", "selected": 0}], "attributes": {"multiple": 0}}', 0, 2, @default_funder1_id, CURDATE(), @default_funder1_id, CURDATE());
