INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1097, 156, 740, 1506, '<p>Describe whether and how data will be archived and how preservation of access will be handled. For example, will hardcopy logs, instrument outputs, and physical samples be stored in a location where there are safeguards against fire or water damage? Is there a plan to transfer digitized information to new storage media or devices as technological standards or practices change? Will there be an easily accessible index that documents where all archived data are stored and how they can be accessed? If the data will be archived by a third party, please refer to their preservation plans (if available).</p>', '', '<p><a href="https://www.nsf.gov/geo/geo-data-policies/ags/ags_data_mgt_guidance-v3.docx">AGS Advice to PIs on Data Management Plans&nbsp;</a>(MS-Word)</pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF Geosciences (GEO) Home, Atmospheric &amp; Geospace Sciences. https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jspov/publications/pub_summ.jsp?ods_key=nsf17060</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1098, 157, 741, 1371, '<p>Describe your policies regarding the use of data provided via general access or sharing. For example, if you plan to provide data and images on your website, will the website contain disclaimers, or conditions regarding the use of the data in other publications or products? If the data or products (e.g., images) are copyrighted (by a journal, for example), how will this be noted on the website?</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">Directorate of Mathematical and Physical Sciences<br />Division of Astronomical Sciences (AST)<br />Advice to PIs on Data Management Plans, 2011 NSF AST division guidance (PDF)</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm">Mid-Scale Innovations Program in Astronomical Sciences (MSIP) Program Solicitation NSF 17-592. https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1099, 157, 742, 1372, '<p>Describe whether and how data will be archived and how preservation of access will be handled. If the data will be archived by a third party (e.g., national observatory or journal), please refer to their preservation plans if available.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">Directorate of Mathematical and Physical Sciences<br />Division of Astronomical Sciences (AST)<br />Advice to PIs on Data Management Plans, 2011 NSF AST division guidance (PDF)</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm">Mid-Scale Innovations Program in Astronomical Sciences (MSIP) Program Solicitation NSF 17-592. https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1101, 158, 744, 573, '<p>Specify how data or products are to be stored, preserved, and shared.</p>', '', '<p>The DMP should describe dissemination approaches that will be used to make data and metadata avilable to others. As appropriate, the DMP should describe physical and cyber resources and facilities that will be used for the effective preservation and storage of research data.&nbsp; These can include third party facilities and repositories.&nbsp; Anticipated costs for data or material management should be list in the proposal Budget and explained in the Budget Justification (and not in the DMP). Research centers and major partnerships with industry or other use communities must also address how data are to be shared and managed with partners, center members, and other major stakeholders.</pNSFsp>Data storage and preservation:</pNSFsp>The DMP should describe physical and cyber resources and facilities that will be used to effectively preserve and store research data. These can include third-party facilities and repositories.</pNSFsp><br />Consider the following:</pNSFsulNSFsli>What is the long-term strategy for maintaining, curating, and archiving the data?</liNSFsli>Which archive/repository/database have you identified as a place to deposit data?</liNSFsli>What procedures does your intended long-term data storage facility have in place for preservation and backup?</liNSFsli>How long will/should data be kept beyond the life of the project?</liNSFsli>What data will be preserved for the long-term?</liNSFsli>On what basis will data be selected for long-term preservation?</liNSFsli>What metadata/documentation will be submitted alongside the data or created on deposit/transformation in order to make the data reusable?</liNSFsli>Outline the staff/organizational roles and responsibilities for implementing this data management plan.<a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management FAQ</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF - Dissemination of results</a><br /><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Grant Proposal Guide on DMPs</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">NSF EHR directorate guidance (PDF)</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Guidance</a><br /><a href="http://www.icpsr.umich.edu/icpsrweb/content/datamanagement/dmp/index.html">ICPSR Guidelines for Effective DMPs</a><br /><a href="http://www.re3data.org/">re3data: Registry of data repositories</a><br /><a href="http://www.dataone.org/best-practices/identify-suitable-repositories-data">DataONE Best Practice: Identify Suitable Repositories for your Data</a><br /><a href="http://www.dataone.org/best-practices/identify-data-long-term-value">DataONE Best Practice: Identify Data with Long-Term Value</aNSFsp>&nbsp;</pNSFs/liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1100, 158, 743, 576, '<p>Specify how long access to data and products, and sharing of data or products, will be maintained after the life of the project, and how any associated costs will be covered and by whom.</p>', '', '<p>The data management plan should clearly state how and when data and other materials will be released, and how long they will be stored or archived.</pNSFsp>Data retention:<br />Describe how long you plan to retain the data produced or used in your research. If you plan to embargo the data for a period of time after the research is completed, describe why this is necessary. Consider the following:</pNSFsulNSFsli>How long will the original data collector/creator/principal investigator retain the right to use the data before opening it up to wider use?</liNSFsli>Explain details of any embargo periods for political/commercial/patent or publisher reasons.</liNSFs/ulNSFsp><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management FAQ</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF - Dissemination of results</a><br /><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Grant Proposal Guide on DMPs</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">NSF EHR directorate guidance (PDF)</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Guidance</a><br /><a href="http://www.icpsr.umich.edu/icpsrweb/content/datamanagement/dmp/index.html">ICPSR Guidelines for Effective DMPs</a><br /><a href="http://www.dataone.org/best-practices/sharing-data-legal-and-policy-considerations">DataONE Best Practice: Sharing Data: Legal and Policy Considerations</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1102, 158, 745, 578, '<p>More stringent data management requirements may be specified in particular NSF solicitations or result from local policies and best practices at the PI&rsquo;s home institution. Additional requirements will be specified in the program solicitation and award conditions. Principal Investigators to be supported by such programs must discuss how they will meet these additional requirements in their Data Management Plans.</p>', '', '<p>Additional data management requirements:</pNSFsp>Describe here any additional program-specific data management requirements. If none exist you may leave this section blank.</pNSFsp>&nbsp;</pNSFsp>Please note the <a style="color: #ff4500;" href="http://grants.nih.gov/reproducibility/index.htm" target="blank"><strong>Rigor and Reproducibility</strong></a> requirements that involve updates to grant application instructions and review criteria (but not Data Sharing Plans).</pNSFsp><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management FAQ</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF - Dissemination of results</a><br /><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Grant Proposal Guide on DMPs</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">NSF EHR directorate guidance (PDF)</a><br /><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Guidance</a><br /><a href="http://www.icpsr.umich.edu/icpsrweb/content/datamanagement/dmp/index.html">ICPSR Guidelines for Effective DMPs</a><br /><a href="http://www.dataone.org/best-practices/revisit-data-management-plan-throughout-project-life-cycle">DataONE Best Practice: Revisit Data Management Plan Throughout the Project Life Cycle</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1103, 159, 746, 2153, '<p>Describe the types of data, physical samples or collections, software, curriculum materials, and other materials to be produced in the course of the project. (For collaborative proposals, the DMP must cover all the various data types being collected by each collaborator.)</p>', '', '<p>Data Collected BIO 2015:<br />Describe the data to be collected (actual observations) during your research including amount (if known). Name the type of data, the instrument or collection approach, and how the data will be sampled. If actual data are interpreted, note the interpretation. Describe any quality control measures. Also describe the final derivative products (datasets and software or computer code) and the analysis used including analytical software packages that are required for replication, etc. Describe data (both digital and analog) and physical materials (samples and collections) gathered or generated during the time of the award.</pNSFsp><em>Consider these questions</em>:</pNSFsp>What data will be generated in the research?</pNSFsp>What data types will you be creating or capturing? (e.g. experimental measures, qualitative, raw, processed)</pNSFsp>How will you capture or create the data? (This should cover content selection, instrumentation, technologies and approaches chosen, met</pNSFsp><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a></pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a></pNSFsp><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a></pNSFsp><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a></pNSFsp><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1104, 159, 747, 2154, '<p>Describe the standards to be used for all the data types anticipated, including data or file format and metadata.</p>', '', '<p>Standards Formats:<br />Describe the format of your data; think about what details (metadata) someone else would need to be able to use these files. Describe the structural standards that you will apply in making data and metadata available. For example, for most ecological data, documentation should be structured in Ecological Metadata Language (EML). An example of metadata could also be as simple as a "readme file" to explain variables, structure of the files, etc. <br /><em>Consider these questions</em>:</pNSFsp>&nbsp;</pNSFsulNSFsulNSFsli>Which file formats will you use for your data and why?</liNSFs/ulNSFs/ulNSFsp>&nbsp;</pNSFsulNSFsulNSFsli>What form will the metadata describing/documenting your data take?</liNSFs/ulNSFs/ulNSFsp>&nbsp;</pNSFsulNSFsulNSFsli>How will you create or capture these details?</liNSFs/ulNSFs/ulNSFsp>&nbsp;</pNSFsulNSFsulNSFsli>Which metadata standards will you use and why have you chosen them? (e.g. accepted domain-local standards, widespread usage)</liNSFs/ulNSFs/ulNSFsp>&nbsp;</pNSFsulNSFsli>What contextual details (metadata) are needed to make the data you capture or collect meaningful?</liNSFs/ulNSFsp><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a><br /><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a><br /><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a><br /><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">Guidance</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1105, 159, 748, 2155, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data (including contingency plans for the departure of key personnel from the project).</p>', '', '<p>Roles and responsibilities:<br></p><p>Explain how the responsibilities regarding the management of your data will be delegated.  This should include time allocations, project management of technical aspects, training requirements, and contributions of non-project staff - individuals should be named where possible.  Remember that those responsible for long-term decisions about your data will likely be the custodians of the repository/archive you choose to store your data.  While the costs associated with your research (and the results of your research) must be specified in the Budget Justification portion of the proposal, you may want to reiterate who will be responsible for funding the management of your data. </p><br>Consider the following:<ulNSFsli>Outline the staff/organizational roles and responsibilities for implementing this data management plan.</liNSFsli>Who will be responsible for data management and for monitoring the data management plan?</liNSFsli>How will adherence to this data management plan be checked<br><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a><br><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a><br><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a><br><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a><br><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">Guidance</a><p></pNSFs/liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1106, 159, 749, 2156, '<p>Describe the dissemination methods that will be used to make data and metadata available to others during the period of the award, and any modifications or additional technical information regarding data access after the grant ends.</p>', '', '<p>Dissemination methods:<br />Describe how and where you will make these data and metadata available to the community. Remember BIO is committed to timely and rapid data distribution; make sure you address how soon your data will be available. Indicate what data will be made available and preserved. Will data be accessible on a web page, by email request, via open-access repository, etc.? <em>Consider these questions</em>:</pNSFsulNSFsli>What data will be made available from the study and preserved for the long-term?</liNSFsli>How and when will you make the data available? (Include resources needed to make the data available: equipment, systems, expertise, etc.)</liNSFsli>What transformations will be necessary to prepare data for preservation / data sharing?</liNSFsli>What metadata/ documentation will be submitted alongside the data or created on deposit/ transformation in order to make the data reusable?</liNSFsli>What related information will be deposited?</liNSFsli>What is the process for gaining access to the data?</liNSFsli>H<br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a><br /><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a><br /><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a><br /><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">Guidance</aNSFsp>&nbsp;</pNSFs/liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1107, 159, 750, 2157, '<p>Describe the PI&rsquo;s policies for data sharing, public access and re-use, including re-distribution by others and the production of derivatives. Where appropriate, include provisions for protection of privacy, confidentiality, security, intellectual property rights and other rights.</p>', '', '<p>Policies for sharing:<br />Describe the policies under which these data will be made available. It is very important, the reason a DMP is required, that you specify how you will share your data with non-group members after the project is completed. If the data is of a sensitive nature&mdash;privacy or ecological endangerment concerns, for instance&mdash;and public access is inappropriate, address here the means by which granular control and access will be achieved (e.g. formal consent agreements, anonymized data, only available within a secure network, etc.). <em>Consider these questions</em>:</pNSFsulNSFsulNSFsli>Will any permission restrictions need to be placed on the data?</liNSFsli>Are there ethical and privacy issues? If so, how will these be resolved?</liNSFsli>What have you done to comply with your obligations in your IRB Protocol?</liNSFsli>Who will hold the intellectual property rights to the data and how might this affect data access?</liNSFsli>What and who are the intended or foreseeable uses/users of the data?</li>&lt;li</ulNSFs/ulNSFsp><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a><br /><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a><br /><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a><br /><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">Guidance</a></pNSFsp>&nbsp;</pNSFsulNSFsul>&lt;/li</ulNSFs/ulNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1108, 159, 751, 2158, '<p>Where relevant, describe plans for archiving data, samples, software, and other research products, and for on-going access to these products through their lifecycle of usefulness to research and education.</p>', '', '<p>Archiving Storage Preservation:<br />Consider which data (or research products) will be deposited for long-term access and where. (What physical and/or cyber resources and facilities (including third party resources) will be used to store and preserve the data after the grant ends?)<br />Describe your long-term strategy for storing, archiving and preserving the data you will generate or use. Consider the following:</pNSFsulNSFsli>What is the long-term strategy for maintaining, curating and archiving the data?</liNSFsli>Which archive/repository/database have you identified as a place to deposit data?</liNSFsli>What procedures does your intended long-term data storage facility have in place for preservation and backup?</liNSFsli>How long will/should data be kept beyond the life of the project?</liNSFsli>What data will be preserved for the long-term?</liNSFsli>On what basis will data be selected for long-term preservation?</liNSFsli>What metadata/documentation will be submitted alongside the data or created on deposit/transf<br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">NSF BIO Guidance</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Public Access: Frequently Asked Questions</a><br /><a href="http://www.esa.org/esa/science/data-sharing/resources-and-tools/">ESA Data Sharing Resources</a><br /><a href="https://www.dataone.org/best-practices">DataONE Best Practices</a><br /><a href="http://www.usgs.gov/datamanagement/index.php">USGS Data Management Best Practices</a><br /><a href="http://www.nsf.gov/bio/pubs/BIODMP_Guidance.pdf">Guidance</a><br /><a href="http://www.re3data.org/">re3data: Registry of data repositories</aNSFsp>&nbsp;</pNSFs/liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1109, 160, 752, 2975, '<p>The Division of Earth Sciences requires that full data sets, derived data products (e.g. model results, output, and workflows), software, and physical collections must be made publicly accessible within two (2) years of final collection.</p>', '', '<p><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">Data and Sample Policy, Division of Earth Sciences, NSF https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf</a></pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017-appendix.xlsx">EAR Division Data Sharing Policy Appendix</a>&nbsp;(September 2017) (MS-Excel)</pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp">NSF Geosciences (GEO) Home. https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jspov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1110, 160, 753, 2976, '<p>Standards to be used for data and metadata format and content (where existing standards are absent or deemed inadequate, this should be documented along with any proposed&nbsp;solutions or remedies).</p>', '', '<p><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">Data and Sample Policy, Division of Earth Sciences, NSF https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf</a></pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017-appendix.xlsx">EAR Division Data Sharing Policy Appendix</a>&nbsp;(September 2017) (MS-Excel)</pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp">NSF Geosciences (GEO) Home. https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jspov/publications/pub_summ.jsp?ods_key=nsf17060</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1111, 160, 754, 2977, '<p>It is the responsibility of researchers and organizations to make results, data, derived data products, and collections available to the research community in a timely manner and at a reasonable cost. In the interest of full and open access, data should be provided at the lowest possible cost to researchers and educators. This cost should, as a first principle, be no more than the marginal cost of filling a specific user request. Data may be made available for secondary use through submission to a national data center, publication in a widely available scientific journal, book or website, through the institutional archives that are standard for a particular discipline (e.g. IRIS for seismological data, UNAVCO for GP data), or through other EAR-specified repositories. Data inventories should be published or entered into a public database periodically and when there is a significant change in type, location or frequency of such observations. Principal Investigators working in coordinated programs may establish (in consultation with other funding agencies and NSF) more stringent data submission procedures.</p>', '', '<p><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">Data and Sample Policy, Division of Earth Sciences, NSF https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf</a></pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017-appendix.xlsx">EAR Division Data Sharing Policy Appendix</a>&nbsp;(September 2017) (MS-Excel)</pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp">NSF Geosciences (GEO) Home. https://www.nsf.gov/geo/geo-data-policies/ear/index.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jspov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp><br /><br /></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
