INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1128, 163, 772, 1415, '<p>The Data Management Plan should address the roles and responsibilities of all parties with respect to the management of the data (including contingency plans for the departure of key personnel from the project) after the grant ends.</p>', '', '<p><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards. https://www.nsf.gov/cise/cise_dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/dir/index.jsp?org=CISE">Computer &amp; Information Science &amp; Engineering Home: https://www.nsf.gov/dir/index.jsp?org=CISE</a></pNSFsp><a href="https://www.nsf.gov/funding/pgm_list.jsp?org=cise">Computer &amp; Information Science &amp; Engineering (CISE) Active Funding Opportunities. https://www.nsf.gov/funding/pgm_list.jsp?org=cise</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1132, 158, 771, 575, '<p>Specify what data formats will be used (e.g., XML files, websites, image files, data tables, software code, text documents, physical materials).</p>', '', '<p>The DMP should describe data formats and media approaches that will be used to make data and metadata available to others.</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
