INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1134, 164, 777, 3358, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF DMR division guidance on DMPs January 2011(PDF)</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=DMR">NSF Mathematical &amp; Physical Sciences, Division of Materials Research (DMR)&nbsp;</a><a href="https://www.nsf.gov/div/index.jsp?div=DMR">https://www.nsf.gov/div/index.jsp?div=DMR</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1135, 164, 778, 3359, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<p>&nbsp;</pNSFsp><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF DMR division guidance on DMPs January 2011(PDF)</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=DMR">NSF Mathematical &amp; Physical Sciences, Division of Materials Research (DMR)&nbsp;</a><a href="https://www.nsf.gov/div/index.jsp?div=DMR">https://www.nsf.gov/div/index.jsp?div=DMR</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1136, 164, 779, 3360, '<p>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF DMR division guidance on DMPs January 2011(PDF)</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=DMR">NSF Mathematical &amp; Physical Sciences, Division of Materials Research (DMR)&nbsp;</a><a href="https://www.nsf.gov/div/index.jsp?div=DMR">https://www.nsf.gov/div/index.jsp?div=DMR</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1137, 164, 780, 3361, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF DMR division guidance on DMPs January 2011(PDF)</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=DMR">NSF Mathematical &amp; Physical Sciences, Division of Materials Research (DMR)&nbsp;</a><a href="https://www.nsf.gov/div/index.jsp?div=DMR">https://www.nsf.gov/div/index.jsp?div=DMR</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1138, 164, 781, 3362, '<p>NSF program management will implement these policies for dissemination and sharing of research results, in ways appropriate to field and circumstances, through the proposal review process; through award negotiations and conditions; and through appropriate support and incentives for data cleanup, documentation, dissemination, storage and the like.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF DMR division guidance on DMPs January 2011(PDF)</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=DMR">NSF Mathematical &amp; Physical Sciences, Division of Materials Research (DMR)&nbsp;</a><a href="https://www.nsf.gov/div/index.jsp?div=DMR">https://www.nsf.gov/div/index.jsp?div=DMR</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1139, 157, 782, 1370, '<p>"Access to data" refers to data made accessible without explicit request from the interested party, for example those posted on a website or made available to a public database. Describe your plans, if any, for providing such general access to data, including websites maintained by your research group, and direct contributions to public databases. If maintenance of a web site or data base is the direct responsibility of your group, provide information about the period of time the web site or data base is expected to be maintained. Note that data taken at national or private observatories may be accessible through public archives (perhaps after a standard proprietary period). Various forms of data (e.g.FITS image and tables, other data tables) also may be deposted with published articles in the AAS journals and other journals. Particular attention should be paid to data sets that are products of well-defined surveys. Also describe your practice before or after formal publication.</pNSFsp>"Data sharing" refers to the release of data in response to a specific request from an interested party. Describe your polies for data sharing, including where applicable provisions for protection of privacy, confidentiality, intellectual propoerty, national security, or other rights or requirements.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">Directorate of Mathematical and Physical Sciences<br />Division of Astronomical Sciences (AST)<br />Advice to PIs on Data Management Plans, 2011 NSF AST division guidance (PDF)</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm">Mid-Scale Innovations Program in Astronomical Sciences (MSIP) Program Solicitation NSF 17-592. https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1140, 157, 783, 1369, '<p>Describe the format in which the data or products are stored (e.g., ASCII, html, FITS, VO-compliant tables, XML files, etc.). Include a description of the metadata that will make the actual data products useful to the general researcher. Where data are stored in unusual or not generally accessible formats, explain how the data may be converted to a more accessible format or otherwise made available to interested parties. In general, solutions and remedies should be provided.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">Directorate of Mathematical and Physical Sciences<br />Division of Astronomical Sciences (AST)<br />Advice to PIs on Data Management Plans, 2011 NSF AST division guidance (PDF)</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm">Mid-Scale Innovations Program in Astronomical Sciences (MSIP) Program Solicitation NSF 17-592. https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1141, 157, 784, 1368, '<p>Describe the types of data and products that will be generated in the research, such as images of astronomical objects, spectra, data tables, time series, theoretical formalisms, computational strategies, software, and curriculum materials.</p>', '', '<p><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">Directorate of Mathematical and Physical Sciences<br />Division of Astronomical Sciences (AST)<br />Advice to PIs on Data Management Plans, 2011 NSF AST division guidance (PDF)</a></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;"><span style="font-size: 10.5pt; font-family: \'Helvetica\',sans-serif; color: #333333;"><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001"><span style="color: #23527c;">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</span></a></span></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;"><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j"><span style="font-size: 10.5pt; font-family: \'Helvetica\',sans-serif; color: #23527c;">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</span></a></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;"><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4"><span style="font-size: 10.5pt; font-family: \'Helvetica\',sans-serif; color: #337ab7; text-decoration: none; text-underline: none;">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</span></a></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;"><span style="font-size: 10.5pt; font-family: \'Helvetica\',sans-serif; color: #333333;"><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060"><span style="color: #337ab7; text-decoration: none; text-underline: none;">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</span></a></span></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;"><span style="font-size: 10.5pt;"><a href="https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm"><span style="font-family: Helvetica, sans-serif; color: #333333;"><span style="color: #337ab7; text-decoration: none; text-underline: none;">Mid-Scale Innovations Program in Astronomical Sciences (MSIP) Program Solicitation NSF 17-592. https://www.nsf.gov/pubs/2017/nsf17592/nsf17592.htm</span></span></a></span></pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;">&nbsp;</pNSFsp style="background: white; margin: 0in 0in 7.5pt 0in;">&nbsp;</pNSFsh2>&nbsp;</h2>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1142, 165, 786, 1168, '<p>The DMP should clearly articulate how "sharing of primary data" is to be implemented. It should outline the rights and obligations of all parties as to their roles and responsibilities in the management and retention of research data. It must also consider changes to roles and responsibilities that will occur should a principal investigator or co-PI leave the institution. Any costs should be explained in the Budget Justification pages.&nbsp;</p>', '', '<p>&nbsp;</pNSFsp><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards. https://nsf.gov/eng/general/ENG_DMP_Policy.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><br /><br /></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1143, 165, 787, 1169, '<p>The Data Management Plan should describe the types of data, samples, physical collections, software, curriculum materials, and other materials to be produced in the course of the project. It should then describe the expected types of data to be retained.</p>', '', '<p><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards. https://nsf.gov/eng/general/ENG_DMP_Policy.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1144, 165, 788, 1170, '<p>The DMP should describe the period of data retention. Minimum data retention of research data is three years after conclusion of the award or three years after public release, whichever is later.&nbsp;</p>', '', '<p><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards. https://nsf.gov/eng/general/ENG_DMP_Policy.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp>&nbsp;</pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1145, 165, 789, 1171, '<p>The DMP should describe the specific data formats, media, and dissemination approaches that will be used to make data available to others, including any metadata.&nbsp; Policies for public access and sharing should be described, including provisions for appropirate protection of privacy, confidentiality, security, intellectual property, or other rights or requirements.</pNSFsp>Research centers and major partnerships with industry or other user communities must also address how data are to be shared and managed with partners, center members, and other major stakeholders.&nbsp; Publication delay policies (if applicable) must be clearly stated.&nbsp; Investigators are expected to submit significant findings for publications quickly that are consistent with the publication delay obligations of key partners, such as industrial members of a research center.</p>', '', '<p><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards. https://nsf.gov/eng/general/ENG_DMP_Policy.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1146, 165, 790, 1172, '<p>The DMP should describe physical and cyber resources and facilities that will be used for the effective preservation and storage of research data. In collaborative proposals or proposals involving sub-awards, the lead PI is responsible for assuring data storage and access.</p>', '', '<p><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards. https://nsf.gov/eng/general/ENG_DMP_Policy.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1147, 166, 791, 579, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1148, 166, 792, 580, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1149, 166, 793, 581, '<p>&nbsp;</pNSFsp>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</pNSFsp>&nbsp;</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1150, 166, 794, 582, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1151, 166, 795, 583, '<p>NSF program management will implement these policies for dissemination and sharing of research results, in ways appropriate to field and circumstances, through the proposal review process; through award negotiations and conditions; and through appropriate support and incentives for data cleanup, documentation, dissemination, storage and the like.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp><strong><br /><br /><br /></strong></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1152, 156, 785, 1507, '<p>If implementing the DMP will incur additional costs to the project this fact should be mentioned in the appropriate section of the plan (for example the cost of setting up and maintaining a web site). Details of the costs must be included in the budget justification in the budget section of the proposal.</p>', '', '<p><a href="https://www.nsf.gov/geo/geo-data-policies/ags/ags_data_mgt_guidance-v3.docx">AGS Advice to PIs on Data Management Plans&nbsp;</a>(MS-Word)</pNSFsp><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF Geosciences (GEO) Home, Atmospheric &amp; Geospace Sciences. https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1220, 163, 834, 1412, '<p>The Data Management Plan should address the physical and/or cyber resources and facilities (including those supplied by third parties) that will be used to store and preserve the data after the grant ends.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards. https://www.nsf.gov/cise/cise_dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/dir/index.jsp?org=CISE">Computer &amp; Information Science &amp; Engineering Home: https://www.nsf.gov/dir/index.jsp?org=CISE</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
