INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1237, 178, 849, 576, '<p>Specify how long access to data and products, and sharing of data or products, will be maintained after the life of the project, and how any associated costs will be covered and by whom.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards 03/10/2011. tps://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1238, 178, 850, 577, '<p>If data or products are to be preserved by a third party, please refer to their preservation plans if available.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards 03/10/2011. tps://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1239, 178, 851, 578, '<p>More stringent data management requirements may be specified in particular NSF solicitations or result from local policies and best practices at the PI&rsquo;s home institution. Additional requirements will be specified in the program solicitation and award conditions. Principal Investigators to be supported by such programs must discuss how they will meet these additional requirements in their Data Management Plans.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards 03/10/2011. tps://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp>&nbsp;</pNSFsp>&nbsp;</pNSFsp><br /><br /><br /></pNSFsp>&nbsp;</pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1273, 184, 877, 2921, '<p>If the proposed project involves a research cruise, describe the cruise plans. (Skip this section if it is not relevant to your proposal.) Consider the following questions: (1) How will pre-cruise planning be coordinated? (e.g. email, teleconference, workshop) (2) What types of sampling instruments will be deployed on the cruise? (3) How will the cruise event log be recorded? (e.g. the Rolling Deck to Repository (R2R) event logger application, an Excel spreadsheet, or paper logs) (4) Will you prepare a cruise report?</p>', '', '<p>If cruise plans are not known at this time, it is appropriate to omit this section or to state that cruise plans will be made at a later date. Funded projects that involve deployments (including research cruises as well as deployments of moorings, floats, and gliders) will be expected to provide deployment metadata to BCO-DMO, along with the project and dataset metadata. Information on how to contribute deployment metadata to BCO-DMO is available on the BCO-DMO website at http://www.bco-dmo.org/how-get-started.</pNSFsp><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Frequently Asked Questions (FAQs) for Public Access</a><br /><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br /><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br /><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br /><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a><br /><a href="http://www.rvdata.us/about/eventlog">R2R Scientific Sampling Event Log</a><br /><a href="http://bcodmo.org/sites/default/files/theTwoPagePlan_110602.pdf">BCO-DMO DMP Suggestions for Proposals Involving a Research Cruise (PDF)</a><br /><a href="http://www.imber.info/index.php/Science/Working-Groups/Data-Management/Cookbook">IMBER Data Management Cookbook</a><br /><a href="http://www.bco-dmo.org/sites/default/files/page_files/Roscop.doc">ROSCOP (Report of Observations/Samples collected by Oceanographic Programmes) Cruise Summary Form</a></p>', '<p>Pre-cruise planning will be done via teleconferencing and a planning workshop. Detailed plans for station locations, instrument deployment, water sampling strategy, and water sample allocation will be written up as a science implementation plan for the cruise. The actual sampling events will be recorded on paper logs (scanned into PDF documents) and/or in a digital event log using the R2R event logger application (if available).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1274, 184, 878, 2922, '<p>Provide a description of the types of data to be produced during the project. Identify the types of data, samples, physical collections, software, derived models, curriculum materials, and other materials to be produced in the course of the project. Include a description of the location of collection, collection methods and instruments, expected dates or duration of collection. If you will be using existing datasets, state this and include how you will obtain them.</p>', '', '<p>Description of Data Types:<br></p><p>It may be useful to group data into four categories:</p><br><ulNSFsbr><li>(1) Observational (e.g. in-situ, collected in the field). Examples may include: shipboard underway data; mesozooplankton samples collected by a net system; copepod specimens collected, identified, and preserved; hydrographic casts; alongtrack data; remote sensing (e.g. ocean color); acoustic data.</liNSFsbr><li>(2) Experimental (e.g. generated in a lab or under controlled conditions). Examples: controlled carbonate chemistry experiments; DNA and RNA sequences.</liNSFsbr><li>(3) Simulations (e.g. machine-generated). Example: models and their output.</liNSFsbr><li>(4) Derived (e.g. synthesized from existing datasets). Examples: compiled database, products, reports.</liNSFsbrNSFs/ul><br><p> If the expected dates or duration of collection are not known at this time (e.g. due to funding schedules or ship availability), it is appropriate to give approximations, the ideal dates/duration, or to state that these details will be determin<br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a></p>', '<p>Description of data types:<br></p><p>The project will produce several observational and experimental datasets, described in the list below. In addition to the datasets described below, educational resources produced by the project, including data and images, will be made available for public use on the COSEE.net website. Observational data will be collected on a North Atlantic research cruise planned to take place during the summer months (July-August).</p><br><p><strong>Observational Datasets:</strong></p><br><olNSFsbr><liNSFsstrong>CTD and Niskin bottle data:</strong> CTD data collected using a SeaBird SBE CTD package; processing to be done using SeaBird&rsquo;s SeaSave software; data will include standard environmental measurements (such as pressure, temperature, salinity, fluorescence). File types: Raw (.con, .hdr, .hex, .bl) and processed  and .cnv, .asc, .btl) ASCII files. Repository: BCO-DMO</liNSFsbr><liNSFsstrong>Event log:</strong> Cruise scientific sampling event log; will include event numbers, start/end dates, times<p></pNSFs/liNSFs/ol>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1275, 184, 879, 2923, '<p>Identify the formats and standards to be used for data and metadata formatting and content. Where existing standards are absent or deemed inadequate, these formats and contents should be documented along with any proposed solutions or remedies. Consider the following questions: (1) Which file formats will be used to store your data? (2) What type of contextual details (metadata) will you document and how? (3) Are there specific data or metadata standards that you will be adhering to? (4) Will you be using or creating a data dictionary, code list, or glossary? (5) What types of quality control will be used? How will data quality be assessed and flagged?</p>', '', '<p>Data and Metadata Formats and Standards:<br>Usually, data served by BCO-DMO are submitted as comma- or tab-separated ASCII files (.csv, .txt) or as spreadsheet files (.xls, .xlsx). However, BCO-DMO is flexible and willing to work with whatever reasonably organized format the investigator uses.<br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a><br><a href="http://www.bco-dmo.org/submitting-data-spreadsheet">BCO-DMO Guidance on Organizing Data in a Spreadsheet</a><br><a href="https://dmptool.org/dm_guidance#formats">DMPTool Guidance on File Formats</a></p>', '<p>Data and Metadata Formats and Standards:<br>Field observation data will be stored in flat ASCII files, which can be read easily by different software packages. Field data will include date, time, latitude, longitude, cast number, and depth, as appropriate. Quality flags will be assigned according to the ODS IODE Quality Flag scheme (IOC Manuals and Guides, 54, volume 3; http://www.iode.org/mg54_3 ). Metadata will be prepared in accordance with BCO-DMO conventions (i.e. using the BCO-DMO metadata forms) and will include detailed descriptions of collection and analysis procedures.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1276, 184, 880, 2924, '<p>Describe how project data will be stored, accessed, and shared among project participants during the course of the project. Consider the following: (1) How will data be shared among project participants during the data collection and analysis phases? (e.g. web page, shared network drive) (2) How/where will data be stored and backed-up? (3) If data volumes will be significant, what is the estimated total file size?</p>', '', '<p><br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a><br><a href="https://dmptool.org/dm_guidance#security">DMPTool Guidance on Security and Storage</a></p>', '<p>Data Storage and Access During the Project:<br>The investigators will store project data (including spreadsheets, ASCII files, images, and PDFs of scanned logs) on laboratory computers that are backed up by the University&rsquo;s central IT organization. The Principal Investigator (PI) has also established an account with the San Diego Super Computer&rsquo;s enterprise class Cloud Service for data storage and sharing among project investigators. Personal computers in all laboratories are backed up daily using Apple Time Machine to an onsite external hard drive, and weekly to an offsite hard drive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1277, 184, 881, 2925, '<p>Describe mechanisms for data access and sharing, and describe any related policies and provisions for re-use, re-distribution, and the production of derivatives. Include provisions for appropriate protections of privacy, confidentiality, security, intellectual property, or other rights or requirements. Consider the following: (1) When will data be made publicly available and how? Identify the data repositories you plan to use to make data available. (2) Are the data sensitive in nature (e.g. endangered species concerns, potential patentability)? If so, is public access inappropriate and how will access be provided? (e.g. formal consent agreements, restricted access) (3) Will any permission restrictions (such as an embargo period) need to be placed on the data? If so, what are the reasons and what is the duration of the embargo? (4) Who holds intellectual property rights to the data and how might this affect data access? (5) Who is likely to be interested in re-using the data? What are the foreseeable re-uses of the data?</p>', '', '<p>Mechanisms and Policies for Access and Sharing:<br></p><p>Explain how and when data will be made available. Also describe any re-use and re-distribution policies and how the data sharing plans are related to those policies. Identify who will be allowed to use your data and whether or not they will be allowed to disseminate your data. If access to, use of, or dissemination of data will be restricted, explain how you will codify and communicate these restrictions.</p><br><h2>Data Sharing via BCO-DMO</h2><br><p> If the proposal is being submitted to the NSF Division of Ocean Sciences&rsquo; (OCE) Biological or Chemical Oceanography Sections or Division of Polar Programs (PLR) Antarctic Sciences (ANT) Organisms &amp; Ecosystems Program, then the two page plan can state that BCO-DMO staff will work with you to manage the data, and that data or model results generated during the proposed research project will be contributed to the BCO-DMO system. BCO-DMO provides data management services at no additional cost to projects funded <br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a><br><a href="http://app.iedadata.org/compliance/dmp/replist.php">Data Repositories List (created by Integrated Earth Data Applications (IEDA))</a><br><a href="http://www.bco-dmo.org/terms-use">Terms of Use for Data at BCO-DMO</a><br><a href="http://www.rvdata.us/catalog">R2R Cruise Catalog</a><br><a href="https://dmptool.org/dm_guidance#copyright">DMPTool Guidance on Copyright and Privacy</a><br><a href="http://creativecommons.org/licenses/">Creative Commons License Types</a><br><a href="http://www.unols.org/">University-National Oceanographic Laboratory System (UNOLS)</a><br><a href="http://www.ncbi.nlm.nih.gov/">National Center for Biotechnology Information (NCBI)</a><br><a href="http://www.ncbi.nlm.nih.gov/genbank/">GenBank</a><br><a href="https://portal.lternet.edu/nis/home.jsp">Long Term Ecological Research (LTER) Network Data Portal</a></p>', '<p>Mechanisms and Policies for Access and Sharing:<br></p><p>Immediately after completion of the research cruise, underway data and metadata will be submitted to the Rolling Deck to Repository (R2R) project. DNA sequences will be deposited in the National Center for Biotechnology Information (NCBI) database GenBank upon submission of manuscripts. GenBank accession numbers will be provided to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) in an Excel spreadsheet or .CSV file and metadata will be provided using the BCO-DMO Dataset Metadata submission form. Data sets produced by the science party will be made available through the BCO-DMO data system within two-years from the date of collection. The project investigators will work with BCO-DMO data managers to make project data available online in compliance with the NSF OCE Sample and Data Policy. Data, samples, and other information collected under this project can be made publically available without restriction once submitted to the pub</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1278, 184, 882, 2926, '<p>Describe the plans for long-term archiving of data, samples, and other research products, and for preservation of access to them. Consider the following: (1) What is your long-term strategy for maintaining, curating, and archiving the data? (2) What archive(s) have you identified as a place to deposit data and other research products?</p>', '', '<p>Plans for Archiving:<br></p><p>After data contributed to BCO-DMO are online and fully documented, BCO-DMO ensures that the data are archived properly at the appropriate National Data Center (e.g. NODC) for long-term archive preservation.</p><br><p>The Rolling Deck to Repository program (R2R) is responsible for archiving routine underway data at the appropriate national archive, including the National Geophysical Data Center (NGDC) and the National Oceanographic Data Center (NODC).</p><br><p>See Appendices III and IV of the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</p><br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a><p></p>', '<p>Plans for Archiving:<br>R2R will ensure that the original underway measurements are archived permanently at NODC and/or NGDC as appropriate. BCO-DMO will also ensure that project data are submitted to the appropriate national data archive. The PI will work with R2R and BCO-DMO to ensure data are archived appropriately and that proper and complete documentation are archived along with the data.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1279, 184, 883, 2927, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data. Consider the following: (1) If there are multiple investigators involved, what are the data management responsibilities of each person? (2) Who will be the lead or primary person responsible for ultimately ensuring compliance with the Data Management Plan?</p>', '', '<p><br><a href="http://www.nsf.gov/pubs/2011/nsf11060/nsf11060.pdf">NSF Division of Ocean Sciences Sample and Data Policy (PDF)</a><br><a href="http://www.nsf.gov/bfa/dias/policy/dmpfaqs.jsp">NSF Data Management and Sharing FAQs</a><br><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF OCE Sample and Data Policy, May 2011 (PDF)</a><br><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Data Policies</a></p>', '<p>Roles and Responsibilities:<br>Each PI will be responsible for sharing his/her subset of data among the project participants in a timely fashion. J. Doe will be responsible for collecting and analyzing the zooplankton sampling data. P. Smith will oversee the molecular biology work and will submit the resulting sequences to the National Center for Biotechnology Information&rsquo;s (NCBI) GenBank database. The Lead PI, R. Jones, will coordinate the overall data management and sharing process and will submit the project data, including GenBank accession numbers, and metadata to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) who will be responsible for forwarding these data and metadata to the appropriate national archive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1280, 184, 876, 2920, '<p>Identify any published data policies with which the project will comply, including the NSF OCE Data and Sample Policy as well as other policies that may be relevant if the project is part of a large coordinated research program (e.g. GEOTRACES).</p>', '', '<divNSFsdiv class="editable_resource"NSFsp class="cust_link"><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a><br /><a href="https://www.nsf.gov/pubs/2017/nsf17060/nsf17060.jsp">NSF Frequently Asked Questions (FAQs) for Public Access</a><br /><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a><br /><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a><br /><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></pNSFs/divNSFs/divNSFsdivNSFsdiv class="editable_resource"NSFsp class="cust_link"><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp" target="_blank" rel="noopener">NSF Dissemination and Sharing of Research Results</a></pNSFs/divNSFs/divNSFsdivNSFsdiv class="editable_resource"NSFsp class="cust_link"><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j" target="_blank" rel="noopener">NSF Grant Proposal Guide on DMPs</a></pNSFsp class="cust_link"><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Directorate Guidance</a></pNSFs/divNSFs/div>', '<p>The project investigators will comply with the data management and dissemination policies described in the NSF Award and Administration Guide (AAG, Chapter VI.D.4) and the NSF Division of Ocean Sciences Sample and Data Policy.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1288, 186, 890, 2153, '<p>Describe the types of data, physical samples or collections, software, curriculum materials, and other materials to be produced in the course of the project. (For collaborative proposals, the DMP must cover all the various data types being collected by each collaborator.)</pNSFsp>&nbsp;</p>', '', '<p><a href="https://www.nsf.gov/bio/biodmp.jsp">Biological Sciences Guidance on Data Management Plans. https://www.nsf.gov/bio/biodmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/dir/index.jsp?org=BIO">NSF Biological Sciences (BIO) Home. https://www.nsf.gov/dir/index.jsp?org=BIO</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
