INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1293, 186, 895, 2158, '<p>Where relevant, describe plans for archiving data, samples, software, and other research products, and for on-going access to these products through their lifecycle of usefulness to research and education.&nbsp;Consider which data (or research products) will be deposited for long-term access and where. (What physical and/or cyber resources and facilities (including third party resources) will be used to store and preserve the data after the grant ends?)</pNSFsp>&nbsp;</p>', '', '<p><a href="https://www.nsf.gov/bio/biodmp.jsp">Biological Sciences Guidance on Data Management Plans. https://www.nsf.gov/bio/biodmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF frequently asked questions (FAQs) for Public Access.https://www.nsf.</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf17060">NSF Dissemination and Sharing of Research Results, NSF Data Sharing Policy. https://www.nsf.gov/bfa/dias/policy/dmp.jspov/publications/pub_summ.jsp?ods_key=nsf17060</a></pNSFsp><a href="https://www.nsf.gov/dir/index.jsp?org=BIO">NSF Biological Sciences (BIO) Home. https://www.nsf.gov/dir/index.jsp?org=BIO</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1896, 166, 1123, 584, '<p>Physics Division PIs should include in their Data Management Plan those aspects of data retention and sharing that would allow them to respond to a question about a published result.&nbsp; Members of formal collaborations may refer to the collaboration\'s existing policies and practices.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1897, 208, 1124, 547, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Mathematical Sciences (DMS).&nbsp;Advice to PIs on Data Management Plans&nbsp;January 2, 2018. https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf</a></pNSFsp><a href="https://www.nsf.gov/mps/dms/about.jsp">Mathematical &amp; Physical Sciences (MPS). Mathmatical Sciences (DMS). https://www.nsf.gov/mps/dms/about.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1898, 208, 1125, 548, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Mathematical Sciences (DMS).&nbsp;Advice to PIs on Data Management Plans&nbsp;January 2, 2018. https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf</a></pNSFsp><a href="https://www.nsf.gov/mps/dms/about.jsp">Mathematical &amp; Physical Sciences (MPS). Mathmatical Sciences (DMS). https://www.nsf.gov/mps/dms/about.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1899, 208, 1126, 549, '<p>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</p>', '', '<p><strong>&nbsp;</strong></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (1900, 208, 1127, 550, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<p><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">Directorate of Mathematical and Physical Sciences&nbsp;Division of Physics (PHY)&nbsp;Advice to PIs on Data Management Plans<br />January 2, 2018 https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf</a></pNSFsp><a href="https://www.nsf.gov/div/index.jsp?div=PHY">NSF Mathematical &amp; Physical Sciences, Division of Physics (PHY). https://www.nsf.gov/div/index.jsp?div=PHY</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG), January 2018. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">Plans for data management and sharing of the products of research. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j</a></pNSFsp><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4">NSF 18-1 January 29, 2018, Part II: Award Administration and Monitoring of Grants and Cooperative Agreements-Chapter VI: NSF Awards. https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_6.jsp#XID4</a></pNSFsp><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results. https://www.nsf.gov/bfa/dias/policy/dmp.jsp</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access. https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041</a></pNSFsp>&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
