INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2212, 222, 1236, 2975, '<p>The Division of Earth Sciences requires that full data sets, derived data products (e.g. model results, output, and workflows), software, and physical collections must be made publicly accessible within two (2) years of final collection.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">NSF-EAR Division Data and Sample Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2213, 222, 1237, 2976, '<p>Standards to be used for data and metadata format and content (where existing standards are absent or deemed inadequate, this should be documented along with any proposed&nbsp;solutions or remedies).</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">NSF-EAR Division Data and Sample Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2214, 222, 1238, 2977, '<p>It is the responsibility of researchers and organizations to make results, data, derived data products, and collections available to the research community in a timely manner and at a reasonable cost. In the interest of full and open access, data should be provided at the lowest possible cost to researchers and educators. This cost should, as a first principle, be no more than the marginal cost of filling a specific user request. Data may be made available for secondary use through submission to a national data center, publication in a widely available scientific journal, book or website, through the institutional archives that are standard for a particular discipline (e.g. IRIS for seismological data, UNAVCO for GP data), or through other EAR-specified repositories. Data inventories should be published or entered into a public database periodically and when there is a significant change in type, location or frequency of such observations. Principal Investigators working in coordinated programs may establish (in consultation with other funding agencies and NSF) more stringent data submission procedures.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">NSF-EAR Division Data and Sample Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2215, 222, 1239, 2978, '<p>Policies and provisions for re-use, re-distribution, and the production of derivatives.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">NSF-EAR Division Data and Sample Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2216, 222, 1240, 2979, '<p>Policies and provisions for re-use, re-distribution, and the production of derivatives.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ear/ear-data-policy-sep2017.pdf">NSF-EAR Division Data and Sample Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2217, 223, 1241, 547, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2218, 223, 1242, 548, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2219, 223, 1243, 549, '<p>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2220, 223, 1244, 550, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2221, 223, 1245, 551, '<p>NSF program management will implement these policies for dissemination and sharing of research results, in ways appropriate to field and circumstances, through the proposal review process; through award negotiations and conditions; and through appropriate support and incentives for data cleanup, documentation, dissemination, storage and the like.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2222, 223, 1246, 552, '<p>Each NSF grant contains, as part of the grant terms, an article implementing dissemination and sharing of research results.&nbsp;&nbsp;</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/dms.pdf">NSF-DMS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/mps/dms/about.jsp">NSF Division of Mathmatical Sciences (DMS)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2239, 228, 1263, 2920, '<p>Identify any published data policies with which the project will comply, including the NSF OCE Data and Sample Policy as well as other policies that may be relevant if the project is part of a large coordinated research program (e.g. GEOTRACES).</p>', '', '<divNSFsdiv class="editable_resource"NSFsulNSFsli class="cust_link"><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli class="cust_link"><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli class="cust_link"><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp" target="_blank" rel="noopener">NSF Dissemination and Sharing of Research Results</a></liNSFsli class="cust_link"><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j" target="_blank" rel="noopener">NSF Grant Proposal Guide on DMPs</a></liNSFsli class="cust_link"><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Directorate Guidance</a></liNSFs/ulNSFs/divNSFs/div>', '<p>The project investigators will comply with the data management and dissemination policies described in the NSF Award and Administration Guide (AAG, Chapter VI.D.4) and the NSF Division of Ocean Sciences Sample and Data Policy.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2240, 228, 1264, 2921, '<p>If the proposed project involves a research cruise, describe the cruise plans. (Skip this section if it is not relevant to your proposal.) Consider the following questions:</pNSFsolNSFsli>How will pre-cruise planning be coordinated? (e.g. email, teleconference, workshop)</liNSFsli>What types of sampling instruments will be deployed on the cruise?</liNSFsli>How will the cruise event log be recorded? (e.g. the Rolling Deck to Repository (R2R) event logger application, an Excel spreadsheet, or paper logs)</liNSFsli>Will you prepare a cruise report?</liNSFs/ol>', '', '<p>If cruise plans are not known at this time, it is appropriate to omit this section or to state that cruise plans will be made at a later date. Funded projects that involve deployments (including research cruises as well as deployments of moorings, floats, and gliders) will be expected to provide deployment metadata to BCO-DMO, along with the project and dataset metadata. Information on how to contribute deployment metadata to BCO-DMO is available on the BCO-DMO website at http://www.bco-dmo.org/how-get-started.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli><a href="http://www.rvdata.us/about/eventlog">R2R Scientific Sampling Event Log</a></liNSFsli><a href="http://bcodmo.org/sites/default/files/theTwoPagePlan_110602.pdf">BCO-DMO DMP Suggestions for Proposals Involving a Research Cruise (PDF)</a></liNSFsli><a href="http://www.imber.info/index.php/Science/Working-Groups/Data-Management/Cookbook">IMBER Data Management Cookbook</a></liNSFsli><a href="http://www.bco-dmo.org/sites/default/files/page_files/Roscop.doc">ROSCOP (Report of Observations/Samples collected by Oceanographic Programmes) Cruise Summary Form</a></liNSFs/ul>', '<p>Pre-cruise planning will be done via teleconferencing and a planning workshop. Detailed plans for station locations, instrument deployment, water sampling strategy, and water sample allocation will be written up as a science implementation plan for the cruise. The actual sampling events will be recorded on paper logs (scanned into PDF documents) and/or in a digital event log using the R2R event logger application (if available).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2241, 228, 1265, 2922, '<p>Provide a description of the types of data to be produced during the project. Identify the types of data, samples, physical collections, software, derived models, curriculum materials, and other materials to be produced in the course of the project. Include a description of the location of collection, collection methods and instruments, expected dates or duration of collection. If you will be using existing datasets, state this and include how you will obtain them.</p>', '', '<p>It may be useful to group data into four categories:</pNSFsulNSFsolNSFsli>Observational (e.g. in-situ, collected in the field). Examples may include: shipboard underway data; mesozooplankton samples collected by a net system; copepod specimens collected, identified, and preserved; hydrographic casts; alongtrack data; remote sensing (e.g. ocean color); acoustic data.</liNSFsli>Experimental (e.g. generated in a lab or under controlled conditions). Examples: controlled carbonate chemistry experiments; DNA and RNA sequences.</liNSFsli>Simulations (e.g. machine-generated). Example: models and their output.</liNSFsli>Derived (e.g. synthesized from existing datasets). Examples: compiled database, products, reports.</liNSFs/olNSFs/ulNSFsp>If the expected dates or duration of collection are not known at this time (e.g. due to funding schedules or ship availability), it is appropriate to give approximations, the ideal dates/duration, or to state that these details will be determined at a later date.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br /><br /></liNSFs/ul>', '<p>The project will produce several observational and experimental datasets, described in the list below. In addition to the datasets described below, educational resources produced by the project, including data and images, will be made available for public use on the COSEE.net website. Observational data will be collected on a North Atlantic research cruise planned to take place during the summer months (July-August).</pNSFsp><strong>Observational Datasets:</strong></pNSFsolNSFsli><strong>CTD and Niskin bottle data:</strong> CTD data collected using a SeaBird SBE CTD package; processing to be done using SeaBird&rsquo;s SeaSave software; data will include standard environmental measurements (such as pressure, temperature, salinity, fluorescence). File types: Raw (.con, .hdr, .hex, .bl) and processed and .cnv, .asc, .btl) ASCII files. Repository: BCO-DMO</liNSFsli><strong>Event log:</strong> Cruise scientific sampling event log; will include event numbers, start/end dates, times &amp; locations of instrument deployments. Will be recorded using the R2R event logger (if available) and on paper log sheets. File types: Excel file converted to .csv; scanned PDFs. Repository: BCO-DMO and Rolling Deck to Repository (R2R).</liNSFsli><strong>Cruise underway data:</strong>&nbsp;Routine underway data collected along the ship&rsquo;s track (including meteorological data, sea surface temperature, salinity, fluorescence, ADCP). Will be collected by the shipboard instrumentation. File types: .csv ASCII files. Repository: BCO-DMO and R2R.</liNSFsli><strong>Zooplankton sampling logs and images:</strong>&nbsp;Zooplankton will be sampled via Reeve net trawls and MOCNESS (Multiple Opening/Closing Net and Environmental Sensing System) tows during the cruise. Species identified, tow numbers, locations, depths, dates, and times will be recorded by hand on log sheets. Information from log will be transferred into an Excel spreadsheet. Photographs of each tow/trawl will be taken on the ship using a digital camera. File types: PDF files of scanned log sheets; Excel files of sampling logs; images (.jpg files). Repository: BCO-DMO.</liNSFs/olNSFsp><strong>Experimental Datasets:</strong></pNSFsolNSFsli><strong>Pteropod respiration:</strong>&nbsp;Physiological experiments carried out on pteropods captured at sea raised under controlled pCO2 conditions; dataset will include data on the experimental treatments and the observed respiration rates. Animals will be captured using a Reeve Net or MOCNESS. Experiments will be conducted in the ship&rsquo;s lab. File types: Excel file(s). Repository: BCO-DMO.</liNSFsli><strong>Genetic sequencing:</strong>&nbsp;mRNA and DNA sequences from animals collected at sea. Sequencing will be performed at the PI&rsquo;s lab in Woods Hole, MA following the research cruise. File types: Short-read archive (.sra) and .fasta files. Repository: NCBI; accession numbers to be provided to BCO-DMO.</liNSFs/ol>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2242, 228, 1266, 2923, '<p>Identify the formats and standards to be used for data and metadata formatting and content. Where existing standards are absent or deemed inadequate, these formats and contents should be documented along with any proposed solutions or remedies. Consider the following questions:</pNSFsolNSFsli>Which file formats will be used to store your data?</liNSFsli>What type of contextual details (metadata) will you document and how?</liNSFsli>Are there specific data or metadata standards that you will be adhering to?</liNSFsli>Will you be using or creating a data dictionary, code list, or glossary?</liNSFsli>What types of quality control will be used? How will data quality be assessed and flagged?</liNSFs/ol>', '', '<p>Usually, data served by BCO-DMO are submitted as comma- or tab-separated ASCII files (.csv, .txt) or as spreadsheet files (.xls, .xlsx). However, BCO-DMO is flexible and willing to work with whatever reasonably organized format the investigator uses.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/submitting-data-spreadsheet">BCO-DMO Guidance on Organizing Data in a Spreadsheet</a></liNSFs/ul>', '<p>Field observation data will be stored in flat ASCII files, which can be read easily by different software packages. Field data will include date, time, latitude, longitude, cast number, and depth, as appropriate. Quality flags will be assigned according to the ODS IODE Quality Flag scheme (IOC Manuals and Guides, 54, volume 3; http://www.iode.org/mg54_3 ). Metadata will be prepared in accordance with BCO-DMO conventions (i.e. using the BCO-DMO metadata forms) and will include detailed descriptions of collection and analysis procedures.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2243, 228, 1267, 2924, '<p>Describe how project data will be stored, accessed, and shared among project participants during the course of the project. Consider the following:</pNSFsolNSFsli>How will data be shared among project participants during the data collection and analysis phases? (e.g. web page, shared network drive)</liNSFsli>How/where will data be stored and backed-up?</liNSFsli>If data volumes will be significant, what is the estimated total file size?</liNSFs/ol>', '', '<ulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFs/ul>', '<p>The investigators will store project data (including spreadsheets, ASCII files, images, and PDFs of scanned logs) on laboratory computers that are backed up by the University&rsquo;s central IT organization. The Principal Investigator (PI) has also established an account with the San Diego Super Computer&rsquo;s enterprise class Cloud Service for data storage and sharing among project investigators. Personal computers in all laboratories are backed up daily using Apple Time Machine to an onsite external hard drive, and weekly to an offsite hard drive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2244, 228, 1268, 2925, '<p>Describe mechanisms for data access and sharing, and describe any related policies and provisions for re-use, re-distribution, and the production of derivatives. Include provisions for appropriate protections of privacy, confidentiality, security, intellectual property, or other rights or requirements. Consider the following:</pNSFsolNSFsli>When will data be made publicly available and how? Identify the data repositories you plan to use to make data available.</liNSFsli>Are the data sensitive in nature (e.g. endangered species concerns, potential patentability)? If so, is public access inappropriate and how will access be provided? (e.g. formal consent agreements, restricted access)</liNSFsli>Will any permission restrictions (such as an embargo period) need to be placed on the data? If so, what are the reasons and what is the duration of the embargo?</liNSFsli>Who holds intellectual property rights to the data and how might this affect data access?</liNSFsli>Who is likely to be interested in re-using the data? What are the foreseeable re-uses of the data?</liNSFs/ol>', '', '<p>Explain how and when data will be made available. Also describe any re-use and re-distribution policies and how the data sharing plans are related to those policies. Identify who will be allowed to use your data and whether or not they will be allowed to disseminate your data. If access to, use of, or dissemination of data will be restricted, explain how you will codify and communicate these restrictions.</pNSFsp><strong>Data Sharing via BCO-DMO</strong></pNSFsp>If the proposal is being submitted to the NSF Division of Ocean Sciences&rsquo; (OCE) Biological or Chemical Oceanography Sections or Division of Polar Programs (PLR) Antarctic Sciences (ANT) Organisms &amp; Ecosystems Program, then the two page plan can state that BCO-DMO staff will work with you to manage the data, and that data or model results generated during the proposed research project will be contributed to the BCO-DMO system. BCO-DMO provides data management services at no additional cost to projects funded by these NSF sections/programs.</pNSFsp>Project investigators funded by these sections/programs can work with BCO-DMO to make project data available online, in compliance with the NSF OCE Sample and Data Policy. PIs of funded projects will be expected to submit project metadata to BCO-DMO beginning with the award/proposal number and DMP for proposals that are recommended for funding.</pNSFsp>BCO-DMO can deal with a wide variety of data, including but not limited to biological, chemical, and physical oceanography measurements. BCO-DMO data managers routinely serve in-situ data including standard hydrographic, biogeochemical, biological, ecological, and microbial measurements and chemical tracers; experimental and model results; images and movies, etc. If you are uncertain if BCO-DMO is an appropriate repository for your data, please contact info@bco-dmo.org.</pNSFsp>See Appendices III and IV of the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</pNSFsp><strong>Genomic Data and Other Specialized Repositories</strong></pNSFsp>Provisions should be made for the sharing and storage of genetic and molecular data in a publicly accessible, permanent database such as the various NCBI databases (e.g. GenBank), RAST, MG-RAST, etc. Information about the types of data accepted by GenBank is available on their website at http://www.ncbi.nlm.nih.gov/genbank/submit_types. If known, it&rsquo;d be helpful to disclose the details and availability of bioinformatics pipelines that will be used in next-generation methods. Accommodations for sharing of metagenome, metatranscriptome, and proteomics data should also be described in the DMP.</pNSFsp>BCO-DMO can enable discovery of data that have been contributed to specialized repositories (e.g. NCBI, LTER data catalog, CDIAC). For example, genetic sequence data are best served by an NCBI repository such as GenBank. Metadata and accession numbers/URLs/unique identifiers can then be provided to BCO-DMO so that access to these alternate repositories can be provided through the BCO-DMO website.</pNSFsp><strong>Underway Shipboard Data</strong></pNSFsp>All routine underway data collected by vessel-resident instrumentation aboard UNOLS-supported oceanographic research vessels will be submitted to the appropriate long-term archive through the Rolling Deck to Repository (R2R) program. The PI is responsible for disseminating the data and metadata produced by the science party&rsquo;s research.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli><a href="http://app.iedadata.org/compliance/dmp/replist.php">Data Repositories List (created by Integrated Earth Data Applications (IEDA))</a></liNSFsli><a href="http://www.bco-dmo.org/terms-use">Terms of Use for Data at BCO-DMO</a></liNSFsli><a href="http://www.rvdata.us/catalog">R2R Cruise Catalog</a></liNSFsli><a href="http://creativecommons.org/licenses/">Creative Commons License Types</a></liNSFsli><a href="http://www.unols.org/">University-National Oceanographic Laboratory System (UNOLS)</a></liNSFsli><a href="http://www.ncbi.nlm.nih.gov/">National Center for Biotechnology Information (NCBI)</a></liNSFsli><a href="http://www.ncbi.nlm.nih.gov/genbank/">GenBank</a></liNSFsli><a href="https://portal.lternet.edu/nis/home.jsp">Long Term Ecological Research (LTER) Network Data Portal</a></liNSFs/ul>', '<p>Immediately after completion of the research cruise, underway data and metadata will be submitted to the Rolling Deck to Repository (R2R) project. DNA sequences will be deposited in the National Center for Biotechnology Information (NCBI) database GenBank upon submission of manuscripts. GenBank accession numbers will be provided to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) in an Excel spreadsheet or .CSV file and metadata will be provided using the BCO-DMO Dataset Metadata submission form. Data sets produced by the science party will be made available through the BCO-DMO data system within two-years from the date of collection. The project investigators will work with BCO-DMO data managers to make project data available online in compliance with the NSF OCE Sample and Data Policy. Data, samples, and other information collected under this project can be made publically available without restriction once submitted to the&nbsp;public repositories.</pNSFsp>Data produced by this project may be of interest to chemical and biological oceanographers, and climate scientists interested in the role of biogeochemistry in the global climate system. We will adhere to and promote the standards, policies, and provisions for data and metadata submission, access, re-use, distribution, and ownership as prescribed by the BCO-DMO Terms of Use (http://www.bco-dmo.org/terms-use).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2245, 228, 1269, 2926, '<p>Describe the plans for long-term archiving of data, samples, and other research products, and for preservation of access to them. Consider the following:</pNSFsolNSFsli>What is your long-term strategy for maintaining, curating, and archiving the data?</liNSFsli>What archive(s) have you identified as a place to deposit data and other research products?</liNSFs/ol>', '', '<div>After data contributed to BCO-DMO are online and fully documented, BCO-DMO ensures that the data are archived properly at the appropriate National Data Center (e.g. NCEI) for long-term archive preservation.</divNSFsdiv>&nbsp;</divNSFsdiv>The Rolling Deck to Repository program (R2R) is responsible for archiving routine underway data at the appropriate national archive, including the National Geophysical Data Center (NGDC) and the National Centers for Environmental Information (NCEI).</divNSFsdiv>&nbsp;</divNSFsdiv>See Appendices III and IV of the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</divNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a>&nbsp;</liNSFs/ul>', '<p>R2R will ensure that the original underway measurements are archived permanently at NCEI and/or NGDC as appropriate. BCO-DMO will also ensure that project data are submitted to the appropriate national data archive. The PI will work with R2R and BCO-DMO to ensure data are archived appropriately and that proper and complete documentation are archived along with the data.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2246, 228, 1270, 2927, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data. Consider the following:</pNSFsolNSFsli>If there are multiple investigators involved, what are the data management responsibilities of each person</liNSFsli>Who will be the lead or primary person responsible for ultimately ensuring compliance with the Data Management Plan?</liNSFs/ol>', '', '<ulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFs/ul>', '<p>Each PI will be responsible for sharing his/her subset of data among the project participants in a timely fashion. J. Doe will be responsible for collecting and analyzing the zooplankton sampling data. P. Smith will oversee the molecular biology work and will submit the resulting sequences to the National Center for Biotechnology Information&rsquo;s (NCBI) GenBank database. The Lead PI, R. Jones, will coordinate the overall data management and sharing process and will submit the project data, including GenBank accession numbers, and metadata to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) who will be responsible for forwarding these data and metadata to the appropriate national archive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2251, 230, 1275, 2920, '<p>Identify any published data policies with which the project will comply, including the NSF OCE Data and Sample Policy as well as other policies that may be relevant if the project is part of a large coordinated research program (e.g. GEOTRACES).</p>', '', '<divNSFsdiv class="editable_resource"NSFsulNSFsli class="cust_link"><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli class="cust_link"><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli class="cust_link"><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli class="cust_link"><a href="http://www.nsf.gov/bfa/dias/policy/dmp.jsp" target="_blank" rel="noopener">NSF Dissemination and Sharing of Research Results</a></liNSFsli class="cust_link"><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j" target="_blank" rel="noopener">NSF Grant Proposal Guide on DMPs</a></liNSFsli class="cust_link"><a href="http://www.nsf.gov/geo/geo-data-policies/">NSF GEO Directorate Guidance</a></liNSFs/ulNSFs/divNSFs/div>', '<p>The project investigators will comply with the data management and dissemination policies described in the NSF Award and Administration Guide (AAG, Chapter VI.D.4) and the NSF Division of Ocean Sciences Sample and Data Policy.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2252, 230, 1276, 2921, '<p>If the proposed project involves a research cruise, describe the cruise plans. (Skip this section if it is not relevant to your proposal.) Consider the following questions:</pNSFsolNSFsli>How will pre-cruise planning be coordinated? (e.g. email, teleconference, workshop)</liNSFsli>What types of sampling instruments will be deployed on the cruise?</liNSFsli>How will the cruise event log be recorded? (e.g. the Rolling Deck to Repository (R2R) event logger application, an Excel spreadsheet, or paper logs)</liNSFsli>Will you prepare a cruise report?</liNSFs/ol>', '', '<p>If cruise plans are not known at this time, it is appropriate to omit this section or to state that cruise plans will be made at a later date. Funded projects that involve deployments (including research cruises as well as deployments of moorings, floats, and gliders) will be expected to provide deployment metadata to BCO-DMO, along with the project and dataset metadata. Information on how to contribute deployment metadata to BCO-DMO is available on the BCO-DMO website at http://www.bco-dmo.org/how-get-started.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli><a href="http://www.rvdata.us/about/eventlog">R2R Scientific Sampling Event Log</a></liNSFsli><a href="http://bcodmo.org/sites/default/files/theTwoPagePlan_110602.pdf">BCO-DMO DMP Suggestions for Proposals Involving a Research Cruise (PDF)</a></liNSFsli><a href="http://www.imber.info/index.php/Science/Working-Groups/Data-Management/Cookbook">IMBER Data Management Cookbook</a></liNSFsli><a href="http://www.bco-dmo.org/sites/default/files/page_files/Roscop.doc">ROSCOP (Report of Observations/Samples collected by Oceanographic Programmes) Cruise Summary Form</a></liNSFs/ul>', '<p>Pre-cruise planning will be done via teleconferencing and a planning workshop. Detailed plans for station locations, instrument deployment, water sampling strategy, and water sample allocation will be written up as a science implementation plan for the cruise. The actual sampling events will be recorded on paper logs (scanned into PDF documents) and/or in a digital event log using the R2R event logger application (if available).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2253, 230, 1277, 2922, '<p>Provide a description of the types of data to be produced during the project. Identify the types of data, samples, physical collections, software, derived models, curriculum materials, and other materials to be produced in the course of the project. Include a description of the location of collection, collection methods and instruments, expected dates or duration of collection. If you will be using existing datasets, state this and include how you will obtain them.</p>', '', '<p>It may be useful to group data into four categories:</pNSFsulNSFsolNSFsli>Observational (e.g. in-situ, collected in the field). Examples may include: shipboard underway data; mesozooplankton samples collected by a net system; copepod specimens collected, identified, and preserved; hydrographic casts; alongtrack data; remote sensing (e.g. ocean color); acoustic data.</liNSFsli>Experimental (e.g. generated in a lab or under controlled conditions). Examples: controlled carbonate chemistry experiments; DNA and RNA sequences.</liNSFsli>Simulations (e.g. machine-generated). Example: models and their output.</liNSFsli>Derived (e.g. synthesized from existing datasets). Examples: compiled database, products, reports.</liNSFs/olNSFs/ulNSFsp>If the expected dates or duration of collection are not known at this time (e.g. due to funding schedules or ship availability), it is appropriate to give approximations, the ideal dates/duration, or to state that these details will be determined at a later date.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a><br /><br /></liNSFs/ul>', '<p>The project will produce several observational and experimental datasets, described in the list below. In addition to the datasets described below, educational resources produced by the project, including data and images, will be made available for public use on the COSEE.net website. Observational data will be collected on a North Atlantic research cruise planned to take place during the summer months (July-August).</pNSFsp><strong>Observational Datasets:</strong></pNSFsolNSFsli><strong>CTD and Niskin bottle data:</strong> CTD data collected using a SeaBird SBE CTD package; processing to be done using SeaBird&rsquo;s SeaSave software; data will include standard environmental measurements (such as pressure, temperature, salinity, fluorescence). File types: Raw (.con, .hdr, .hex, .bl) and processed and .cnv, .asc, .btl) ASCII files. Repository: BCO-DMO</liNSFsli><strong>Event log:</strong> Cruise scientific sampling event log; will include event numbers, start/end dates, times &amp; locations of instrument deployments. Will be recorded using the R2R event logger (if available) and on paper log sheets. File types: Excel file converted to .csv; scanned PDFs. Repository: BCO-DMO and Rolling Deck to Repository (R2R).</liNSFsli><strong>Cruise underway data:</strong>&nbsp;Routine underway data collected along the ship&rsquo;s track (including meteorological data, sea surface temperature, salinity, fluorescence, ADCP). Will be collected by the shipboard instrumentation. File types: .csv ASCII files. Repository: BCO-DMO and R2R.</liNSFsli><strong>Zooplankton sampling logs and images:</strong>&nbsp;Zooplankton will be sampled via Reeve net trawls and MOCNESS (Multiple Opening/Closing Net and Environmental Sensing System) tows during the cruise. Species identified, tow numbers, locations, depths, dates, and times will be recorded by hand on log sheets. Information from log will be transferred into an Excel spreadsheet. Photographs of each tow/trawl will be taken on the ship using a digital camera. File types: PDF files of scanned log sheets; Excel files of sampling logs; images (.jpg files). Repository: BCO-DMO.</liNSFs/olNSFsp><strong>Experimental Datasets:</strong></pNSFsolNSFsli><strong>Pteropod respiration:</strong>&nbsp;Physiological experiments carried out on pteropods captured at sea raised under controlled pCO2 conditions; dataset will include data on the experimental treatments and the observed respiration rates. Animals will be captured using a Reeve Net or MOCNESS. Experiments will be conducted in the ship&rsquo;s lab. File types: Excel file(s). Repository: BCO-DMO.</liNSFsli><strong>Genetic sequencing:</strong>&nbsp;mRNA and DNA sequences from animals collected at sea. Sequencing will be performed at the PI&rsquo;s lab in Woods Hole, MA following the research cruise. File types: Short-read archive (.sra) and .fasta files. Repository: NCBI; accession numbers to be provided to BCO-DMO.</liNSFs/ol>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2254, 230, 1278, 2923, '<p>Identify the formats and standards to be used for data and metadata formatting and content. Where existing standards are absent or deemed inadequate, these formats and contents should be documented along with any proposed solutions or remedies. Consider the following questions:</pNSFsolNSFsli>Which file formats will be used to store your data?</liNSFsli>What type of contextual details (metadata) will you document and how?</liNSFsli>Are there specific data or metadata standards that you will be adhering to?</liNSFsli>Will you be using or creating a data dictionary, code list, or glossary?</liNSFsli>What types of quality control will be used? How will data quality be assessed and flagged?</liNSFs/ol>', '', '<p>Usually, data served by BCO-DMO are submitted as comma- or tab-separated ASCII files (.csv, .txt) or as spreadsheet files (.xls, .xlsx). However, BCO-DMO is flexible and willing to work with whatever reasonably organized format the investigator uses.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/submitting-data-spreadsheet">BCO-DMO Guidance on Organizing Data in a Spreadsheet</a></liNSFs/ul>', '<p>Field observation data will be stored in flat ASCII files, which can be read easily by different software packages. Field data will include date, time, latitude, longitude, cast number, and depth, as appropriate. Quality flags will be assigned according to the ODS IODE Quality Flag scheme (IOC Manuals and Guides, 54, volume 3; http://www.iode.org/mg54_3 ). Metadata will be prepared in accordance with BCO-DMO conventions (i.e. using the BCO-DMO metadata forms) and will include detailed descriptions of collection and analysis procedures.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2255, 230, 1279, 2924, '<p>Describe how project data will be stored, accessed, and shared among project participants during the course of the project. Consider the following:</pNSFsolNSFsli>How will data be shared among project participants during the data collection and analysis phases? (e.g. web page, shared network drive)</liNSFsli>How/where will data be stored and backed-up?</liNSFsli>If data volumes will be significant, what is the estimated total file size?</liNSFs/ol>', '', '<ulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFs/ul>', '<p>The investigators will store project data (including spreadsheets, ASCII files, images, and PDFs of scanned logs) on laboratory computers that are backed up by the University&rsquo;s central IT organization. The Principal Investigator (PI) has also established an account with the San Diego Super Computer&rsquo;s enterprise class Cloud Service for data storage and sharing among project investigators. Personal computers in all laboratories are backed up daily using Apple Time Machine to an onsite external hard drive, and weekly to an offsite hard drive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2256, 230, 1280, 2925, '<p>Describe mechanisms for data access and sharing, and describe any related policies and provisions for re-use, re-distribution, and the production of derivatives. Include provisions for appropriate protections of privacy, confidentiality, security, intellectual property, or other rights or requirements. Consider the following:</pNSFsolNSFsli>When will data be made publicly available and how? Identify the data repositories you plan to use to make data available.</liNSFsli>Are the data sensitive in nature (e.g. endangered species concerns, potential patentability)? If so, is public access inappropriate and how will access be provided? (e.g. formal consent agreements, restricted access)</liNSFsli>Will any permission restrictions (such as an embargo period) need to be placed on the data? If so, what are the reasons and what is the duration of the embargo?</liNSFsli>Who holds intellectual property rights to the data and how might this affect data access?</liNSFsli>Who is likely to be interested in re-using the data? What are the foreseeable re-uses of the data?</liNSFs/ol>', '', '<p>Explain how and when data will be made available. Also describe any re-use and re-distribution policies and how the data sharing plans are related to those policies. Identify who will be allowed to use your data and whether or not they will be allowed to disseminate your data. If access to, use of, or dissemination of data will be restricted, explain how you will codify and communicate these restrictions.</pNSFsp><strong>Data Sharing via BCO-DMO</strong></pNSFsp>If the proposal is being submitted to the NSF Division of Ocean Sciences&rsquo; (OCE) Biological or Chemical Oceanography Sections or Division of Polar Programs (PLR) Antarctic Sciences (ANT) Organisms &amp; Ecosystems Program, then the two page plan can state that BCO-DMO staff will work with you to manage the data, and that data or model results generated during the proposed research project will be contributed to the BCO-DMO system. BCO-DMO provides data management services at no additional cost to projects funded by these NSF sections/programs.</pNSFsp>Project investigators funded by these sections/programs can work with BCO-DMO to make project data available online, in compliance with the NSF OCE Sample and Data Policy. PIs of funded projects will be expected to submit project metadata to BCO-DMO beginning with the award/proposal number and DMP for proposals that are recommended for funding.</pNSFsp>BCO-DMO can deal with a wide variety of data, including but not limited to biological, chemical, and physical oceanography measurements. BCO-DMO data managers routinely serve in-situ data including standard hydrographic, biogeochemical, biological, ecological, and microbial measurements and chemical tracers; experimental and model results; images and movies, etc. If you are uncertain if BCO-DMO is an appropriate repository for your data, please contact info@bco-dmo.org.</pNSFsp>See Appendices III and IV of the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</pNSFsp><strong>Genomic Data and Other Specialized Repositories</strong></pNSFsp>Provisions should be made for the sharing and storage of genetic and molecular data in a publicly accessible, permanent database such as the various NCBI databases (e.g. GenBank), RAST, MG-RAST, etc. Information about the types of data accepted by GenBank is available on their website at http://www.ncbi.nlm.nih.gov/genbank/submit_types. If known, it&rsquo;d be helpful to disclose the details and availability of bioinformatics pipelines that will be used in next-generation methods. Accommodations for sharing of metagenome, metatranscriptome, and proteomics data should also be described in the DMP.</pNSFsp>BCO-DMO can enable discovery of data that have been contributed to specialized repositories (e.g. NCBI, LTER data catalog, CDIAC). For example, genetic sequence data are best served by an NCBI repository such as GenBank. Metadata and accession numbers/URLs/unique identifiers can then be provided to BCO-DMO so that access to these alternate repositories can be provided through the BCO-DMO website.</pNSFsp><strong>Underway Shipboard Data</strong></pNSFsp>All routine underway data collected by vessel-resident instrumentation aboard UNOLS-supported oceanographic research vessels will be submitted to the appropriate long-term archive through the Rolling Deck to Repository (R2R) program. The PI is responsible for disseminating the data and metadata produced by the science party&rsquo;s research.</pNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFsli><a href="http://app.iedadata.org/compliance/dmp/replist.php">Data Repositories List (created by Integrated Earth Data Applications (IEDA))</a></liNSFsli><a href="http://www.bco-dmo.org/terms-use">Terms of Use for Data at BCO-DMO</a></liNSFsli><a href="http://www.rvdata.us/catalog">R2R Cruise Catalog</a></liNSFsli><a href="http://creativecommons.org/licenses/">Creative Commons License Types</a></liNSFsli><a href="http://www.unols.org/">University-National Oceanographic Laboratory System (UNOLS)</a></liNSFsli><a href="http://www.ncbi.nlm.nih.gov/">National Center for Biotechnology Information (NCBI)</a></liNSFsli><a href="http://www.ncbi.nlm.nih.gov/genbank/">GenBank</a></liNSFsli><a href="https://portal.lternet.edu/nis/home.jsp">Long Term Ecological Research (LTER) Network Data Portal</a></liNSFs/ul>', '<p>Immediately after completion of the research cruise, underway data and metadata will be submitted to the Rolling Deck to Repository (R2R) project. DNA sequences will be deposited in the National Center for Biotechnology Information (NCBI) database GenBank upon submission of manuscripts. GenBank accession numbers will be provided to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) in an Excel spreadsheet or .CSV file and metadata will be provided using the BCO-DMO Dataset Metadata submission form. Data sets produced by the science party will be made available through the BCO-DMO data system within two-years from the date of collection. The project investigators will work with BCO-DMO data managers to make project data available online in compliance with the NSF OCE Sample and Data Policy. Data, samples, and other information collected under this project can be made publically available without restriction once submitted to the&nbsp;public repositories.</pNSFsp>Data produced by this project may be of interest to chemical and biological oceanographers, and climate scientists interested in the role of biogeochemistry in the global climate system. We will adhere to and promote the standards, policies, and provisions for data and metadata submission, access, re-use, distribution, and ownership as prescribed by the BCO-DMO Terms of Use (http://www.bco-dmo.org/terms-use).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2257, 230, 1281, 2926, '<p>Describe the plans for long-term archiving of data, samples, and other research products, and for preservation of access to them. Consider the following:</pNSFsolNSFsli>What is your long-term strategy for maintaining, curating, and archiving the data?</liNSFsli>What archive(s) have you identified as a place to deposit data and other research products?</liNSFs/ol>', '', '<div>After data contributed to BCO-DMO are online and fully documented, BCO-DMO ensures that the data are archived properly at the appropriate National Data Center (e.g. NCEI) for long-term archive preservation.</divNSFsdiv>&nbsp;</divNSFsdiv>The Rolling Deck to Repository program (R2R) is responsible for archiving routine underway data at the appropriate national archive, including the National Geophysical Data Center (NGDC) and the National Centers for Environmental Information (NCEI).</divNSFsdiv>&nbsp;</divNSFsdiv>See Appendices III and IV of the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</divNSFsulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a>&nbsp;</liNSFs/ul>', '<p>R2R will ensure that the original underway measurements are archived permanently at NCEI and/or NGDC as appropriate. BCO-DMO will also ensure that project data are submitted to the appropriate national data archive. The PI will work with R2R and BCO-DMO to ensure data are archived appropriately and that proper and complete documentation are archived along with the data.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2258, 230, 1282, 2927, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data. Consider the following:</pNSFsolNSFsli>If there are multiple investigators involved, what are the data management responsibilities of each person</liNSFsli>Who will be the lead or primary person responsible for ultimately ensuring compliance with the Data Management Plan?</liNSFs/ol>', '', '<ulNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="http://www.bco-dmo.org/files/bcodmo/BCO-DMO_best_practices.pdf">BCO-DMO Data Management Best Practices Guide (PDF)</a></liNSFsli><a href="http://www.bco-dmo.org/how-get-started">How to Get Started Contributing Data to BCO-DMO</a></liNSFsli><a href="http://www.bco-dmo.org/faq-page">Frequently Asked Questions (FAQs) about BCO-DMO</a></liNSFs/ul>', '<p>Each PI will be responsible for sharing his/her subset of data among the project participants in a timely fashion. J. Doe will be responsible for collecting and analyzing the zooplankton sampling data. P. Smith will oversee the molecular biology work and will submit the resulting sequences to the National Center for Biotechnology Information&rsquo;s (NCBI) GenBank database. The Lead PI, R. Jones, will coordinate the overall data management and sharing process and will submit the project data, including GenBank accession numbers, and metadata to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) who will be responsible for forwarding these data and metadata to the appropriate national archive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2348, 236, 1317, 1502, '<p>Describe the types of data and products that will be generated in the research, such as physical samples, space and/or time-dependent information on chemical and physical processes, images, spectra, final or intermediate numerical results, theoretical formalisms, computational strategies, software, and curriculum materials.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2349, 236, 1318, 1503, '<p>Describe the format in which the data or products are stored (e.g. hardcopy logs and/or instrument outputs, ASCII, XML files, HDF5, CDF, etc).&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2350, 236, 1319, 1504, '<p>Describe your plans for providing access to data, including websites maintained by your research group and contributions to public databases. If maintenance of a web site or database is the direct responsibility of your group, provide information about the period of time the web site or database is expected to be maintained. Also describe your practice or policies regarding the release of data&mdash;for example whether data are available before or after formal publication and the approximate duration of time that the data will be kept private. Describe your policies (where applicable) for protection of propriety data, privacy and confidentiality, intellectual property, or other rights or requirements.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2351, 236, 1320, 1505, '<p>Describe your policies regarding the use of data provided via general access or sharing. If you plan to provide data on a website, will the site contain disclaimers, or conditions regarding the use of the data in other publications or products? If the data or products are copyrighted, how will this be noted on the website?</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2352, 236, 1321, 1506, '<p>Describe whether and how data will be archived and how preservation of access will be handled. For example, will hardcopy logs, instrument outputs, and physical samples be stored in a location where there are safeguards against fire or water damage? Is there a plan to transfer digitized information to new storage media or devices as technological standards or practices change? Will there be an easily accessible index that documents where all archived data are stored and how they can be accessed? If the data will be archived by a third party, please refer to their preservation plans (if available).</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
