INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2377, 240, 1352, 570, '<p>Plans for archiving data, samples, and other research products, and for preservation of access to them.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmp.jsp">NSF Dissemination and Sharing of Research Results</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2378, 241, 1353, 1368, '<p>Describe the types of data and products that will be generated in the research, such as images of astronomical objects, spectra, data tables, time series, theoretical formalisms, computational strategies, software, and curriculum materials.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">NSF-AST Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2379, 241, 1354, 1369, '<p>Describe the format in which the data or products are stored (e.g., ASCII, html, FITS, VO-compliant tables, XML files, etc.). Include a description of the metadata that will make the actual data products useful to the general researcher. Where data are stored in unusual or not generally accessible formats, explain how the data may be converted to a more accessible format or otherwise made available to interested parties. In general, solutions and remedies should be provided.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">NSF-AST Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2380, 241, 1355, 1370, '<p>"Access to data" refers to data made accessible without explicit request from the interested party, for example those posted on a website or made available to a public database. Describe your plans, if any, for providing such general access to data, including websites maintained by your research group, and direct contributions to public databases. If maintenance of a web site or data base is the direct responsibility of your group, provide information about the period of time the web site or data base is expected to be maintained. Note that data taken at national or private observatories may be accessible through public archives (perhaps after a standard proprietary period). Various forms of data (e.g.FITS image and tables, other data tables) also may be deposted with published articles in the AAS journals and other journals. Particular attention should be paid to data sets that are products of well-defined surveys. Also describe your practice before or after formal publication.</pNSFsp>"Data sharing" refers to the release of data in response to a specific request from an interested party. Describe your polies for data sharing, including where applicable provisions for protection of privacy, confidentiality, intellectual propoerty, national security, or other rights or requirements.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">NSF-AST Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2381, 241, 1356, 1371, '<p>Describe your policies regarding the use of data provided via general access or sharing. For example, if you plan to provide data and images on your website, will the website contain disclaimers, or conditions regarding the use of the data in other publications or products? If the data or products (e.g., images) are copyrighted (by a journal, for example), how will this be noted on the website?</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">NSF-AST Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2382, 241, 1357, 1372, '<p>Describe whether and how data will be archived and how preservation of access will be handled. If the data will be archived by a third party (e.g., national observatory or journal), please refer to their preservation plans if available.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/ast.pdf">NSF-AST Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2383, 242, 1358, 2153, '<p>Describe the types of data, physical samples or collections, software, curriculum materials, and other materials to be produced in the course of the project. (For collaborative proposals, the DMP must cover all the various data types being collected by each collaborator.)</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
