INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2385, 242, 1360, 2155, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data (including contingency plans for the departure of key personnel from the project).</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2386, 242, 1361, 2156, '<p>Describe the dissemination methods that will be used to make data and metadata available to others during the period of the award, and any modifications or additional technical information regarding data access after the grant ends.</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2387, 242, 1362, 2157, '<p>Describe the PI&rsquo;s policies for data sharing, public access and re-use, including re-distribution by others and the production of derivatives. Where appropriate, include provisions for protection of privacy, confidentiality, security, intellectual property rights and other rights.</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2388, 242, 1363, 2158, '<p>Where relevant, describe plans for archiving data, samples, software, and other research products, and for on-going access to these products through their lifecycle of usefulness to research and education.&nbsp;Consider which data (or research products) will be deposited for long-term access and where. (What physical and/or cyber resources and facilities (including third party resources) will be used to store and preserve the data after the grant ends?)</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2389, 243, 1364, 1502, '<p>Describe the types of data and products that will be generated in the research, such as physical samples, space and/or time-dependent information on chemical and physical processes, images, spectra, final or intermediate numerical results, theoretical formalisms, computational strategies, software, and curriculum materials.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2390, 243, 1365, 1503, '<p>Describe the format in which the data or products are stored (e.g. hardcopy logs and/or instrument outputs, ASCII, XML files, HDF5, CDF, etc).&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2391, 243, 1366, 1504, '<p>Describe your plans for providing access to data, including websites maintained by your research group and contributions to public databases. If maintenance of a web site or database is the direct responsibility of your group, provide information about the period of time the web site or database is expected to be maintained. Also describe your practice or policies regarding the release of data&mdash;for example whether data are available before or after formal publication and the approximate duration of time that the data will be kept private. Describe your policies (where applicable) for protection of propriety data, privacy and confidentiality, intellectual property, or other rights or requirements.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2392, 243, 1367, 1505, '<p>Describe your policies regarding the use of data provided via general access or sharing. If you plan to provide data on a website, will the site contain disclaimers, or conditions regarding the use of the data in other publications or products? If the data or products are copyrighted, how will this be noted on the website?</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2393, 243, 1368, 1506, '<p>Describe whether and how data will be archived and how preservation of access will be handled. For example, will hardcopy logs, instrument outputs, and physical samples be stored in a location where there are safeguards against fire or water damage? Is there a plan to transfer digitized information to new storage media or devices as technological standards or practices change? Will there be an easily accessible index that documents where all archived data are stored and how they can be accessed? If the data will be archived by a third party, please refer to their preservation plans (if available).</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2394, 243, 1369, 1507, '<p>If implementing the DMP will incur additional costs to the project this fact should be mentioned in the appropriate section of the plan (for example the cost of setting up and maintaining a web site). Details of the costs must be included in the budget justification in the budget section of the proposal.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/geo/geo-data-policies/ags/index.jsp">NSF AGS Advice to PIs on DMPs</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg17_1/pappg_2.jsp#IIC2j">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access&nbsp;</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
