INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2399, 244, 1374, 575, '<p>Specify what data formats will be used (e.g., XML files, websites, image files, data tables, software code, text documents, physical materials).</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2400, 244, 1375, 576, '<p>Specify how long access to data and products, and sharing of data or products, will be maintained after the life of the project, and how any associated costs will be covered and by whom.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2401, 244, 1376, 577, '<p>If data or products are to be preserved by a third party, please refer to their preservation plans if available.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2402, 244, 1377, 578, '<p>More stringent data management requirements may be specified in particular NSF solicitations or result from local policies and best practices at the PI&rsquo;s home institution. Additional requirements will be specified in the program solicitation and award conditions. Principal Investigators to be supported by such programs must discuss how they will meet these additional requirements in their Data Management Plans.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/ehr.pdf">Data Management for NSF EHR Directorate&nbsp;Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2403, 245, 1378, 579, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2404, 245, 1379, 580, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<p>&nbsp;</pNSFsulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2405, 245, 1380, 581, '<p>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2406, 245, 1381, 582, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2407, 245, 1382, 583, '<p>NSF program management will implement these policies for dissemination and sharing of research results, in ways appropriate to field and circumstances, through the proposal review process; through award negotiations and conditions; and through appropriate support and incentives for data cleanup, documentation, dissemination, storage and the like.</p>', '', '<p>&nbsp;</pNSFsulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2408, 245, 1383, 584, '<p>Physics Division PIs should include in their Data Management Plan those aspects of data retention and sharing that would allow them to respond to a question about a published result. Members of formal collaborations may refer to the collaboration\'s existing policies and practices.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bfa/dias/policy/dmpdocs/phy.pdf">NSF-PHY Advice to PIs on Data Management Plans<br /></a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2409, 246, 1384, 1168, '<p>The DMP should clearly articulate how "sharing of primary data" is to be implemented. It should outline the rights and obligations of all parties as to their roles and responsibilities in the management and retention of research data. It must also consider changes to roles and responsibilities that will occur should a principal investigator or co-PI leave the institution. Any costs should be explained in the Budget Justification pages.&nbsp;</p>', '', '<ulNSFsli><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2410, 246, 1385, 1169, '<p>The Data Management Plan should describe the types of data, samples, physical collections, software, curriculum materials, and other materials to be produced in the course of the project. It should then describe the expected types of data to be retained.</p>', '', '<ulNSFsli><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2411, 246, 1386, 1170, '<p>The DMP should describe the period of data retention. Minimum data retention of research data is three years after conclusion of the award or three years after public release, whichever is later.&nbsp;</p>', '', '<ulNSFsli><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2412, 246, 1387, 1171, '<p>The DMP should describe the specific data formats, media, and dissemination approaches that will be used to make data available to others, including any metadata.&nbsp; Policies for public access and sharing should be described, including provisions for appropirate protection of privacy, confidentiality, security, intellectual property, or other rights or requirements.</pNSFsp>Research centers and major partnerships with industry or other user communities must also address how data are to be shared and managed with partners, center members, and other major stakeholders.&nbsp; Publication delay policies (if applicable) must be clearly stated.&nbsp; Investigators are expected to submit significant findings for publications quickly that are consistent with the publication delay obligations of key partners, such as industrial members of a research center.</p>', '', '<ulNSFsli><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2413, 246, 1388, 1172, '<p>The DMP should describe physical and cyber resources and facilities that will be used for the effective preservation and storage of research data. In collaborative proposals or proposals involving sub-awards, the lead PI is responsible for assuring data storage and access.</p>', '', '<ulNSFsli><a href="https://nsf.gov/eng/general/ENG_DMP_Policy.pdf">Data Management for NSF Engineering Directorate Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2414, 247, 1389, 3358, '<p>Investigators are expected to promptly prepare and submit for publication, with authorship that accurately reflects the contributions of those involved, all significant findings from work conducted under NSF grants. Grantees are expected to permit and encourage such publication by those actually performing that work, unless a grantee intends to publish or disseminate such findings itself.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF-DMR Advice to PIs on Data Management Plans</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2415, 247, 1390, 3359, '<p>Investigators are expected to share with other researchers, at no more than incremental cost and within a reasonable time, the primary data, samples, physical collections and other supporting materials created or gathered in the course of work under NSF grants. Grantees are expected to encourage and facilitate such sharing. Privileged or confidential information should be released only in a form that protects the privacy of individuals and subjects involved. General adjustments and, where essential, exceptions to this sharing expectation may be specified by the funding NSF Program or Division/Office for a particular field or discipline to safeguard the rights of individuals and subjects, the validity of results, or the integrity of collections or to accommodate the legitimate interest of investigators. A grantee or investigator also may request a particular adjustment or exception from the cognizant NSF Program Officer.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF-DMR Advice to PIs on Data Management Plans</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2416, 247, 1391, 3360, '<p>Investigators and grantees are encouraged to share software and inventions created under the grant or otherwise make them or their products widely available and usable.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF-DMR Advice to PIs on Data Management Plans</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2417, 247, 1392, 3361, '<p>NSF normally allows grantees to retain principal legal rights to intellectual property developed under NSF grants to provide incentives for development and dissemination of inventions, software and publications that can enhance their usefulness, accessibility and upkeep. Such incentives do not, however, reduce the responsibility that investigators and organizations have as members of the scientific and engineering community, to make results, data and collections available to other researchers.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF-DMR Advice to PIs on Data Management Plans</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2418, 247, 1393, 3362, '<p>NSF program management will implement these policies for dissemination and sharing of research results, in ways appropriate to field and circumstances, through the proposal review process; through award negotiations and conditions; and through appropriate support and incentives for data cleanup, documentation, dissemination, storage and the like.</p>', '', '<ulNSFsli><a href="http://www.nsf.gov/bfa/dias/policy/dmpdocs/dmr.pdf">NSF-DMR Advice to PIs on Data Management Plans</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2419, 248, 1394, 1410, '<p>The Data Management Plan should describe the types of data, metadata, scripts used to generate the data or metadata, experimental results, samples, physical collections, software, curriculum materials, or other materials to be produced in the course of the project.&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2420, 248, 1395, 1411, '<p>The Data Management Plan should address the standards to be used for data and metadata format and content (where existing standards are absent or deemed inadequate, this should be documented along with any proposed solutions or remedies).</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2421, 248, 1396, 1412, '<p>The Data Management Plan should address the physical and/or cyber resources and facilities (including those supplied by third parties) that will be used to store and preserve the data after the grant ends.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2422, 248, 1397, 1413, '<p>The Data Management Plan should address the policies for access and sharing including provisions for appropriate protection of privacy, confidentiality, security, intellectual property, or other rights or requirements.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (2423, 248, 1398, 1414, '<p>The DMP should address the policies and provision for re-use, re-distribution, and the production of derivatives.</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/cise/cise_dmp.jsp">Data Management Guidance for CISE Proposals and Awards</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18001">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
