INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (7328, 618, 3998, 2154, '<p>Describe the standards to be used for all the data types anticipated, including data or file format and metadata. [Note: Where existing standards are absent or deemed inadequate, this should be documented along with any proposed solutions or remedies.]</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=papp">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (7329, 618, 3999, 2155, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data (including contingency plans for the departure of key personnel from the project).</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=papp">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (7330, 618, 4000, 2156, '<p>Describe the dissemination methods that will be used to make data and metadata available to others during the period of the award, and any modifications or additional technical information regarding data access after the grant ends.</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=papp">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (7331, 618, 4001, 2157, '<p>Describe the PI&rsquo;s policies for data sharing, public access and re-use, including re-distribution by others and the production of derivatives. Where appropriate, include provisions for protection of privacy, confidentiality, security, intellectual property rights and other rights.</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=papp">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (7332, 618, 4002, 2158, '<p>Where relevant, describe plans for archiving data, samples, software, and other research products, and for on-going access to these products through their lifecycle of usefulness to research and education.&nbsp;Consider which data (or research products) will be deposited for long-term access and where. (What physical and/or cyber resources and facilities (including third party resources) will be used to store and preserve the data after the grant ends?)</pNSFsp>&nbsp;</p>', '', '<ulNSFsli><a href="https://www.nsf.gov/bio/biodmp.jsp">NSF-BIO Guidance on DMPs&nbsp;</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=papp">NSF Proposal &amp; Award Policies &amp; Procedures Guide (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/pubs/policydocs/pappg18_1/pappg_2.jsp#IIC2j">NSF plans for data management and sharing of the products of research (PAPPG)</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10336, 826, 5336, 2920, '<p>Identify any published data policies with which the project will comply, including the NSF OCE Data and Sample Policy as well as other policies that may be relevant if the project is part of a large coordinated research program (e.g. GEOTRACES).</p>', '', '<p><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></pNSFsp><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></pNSFsp><a href="https://new.nsf.gov/funding/data-management-plan" target="_blank" rel="noopener">NSF Guidance on Data Management Plans</a></pNSFsp><a href="https://new.nsf.gov/funding/data-management-plan" target="_blank" rel="noopener">NSF GEO Directorate Data Policies</a></p>', '<p>The project investigators will comply with the data management and dissemination policies described in the NSF Award and Administration Guide (AAG, Chapter VI.D.4) and the NSF Division of Ocean Sciences Sample and Data Policy.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10337, 826, 5337, 2921, '<p>If the proposed project involves a research cruise, describe the cruise plans. (Skip this section if it is not relevant to your proposal.) Consider the following questions:</pNSFsolNSFsli>How will pre-cruise planning be coordinated? (e.g. email, teleconference, workshop)</liNSFsli>What types of sampling instruments will be deployed on the cruise?</liNSFsli>How will the cruise event log be recorded? (e.g. the Rolling Deck to Repository (R2R) event logger application, an Excel spreadsheet, or paper logs)</liNSFsli>Will you prepare a cruise report?</liNSFs/ol>', '', '<p>If cruise plans are not known at this time, it is appropriate to omit this section or to state that cruise plans will be made at a later date. Funded projects that involve deployments (including research cruises as well as deployments of moorings, floats, and gliders) will be expected to provide deployment metadata to BCO-DMO along with dataset metadata.</pNSFsulNSFsli><a href="http://www.rvdata.us/about/eventlog" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages</a></liNSFsli><a href="https://www.unols.org/" target="_blank" rel="noopener">University-National Oceanographic Laboratory System (UNOLS)</a></liNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a href="https://www.rvdata.us/about/event-log" target="_blank" rel="noopener">R2R Scientific Sampling Event Log</a></liNSFs/ul>', '<p>Pre-cruise planning will be done via teleconferencing and a planning workshop. Detailed plans for station locations, instrument deployment, water sampling strategy, and water sample allocation will be written up as a science implementation plan for the cruise. The actual sampling events will be recorded on paper logs (scanned into PDF documents) and/or in a digital event log using the R2R event logger application (if available).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10338, 826, 5338, 2922, '<p>Provide a description of the types of data to be produced during the project. Identify the types of data, samples, physical collections, software, derived models, curriculum materials, and other materials to be produced in the course of the project. Include a description of the location of collection, collection methods and instruments, expected dates or duration of collection. If you will be using existing datasets, state this and include how you will obtain them.</p>', '', '<p>It may be useful to group data into four categories:</pNSFsulNSFsolNSFsli>Observational (e.g. in-situ, collected in the field). Examples may include: shipboard underway data; mesozooplankton samples collected by a net system; copepod specimens collected, identified, and preserved; hydrographic casts; alongtrack data; remote sensing (e.g. ocean color); acoustic data.</liNSFsli>Experimental (e.g. generated in a lab or under controlled conditions). Examples: controlled carbonate chemistry experiments; DNA and RNA sequences.</liNSFsli>Simulations (e.g. machine-generated). Example: models and their output.</liNSFsli>Derived (e.g. synthesized from existing datasets). Examples: compiled database, products, reports.</liNSFs/olNSFs/ulNSFsp>Keep in mind that code or software developed through your project must also be made publicly available in compliance with NSF Policies. Code may be contributed to BCO-DMO or published with a DOI through Zenodo.</pNSFsp>If the expected dates or duration of collection are not known at this time (e.g. due to funding schedules or ship availability), it is appropriate to give approximations, the ideal dates/duration, or to state that these details will be determined at a later date.</pNSFsulNSFsli><a href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages&nbsp;</a></liNSFsli><a href="https://bcodmo.gitbook.io/how-to/prepare/software-and-code" target="_blank" rel="noopener">BCO-DMO Guidance on Models, Software, and Code</a></liNSFsli><a href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Acces</a></liNSFs/ul>', '<p>The project will produce several observational and experimental datasets, described in the list below. In addition to the datasets described below, educational resources produced by the project, including data and images, will be made available for public use on the COSEE.net website. Observational data will be collected on a North Atlantic research cruise planned to take place during the summer months (July-August).</pNSFsp><strong>Observational Datasets:</strong></pNSFsolNSFsli><strong>CTD and Niskin bottle data:</strong> CTD data collected using a SeaBird SBE CTD package; processing to be done using SeaBird&rsquo;s SeaSave software; data will include standard environmental measurements (such as pressure, temperature, salinity, fluorescence). File types: Raw (.con, .hdr, .hex, .bl) and processed and .cnv, .asc, .btl) ASCII files. Repository: BCO-DMO</liNSFsli><strong>Event log:</strong> Cruise scientific sampling event log; will include event numbers, start/end dates, times &amp; locations of instrument deployments. Will be recorded using the R2R event logger (if available) and on paper log sheets. File types: Excel file converted to .csv; scanned PDFs. Repository: BCO-DMO and Rolling Deck to Repository (R2R).</liNSFsli><strong>Cruise underway data:</strong>&nbsp;Routine underway data collected along the ship&rsquo;s track (including meteorological data, sea surface temperature, salinity, fluorescence, ADCP). Will be collected by the shipboard instrumentation. File types: .csv ASCII files. Repository: BCO-DMO and R2R.</liNSFsli><strong>Zooplankton sampling logs and images:</strong>&nbsp;Zooplankton will be sampled via Reeve net trawls and MOCNESS (Multiple Opening/Closing Net and Environmental Sensing System) tows during the cruise. Species identified, tow numbers, locations, depths, dates, and times will be recorded by hand on log sheets. Information from log will be transferred into an Excel spreadsheet. Photographs of each tow/trawl will be taken on the ship using a digital camera. File types: PDF files of scanned log sheets; Excel files of sampling logs; images (.jpg files). Repository: BCO-DMO.</liNSFs/olNSFsp><strong>Experimental Datasets:</strong></pNSFsolNSFsli><strong>Pteropod respiration:</strong>&nbsp;Physiological experiments carried out on pteropods captured at sea raised under controlled pCO2 conditions; dataset will include data on the experimental treatments and the observed respiration rates. Animals will be captured using a Reeve Net or MOCNESS. Experiments will be conducted in the ship&rsquo;s lab. File types: Excel file(s). Repository: BCO-DMO.</liNSFsli><strong>Genetic sequencing:</strong>&nbsp;mRNA and DNA sequences from animals collected at sea. Sequencing will be performed at the PI&rsquo;s lab in Woods Hole, MA following the research cruise. File types: Short-read archive (.sra) and .fasta files. Repository: NCBI; accession numbers to be provided to BCO-DMO.</liNSFs/ol>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10339, 826, 5339, 2923, '<p>Identify the formats and standards to be used for data and metadata formatting and content. Where existing standards are absent or deemed inadequate, these formats and contents should be documented along with any proposed solutions or remedies. Consider the following questions:</pNSFsolNSFsli>Which file formats will be used to store your data?</liNSFsli>What type of contextual details (metadata) will you document and how?</liNSFsli>Are there specific data or metadata standards that you will be adhering to?</liNSFsli>Will you be using or creating a data dictionary, code list, or glossary?</liNSFsli>What types of quality control will be used? How will data quality be assessed and flagged?</liNSFs/ol>', '', '<p>Usually, data served by BCO-DMO are submitted as comma- or tab-separated ASCII files (.csv, .txt) or as spreadsheet files (.xls, .xlsx). However, BCO-DMO is flexible and willing to work with whatever reasonably organized format the investigator uses. Whenever possible, quality flags should be included as columns within each dataset. The use of standard vocabularies/naming conventions is highly recommended.</pNSFsulNSFsli><a href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages </a></liNSFsli><a title="BCO-DMO Guidance on Organizing Data Tables" href="https://bcodmo.gitbook.io/how-to/prepare/organizing-data-tables" target="_blank" rel="noopener">BCO-DMO Guidance on Organizing Data Tables</a></liNSFsli><a title="NSF Division of Ocean Sciences Sample and Data Policy" href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a title="NSF Frequently Asked Questions (FAQs) for Public Access" href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ul>', '<p>Field observation data will be stored in flat ASCII files, which can be read easily by different software packages. Field data will include date, time, latitude, longitude, cast number, and depth, as appropriate. Quality flags will be assigned according to the ODS IODE Quality Flag scheme (IOC Manuals and Guides, 54, volume 3; http://www.iode.org/mg54_3 ). Metadata will be prepared in accordance with BCO-DMO conventions (i.e. using the BCO-DMO metadata forms) and will include detailed descriptions of collection and analysis procedures.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10340, 826, 5340, 2924, '<p>Describe how project data will be stored, accessed, and shared among project participants during the course of the project. Consider the following:</pNSFsolNSFsli>How will data be shared among project participants during the data collection and analysis phases? (e.g. web page, shared network drive)</liNSFsli>How/where will data be stored and backed-up?</liNSFsli>If data volumes will be significant, what is the estimated total file size?</liNSFs/ol>', '', '<p><a title="BCO-DMO &ldquo;How-To&rdquo; Pages" href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages</a><br><a title="NSF Division of Ocean Sciences Sample and Data Policy" href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a><br><a title="NSF Frequently Asked Questions (FAQs) for Public Access" href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></pNSFsp>&nbsp;</p>', '<p>The investigators will store project data (including spreadsheets, ASCII files, images, and PDFs of scanned logs) on laboratory computers that are backed up by the University&rsquo;s central IT organization. The Principal Investigator (PI) has also established an account with the San Diego Super Computer&rsquo;s enterprise class Cloud Service for data storage and sharing among project investigators. Personal computers in all laboratories are backed up daily using Apple Time Machine to an onsite external hard drive, and weekly to an offsite hard drive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10341, 826, 5341, 2925, '<p>Describe mechanisms for data access and sharing, and describe any related policies and provisions for re-use, re-distribution, and the production of derivatives. Include provisions for appropriate protections of privacy, confidentiality, security, intellectual property, or other rights or requirements. Consider the following:</pNSFsolNSFsli>When will data be made publicly available and how? Identify the data repositories you plan to use to make data available.</liNSFsli>Are the data sensitive in nature (e.g. endangered species concerns, potential patentability)? If so, is public access inappropriate and how will access be provided? (e.g. formal consent agreements, restricted access)</liNSFsli>Will any permission restrictions (such as an embargo period) need to be placed on the data? If so, what are the reasons and what is the duration of the embargo?</liNSFsli>Who holds intellectual property rights to the data and how might this affect data access?</liNSFsli>Who is likely to be interested in re-using the data? What are the foreseeable re-uses of the data?</liNSFs/ol>', '', '<p>Explain how and when data will be made available. Also describe any re-use and re-distribution policies and how the data sharing plans are related to those policies. Identify who will be allowed to use your data and whether or not they will be allowed to disseminate your data. If access to, use of, or dissemination of data will be restricted, explain how you will codify and communicate these restrictions.</pNSFsp><strong>Data Sharing via BCO-DMO</strong></pNSFsp>If the proposal is being submitted to the NSF Division of Ocean Sciences&rsquo; (OCE) Biological or Chemical Oceanography Sections or Division of Polar Programs (PLR) Antarctic Sciences (ANT) Organisms &amp; Ecosystems Program, then the two page plan can state that BCO-DMO staff will work with you to manage the data, and that data or model results generated during the proposed research project will be contributed to the BCO-DMO system. BCO-DMO provides data management services at no additional cost to projects funded by these NSF sections/programs.</pNSFsp>Project investigators funded by these sections/programs can work with BCO-DMO to make project data available online, in compliance with the NSF OCE Sample and Data Policy. PIs of funded projects will be expected to submit project metadata to BCO-DMO beginning with the award/proposal number and DMP for proposals that are recommended for funding.</pNSFsp>BCO-DMO can deal with a wide variety of data, including but not limited to biological, chemical, and physical oceanography measurements. BCO-DMO data managers routinely serve in-situ data including standard hydrographic, biogeochemical, biological, ecological, and microbial measurements and chemical tracers; experimental and model results; images and movies, etc. If you are uncertain if BCO-DMO is an appropriate repository for your data, please contact info@bco-dmo.org.</pNSFsp>Refer to the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</pNSFsp><strong>Genomic Data and Other Specialized Repositories</strong></pNSFsp>Provisions should be made for the sharing and storage of genetic and molecular data in a publicly accessible, permanent database such as the various NCBI databases (e.g. GenBank), RAST, MG-RAST, etc. Information about the types of data accepted by GenBank is available on their website at http://www.ncbi.nlm.nih.gov/genbank/submit_types. If known, it&rsquo;d be helpful to disclose the details and availability of bioinformatics pipelines that will be used in next-generation methods. Accommodations for sharing of metagenome, metatranscriptome, and proteomics data should also be described in the DMP.</pNSFsp>BCO-DMO can enable discovery of data that have been contributed to specialized repositories (e.g. NCBI, LTER data catalog, CDIAC). For example, genetic sequence data are best served by an NCBI repository such as GenBank. Metadata and accession numbers/URLs/unique identifiers can then be provided to BCO-DMO so that access to these alternate repositories can be provided through the BCO-DMO website.</pNSFsp><strong>Underway Shipboard Data</strong></pNSFsp>All routine underway data collected by vessel-resident instrumentation aboard UNOLS-supported oceanographic research vessels will be submitted to the appropriate long-term archive through the Rolling Deck to Repository (R2R) program. The PI is responsible for disseminating the data and metadata produced by the science party&rsquo;s research.</pNSFsulNSFsli><a title="BCO-DMO &ldquo;How-To&rdquo; Pages" href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages</a></liNSFsli><a title="NSF Division of Ocean Sciences Sample and Data Policy" href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a title="NSF Frequently Asked Questions (FAQs) for Public Access" href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFsli><a title="BCO-DMO&rsquo;s Terms of Use" href="https://bcodmo.gitbook.io/how-to/access-and-reuse/attribution" target="_blank" rel="noopener">BCO-DMO&rsquo;s Terms of Use</a></liNSFsli><a title="R2R Cruise Catalog" href="https://www.rvdata.us/search?zoom=1&amp;x=0&amp;y=0&amp;projection=M" target="_blank" rel="noopener">R2R Cruise Catalog</a></liNSFsli><a title="Creative Commons License Types" href="https://creativecommons.org/licenses/" target="_blank" rel="noopener">Creative Commons License Types</a></liNSFsli><a title="National Center for Biotechnology Information (NCBI)" href="https://www.ncbi.nlm.nih.gov/" target="_blank" rel="noopener">National Center for Biotechnology Information (NCBI)</a></liNSFsli><a title="GenBank" href="https://www.ncbi.nlm.nih.gov/genbank/" target="_blank" rel="noopener">GenBank&nbsp;</a></liNSFsli><a title="System for Earth Sample Registration (SESAR)" href="https://www.geosamples.org/" target="_blank" rel="noopener">System for Earth Sample Registration (SESAR)</a></liNSFs/ul>', '<p>Immediately after completion of the research cruise, underway data and metadata will be submitted to the Rolling Deck to Repository (R2R) project. DNA sequences will be deposited in the National Center for Biotechnology Information (NCBI) database GenBank upon submission of manuscripts. GenBank accession numbers and related metadata will be provided to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) in an Excel spreadsheet or .CSV file and metadata will be provided using the BCO-DMO online Submission Tool. Data sets produced by the science party will be made available through the BCO-DMO data system within two-years from the date of collection. The project investigators will work with BCO-DMO data managers to make project data available online in compliance with the NSF OCE Sample and Data Policy. Data, samples, and other information collected under this project can be made publicly available without restriction once submitted to the public repositories.</pNSFsp>Data produced by this project may be of interest to chemical and biological oceanographers, and climate scientists interested in the role of biogeochemistry in the global climate system. We will adhere to and promote the standards, policies, and provisions for data and metadata submission, access, re-use, distribution, and ownership as prescribed by the BCO-DMO Terms of Use (http://www.bco-dmo.org/terms-use).</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10342, 826, 5342, 2926, '<p>Describe the plans for long-term archiving of data, samples, and other research products, and for preservation of access to them. Consider the following:</pNSFsolNSFsli>What is your long-term strategy for maintaining, curating, and archiving the data?</liNSFsli>What archive(s) have you identified as a place to deposit data and other research products?</liNSFs/ol>', '', '<div>After data contributed to BCO-DMO are online and fully documented, BCO-DMO ensures that the data are archived for long-term storage. The Rolling Deck to Repository program (R2R) is responsible for archiving routine underway data at the appropriate national archive, including the National Geophysical Data Center (NGDC) and the National Centers for Environmental Information (NCEI). Refer to the OCE Sample and Data Policy for information on other suggested databases and repositories for physical samples.</divNSFsdivNSFsulNSFsli><a title="BCO-DMO &ldquo;How-To&rdquo; Pages" href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages</a></liNSFsli><a title="NSF Division of Ocean Sciences Sample and Data Policy" href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a title="NSF Frequently Asked Questions (FAQs) for Public Access" href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ulNSFs/div>', '<p>R2R will ensure that the original underway measurements are archived permanently at NCEI and/or NGDC as appropriate. BCO-DMO will also ensure that project data are submitted to the appropriate national data archive. The PI will work with R2R and BCO-DMO to ensure data are archived appropriately and that proper and complete documentation are archived along with the data.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10343, 826, 5343, 2927, '<p>Describe the roles and responsibilities of all parties with respect to the management of the data. Consider the following:</pNSFsolNSFsli>If there are multiple investigators involved, what are the data management responsibilities of each person</liNSFsli>Who will be the lead or primary person responsible for ultimately ensuring compliance with the Data Management Plan?</liNSFs/ol>', '', '<ulNSFsli><a title="BCO-DMO &ldquo;How-To&rdquo; Pages" href="https://bcodmo.gitbook.io/how-to/" target="_blank" rel="noopener">BCO-DMO &ldquo;How-To&rdquo; Pages</a></liNSFsli><a title="NSF Division of Ocean Sciences Sample and Data Policy" href="https://www.nsf.gov/pubs/2017/nsf17037/nsf17037.jsp" target="_blank" rel="noopener">NSF Division of Ocean Sciences Sample and Data Policy</a></liNSFsli><a title="NSF Frequently Asked Questions (FAQs) for Public Access" href="https://www.nsf.gov/publications/pub_summ.jsp?ods_key=nsf18041" target="_blank" rel="noopener">NSF Frequently Asked Questions (FAQs) for Public Access</a></liNSFs/ulNSFsp>&nbsp;</p>', '<p>Each PI will be responsible for sharing his/her subset of data among the project participants in a timely fashion. J. Doe will be responsible for collecting and analyzing the zooplankton sampling data. P. Smith will oversee the molecular biology work and will submit the resulting sequences to the National Center for Biotechnology Information&rsquo;s (NCBI) GenBank database. The Lead PI, R. Jones, will coordinate the overall data management and sharing process and will submit the project data, including GenBank accession numbers, and metadata to the Biological and Chemical Oceanography Data Management Office (BCO-DMO) who will be responsible for forwarding these data and metadata to the appropriate national archive.</p>', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10456, 833, 5389, 2971, '<p>List the types of data and samples to be collected and/or generated. The listing of each data/sample type should briefly identify what metadata will be provided and when data/sample preparation will be considered complete. (Definitions of &ldquo;data&rdquo; and &ldquo;samples&rdquo; are explained above within &ldquo;EAR requirements.&rdquo;) For proposals providing community-serving infrastructure or research services, the DMP should describe the data/sample types to be managed and what guidance or support will be provided to help users meet their data/sample sharing obligations. EAR recognizes that data/samples may undergo multiple transformations in the research process (including destructive analyses), and disciplinary expectations for assignment of metadata and retention of intermediate data and sample products may vary.&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10457, 833, 5389, 2972, '<p>For each data or sample type, identify which personnel and institution(s) will be designated for its management, including contingency plans for the departure of key personnel from the project. For collaborative projects, PI(s) of the award(s) associated with the designated personnel and institution(s) are ultimately responsible for overseeing and reporting on their data and sample management activities.&nbsp;</p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10458, 833, 5390, 2973, '<p>For each data type listed, identify an appropriate long-lived FAIR-aligned repository for data deposit, the timeframe for public data access, and the expected period of data preservation. For each sample type listed, identify an appropriate long-lived FAIRaligned repository for indexing sample metadata, the location for sample storage (preferably a repository appropriate for the specific sample type), and the expected period of sample preservation. (Required timeframes for data/sample access are specified above within &ldquo;EAR Requirements.&rdquo;) Many repositories commit to preserve access to data/samples indefinitely; any deviations from this expectation should be explained. PIs are encouraged to coordinate with designated repositories in advance of planned data/sample submission.&nbsp;</p>', '', '<p>NSF EAR Guidance:&nbsp;</pNSFsp>All new data resulting from the project must be made publicly accessible within two (2) years after completion of data collection or generation, via appropriate long-lived FAIRaligned repositories. Expected timelines for data collection or generation may vary by data type and should align with appropriate disciplinary expectations. All new data collected via continuing observations, large-scale community projects, or RAPID awards must be made accessible as close to the time of initial collection as is practicable. All data in support of peer-reviewed scholarly publications resulting from the project must also be made publicly accessible at or before the time of publication. Exceptions to this policy must be justified (e.g., if an appropriate repository does not exist, or if data access must be restricted). &ldquo;Data available upon request&rdquo; is not acceptable.&nbsp;</pNSFsp>Metadata describing all new samples resulting from the project must be publicly indexed within two (2) years after sample collection is considered complete, via appropriate long-lived FAIR-aligned repositories. Metadata describing samples collected via continuing observations, large-scale community projects, or RAPID awards must be indexed and made accessible as close to the time of collection as is practicable. All sample metadata in support of peer-reviewed scholarly publications must also be publicly indexed at or before the time of publication. Publicly indexed sample metadata should specify provisions for sample access, including the expected period and location of sample preservation, preferably via a repository appropriate for the specific sample type. The samples themselves should also be made publicly accessible within the above timeframes; situations in which samples cannot be made publicly accessible should be explained.&nbsp;</p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (10459, 833, 5390, 2974, '<p>In most cases, it is sufficient for the DMP to identify the repository(ies) to be used and the timeframe for access and preservation for each type of data/sample identified. In these cases, the selected repository(ies) should align with FAIR principles and community-specific standards. Occasionally, appropriate long-lived FAIR-aligned repositories do not exist for certain types of data or samples. In such cases, it may be necessary to adopt alternative approaches to data access and retention, such as via use of a local computer server. In such cases, the DMP should explain how the proposed approach fulfills important attributes for FAIR-aligned repositories, consistent with OSTP guidance on &ldquo;Desirable Characteristics of Data Repositories for Federally Funded Research.&rdquo;9 These attributes include, but are not limited to, the following:</pNSFsp>&bull; Findability. Data should be findable via standard search tools, such as through the assignment of globally unique persistent identifiers (e.g., Digital Object Identifiers (DOIs) and International Geo Sample Numbers (IGSNs)) and rich metadata that is indexed in a searchable resource.</pNSFsp>&bull; Accessibility. Data should be publicly accessible to other researchers, at no more than incremental cost, within the specified timeframe. Any data access limitations must be justified. &ldquo;Data available upon request&rdquo; is not acceptable. &nbsp;</pNSFsp>&bull; Interoperability. To ensure interoperability, data should be described via appropriate metadata standards, in alignment with expectations of the associated scientific discipline(s).</pNSFsp>&bull; Reusability. To facilitate the broadest possible data reuse, data should be assigned clear and accessible usage licenses and metadata descriptors that identify provenance. EAR expects the adoption of unrestrictive open licenses except with specific justification.&nbsp;</p>', '', '<p><a href="https://doi.org/10.5479/10088/113528" target="_blank" rel="noopener">White House Office of Science and Technology Policy (2022), Desirable Characteristics of Data Repositories for Federally Funded Research.&nbsp;</a></p>', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11794, 910, 5943, 3346, '<p>1A. Types and amount of scientific data expected to be generated in the project:</pNSFsp><em>Summarize the types and estimated amount of scientific data expected to be generated in the&nbsp;</em><em>project.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11795, 910, 5943, 3347, '<p>1B. Of the generated scientific data in 1.A, the scientific data that will be preserved and shared, and the associated rationale:</pNSFsp><em>Describe which types of scientific data from the project will be preserved and shared and briefly provide the rationale for this decision.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11796, 910, 5943, 3348, '<p>1C. Metadata and associated documentation:</pNSFsp><em>Briefly list the metadata (provides information about the scientific data) and any associated documentation (e.g., study protocols, research methods, and data-collection instruments) that will be made accessible to facilitate interpretation of the preserved and shared scientific data.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11797, 910, 5944, 3349, '<p>2A. Specialized tools, Software/Code, and/or Services for Access or Interpretation</pNSFsp><em>State whether specialized tools, software/code and/or services are needed to access, manipulate, </em><em>or interpret the preserved and shared data, and if so, provide the name(s) of the tool(s) and </em><em>software/code or services, and specify how they can be found, accessed, and used (a link may b</em><em>e adequate).</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11798, 910, 5944, 3350, '<p>2B. Software/Code and/or Other Digital Products Produced:</pNSFsp><em>State software/code and/or other digital products expected to result from the successful execution of the project and to be distributed according to FAIR principles. Include any license intended to be used for the release and a justification for the choice, and how the product will be made accessible (include methods used to advertise to the broader community). Machine learning models should ideally be distributed along with associated training data.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11799, 910, 5945, 3351, '<p><em>3. State what common data standards will be applied to the preserved and shared scientific data, and associated metadata to enable interoperability of datasets and resources and describe how these data standards will be applied. If applicable, indicate that no consensus standards exist.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11800, 910, 5946, 3352, '<p>4A. Repository where scientific data and metadata will be available for reuse:</pNSFsp><em>Provide the name of the repository(ies) from which preserved and shared scientific data and metadata arising from the project will be available for reuse; see <a href="https://www.nsf.gov/pubs/2022/nsf22055/nsf22055.jsp">Dear Colleague Letter: Effective Practices for Making Research Data Discoverable and Citable (Data Sharing)</a> and <a href="https://doi.org/10.5479/10088/113528">Desirable Characteristics of Data Repositories for Federally Funded Research</a>.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11801, 910, 5946, 3353, '<p>4B. How scientific data will be findable and identifiable:</pNSFsp><em>Describe how the scientific data will be findable and identifiable, i.e., via a persistent unique identifier (e.g., DOI), listing in a registry, or other standard indexing techniques.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 2, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11802, 910, 5946, 3354, '<p>4C. When and for how long the scientific data will be made broadly available:</pNSFsp><em>Describe when the scientific data will be made broadly available (i.e., no later than when an associated publication appears or end of the performance period, whichever comes first) and for how long data will be available. Include any external factors that may affect time of appearance.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 3, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
INSERT INTO versionedQuestions (id, versionedTemplateId, versionedSectionId, questionId, questionText, requirementText, guidanceText, sampleText, json, useSampleTextAsDefault, displayOrder, createdById, created, modifiedById, modified) VALUES (11803, 910, 5947, 3355, '<p>5A. Factors affecting subsequent access, distribution, or reuse of scientific data:</pNSFsp><em>NSF expects that in drafting Plans, researchers should maximize the appropriate sharing of scientific data. Describe and clearly justify any applicable factors or data-use limitations affecting subsequent access, distribution, or reuse of scientific data.</em></p>', '', '', '', '{"meta": {"schemaVersion": "1.0"}, "type": "textArea", "attributes": {"cols": 20, "rows": 2, "asRichText": true}}', 0, 1, @default_funder2_id, CURDATE(), @default_funder2_id, CURDATE());
